import React from 'react';
import styled from 'styled-components';
import { useNavigate } from 'react-router-dom';

const MenuContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 20px;
  padding: 20px;
  max-width: 800px;
  margin: 0 auto;
`;

const MenuTitle = styled.h1`
  color: #2196F3;
  text-align: center;
  margin-bottom: 40px;
  font-size: 2.5rem;
`;

const MenuGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: 20px;
  width: 100%;
`;

const MenuItem = styled.button`
  background-color: white;
  border: 2px solid #2196F3;
  border-radius: 15px;
  padding: 30px;
  cursor: pointer;
  transition: all 0.3s ease;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 15px;

  &:hover {
    transform: translateY(-5px);
    box-shadow: 0 5px 15px rgba(33, 150, 243, 0.3);
  }

  &:active {
    transform: translateY(0);
  }
`;

const MenuIcon = styled.span`
  font-size: 3rem;
`;

const MenuText = styled.span`
  font-size: 1.2rem;
  color: #333;
  font-weight: bold;
`;

const MenuDescription = styled.p`
  color: #666;
  font-size: 0.9rem;
  text-align: center;
  margin: 0;
`;

const MainMenu = () => {
  const navigate = useNavigate();

  const handleGameSelect = (gameType) => {
    switch (gameType) {
      case 'tasks':
        navigate('/tasks');
        break;
      case 'puzzles':
        navigate('/puzzle');
        break;
      case 'scramble':
        navigate('/scramble');
        break;
      case 'drawing':
        navigate('/drawing');
        break;
      case 'colors':
        navigate('/colors');
        break;
      default:
        break;
    }
  };

  const menuItems = [
    {
      id: 'puzzles',
      icon: '🧩',
      title: 'პაზლები',
      description: 'ააწყვე სახალისო პაზლები',
    },
    {
      id: 'tasks',
      icon: '🎯',
      title: 'სახალისო ამოცანები',
      description: 'გამოიცანი და ამოხსენი',
    },
    {
      id: 'scramble',
      icon: '🔤',
      title: 'ასოების დალაგება',
      description: 'დაალაგე არეული ასოები სწორად',
    },
    {
      id: 'drawing',
      icon: '🎨',
      title: 'ხატვა და გაფერადება',
      description: 'ისწავლე ხატვა და გააფერადე საყვარელი ცხოველები',
    },
    {
      id: 'colors',
      icon: '🎨',
      title: 'ფერების თამაში',
      description: 'დააკავშირე ფერები სახელებთან'
    },
  ];

  return (
    <MenuContainer>
      <MenuTitle>🌟 სახალისო თამაშები 🌟</MenuTitle>
      <MenuGrid>
        {menuItems.map((item) => (
          <MenuItem key={item.id} onClick={() => handleGameSelect(item.id)}>
            <MenuIcon>{item.icon}</MenuIcon>
            <MenuText>{item.title}</MenuText>
            <MenuDescription>{item.description}</MenuDescription>
          </MenuItem>
        ))}
      </MenuGrid>
    </MenuContainer>
  );
};

export default MainMenu;
