import React from 'react';
import styled from 'styled-components';
import { useNavigate } from 'react-router-dom';

const MainMenu = () => {
  const navigate = useNavigate();

  const menuItems = [
    {
      title: 'პაზლები',
      icon: '🧩',
      description: 'აწყვე სახალისო პაზლები',
      path: '/puzzle'
    },
    {
      title: 'სახალისო ამოცანები',
      icon: '🎯',
      description: 'გამოიცანი და ამოხსენი',
      path: '/tasks'
    },
    {
      title: 'ასოების დალაგება',
      icon: '📝',
      description: 'დაალაგე არეული ასოები სწორად',
      path: '/scramble'
    },
    {
      title: 'ხატვა და გაფერადება',
      icon: '🎨',
      description: 'ისწავლე ხატვა და გააფერადე სხვადასხვა ფიგურები',
      path: '/coloring',
      isNew: true
    }
  ];

  return (
    <Container>
      <Title>⭐ სახალისო თამაშები ⭐</Title>
      <MenuGrid>
        {menuItems.map((item, index) => (
          <MenuItem 
            key={index} 
            onClick={() => navigate(item.path)}
            isNew={item.isNew}
          >
            <IconWrapper>{item.icon}</IconWrapper>
            <ItemTitle>{item.title}</ItemTitle>
            <Description>{item.description}</Description>
          </MenuItem>
        ))}
      </MenuGrid>
    </Container>
  );
};

const Container = styled.div`
  padding: 2rem;
  text-align: center;
`;

const Title = styled.h1`
  font-size: 2rem;
  color: #333;
  margin-bottom: 2rem;
`;

const MenuGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: 2rem;
  max-width: 1200px;
  margin: 0 auto;
`;

const MenuItem = styled.div`
  background: white;
  border-radius: 12px;
  padding: 2rem;
  box-shadow: 0 4px 6px rgba(0,0,0,0.1);
  cursor: pointer;
  transition: transform 0.2s;
  position: relative;

  &:hover {
    transform: translateY(-5px);
  }

  ${props => props.isNew && `
    &::after {
      content: 'ახალი';
      position: absolute;
      top: -10px;
      right: -10px;
      background: #ff4757;
      color: white;
      padding: 5px 10px;
      border-radius: 20px;
      font-size: 0.8rem;
    }
  `}
`;

const IconWrapper = styled.div`
  font-size: 3rem;
  margin-bottom: 1rem;
`;

const ItemTitle = styled.h2`
  font-size: 1.5rem;
  color: #333;
  margin-bottom: 1rem;
`;

const Description = styled.p`
  color: #666;
  font-size: 1rem;
`;

export default MainMenu;
