  const { updateGameProgress } = usePlayer();
  const navigate = useNavigate();

  // ·É§·Éù·É¢·Éù·Éî·Éë·Éò·É° ·É©·Éê·É¢·Éï·Éò·É†·Éó·Éï·Éê
  const loadImages = useCallback(async () => {
    try {
      const images = [
        '·Éê·Éö·É£·Éë·Éê·Éö·Éò.webp', '·Éê·É†·É¨·Éò·Éï·Éò.webp', '·Éê·É¢·Éê·Éõ·Éò.webp', '·Éê·É•·Éö·Éî·Éõ·Éò.webp', '·Éë·Éê·Éú·Éê·Éú·Éò.webp',
        '·Éë·É†·Éù·É¨·Éî·É£·Éö·Éò.webp', '·Éë·É£.webp', '·Éí·Éï·Éî·Éö·Éò.webp', '·Éì·Éê·Éó·Éï·Éò.webp', '·Éì·Éî·Éö·É§·Éò·Éú·Éò.webp',
        '·Éï·Éê·É®·Éö·Éò.webp', '·Éñ·Éî·Éë·É†·Éê.webp', '·Éñ·Éï·Éò·Éí·Éî·Éú·Éò.webp', '·Éñ·É¶·Éê·É†·Éë·Éò.webp', '·Éó·ÉÆ·Éê (2).webp',
        '·Éò·É†·Éî·Éõ·Éò (2).webp', '·Éô·Éê·É•·É¢·É£·É°·Éò.webp', '·Éô·Éî·Éú·Éí·É£·É†·É£.webp', '·Éô·Éò·É¢·É†·Éò.webp', '·Éô·Éù·Éì·Éê·Éö·Éê.webp',
        '·Éô·É£·É†·Éì·É¶·Éî·Éö·Éò.webp', '·Éö·Éù·Éõ·Éò.webp', '·Éõ·Éí·Éî·Éö·Éù.webp', '·Éõ·Éî·Éö·Éò·Éê.webp', '·Éõ·Éñ·Éî·É°·É£·Éõ·Éñ·Éò·É†·Éê.webp',
        '·Éõ·É°·ÉÆ·Éê·Éö·Éò.webp', '·Éú·Éê·Éß·Éò·Éú·Éò.webp', '·Éú·Éò·Éù·É†·Éò.webp', '·Éù·Éë·Éù·Éë·Éê.webp', '·Éû·Éî·Éû·Éî·Éö·Éê.webp',
        '·Éû·Éù·Éõ·Éò·Éì·Éù·É†·Éò.webp', '·Éü·Éò·É†·Éê·É§·Éò.webp', '·É†·Éô·Éù.webp', '·É°·Éò·Éõ·Éò·Éú·Éì·Éò.webp', '·É°·Éû·Éò·Éö·Éù.webp',
        '·É¢·Éò·É¢·Éê.webp', '·É§·Éù·É†·Éó·Éù·ÉÆ·Éê·Éö·Éò.webp', '·É•·Éö·Éò·Éê·Éï·Éò.webp', '·Éß·É£·É†·É´·Éî·Éú·Éò.webp', '·ÉÆ·Éê·ÉÆ·Éï·Éò.webp'
      ];

      return images.map(filename => ({
        name: filename.replace('.webp', ''),
        url: `${process.env.PUBLIC_URL}/context/newphotos/${filename}`
      }));
    } catch (error) {
      console.error('Error loading images:', error);
      return [];
    }
  }, []);

  // Fisher-Yates shuffle ·Éê·Éö·Éí·Éù·É†·Éò·Éó·Éõ·Éò
  const shuffleArray = useCallback((array) => {
    const shuffled = [...array];
    for (let i = shuffled.length - 1; i > 0; i--) {
      // ·Éï·Éò·Éß·Éî·Éú·Éî·Éë·Éó crypto.getRandomValues ·É£·É§·É†·Éù ·É´·Éö·Éò·Éî·É†·Éò ·É®·Éî·Éõ·Éó·ÉÆ·Éï·Éî·Éï·Éò·Éó·Éù·Éë·Éò·É°·Éó·Éï·Éò·É°
      const j = Math.floor(crypto.getRandomValues(new Uint32Array(1))[0] / (0xffffffff + 1) * (i + 1));
      [shuffled[i], shuffled[j]] = [shuffled[j], shuffled[i]];
    }

    // ·Éï·Éê·Éõ·Éù·É¨·Éõ·Éî·Éë·Éó ·É†·Éù·Éõ ·Éê·ÉÆ·Éê·Éö·Éò ·Éõ·Éê·É°·Éò·Éï·Éò ·Éê·É† ·Éò·Éß·Éù·É° ·Éò·Éí·Éò·Éï·Éî ·É†·Éê·É™ ·Éù·É†·Éò·Éí·Éò·Éú·Éê·Éö·Éò ·Éê·Éú ·É£·Éô·É£·É¶·Éõ·Éê ·Éì·Éê·Éö·Éê·Éí·Éî·Éë·É£·Éö·Éò
    const isReverse = shuffled.every((item, index) => item === array[array.length - 1 - index]);
    const isSame = shuffled.every((item, index) => item === array[index]);

    if (isReverse || isSame) {
      // ·Éó·É£ ·É£·Éô·É£·É¶·Éõ·Éê ·Éê·Éú ·Éò·Éí·Éò·Éï·Éî ·Éó·Éê·Éú·Éõ·Éò·Éõ·Éì·Éî·Éï·É†·Éù·Éë·Éê·Éê, ·Éô·Éò·Éì·Éî·Éï ·Éî·É†·Éó·ÉÆ·Éî·Éö ·Éï·É£·É†·Éî·Éï·Éó
      return shuffleArray(array);
    }

    return shuffled;
  }, []);

  // ·É†·Éî·Éú·Éì·Éù·Éõ ·É§·Éù·É¢·Éù·Éî·Éë·Éò·É° ·Éê·É†·É©·Éî·Éï·Éê
  const selectRandomImages = useCallback(async () => {
    const allImages = await loadImages();
    console.log('All images:', allImages);
    const shuffled = shuffleArray(allImages);
    const selected = shuffled.slice(0, numberOfImages);
    console.log('Selected images:', selected);
    setSelectedImages(selected);
    setShuffledImages([...selected]);
  }, [numberOfImages, loadImages, shuffleArray]);

  // ·Éó·Éê·Éõ·Éê·É®·Éò·É° ·Éì·Éê·É¨·Éß·Éî·Éë·Éê
  const startGame = useCallback(async () => {
    await selectRandomImages();
    setGameState('showing');
    setTimer(5);
  }, [selectRandomImages]);

  // ·É§·Éù·É¢·Éù·Éî·Éë·Éò·É° ·Éê·É†·Éî·Éï·Éê
  const shuffleImages = useCallback(() => {
    setShuffledImages(prev => {
      const shuffled = shuffleArray(prev);
      console.log('Original order:', prev.map(img => img.name));
      console.log('Shuffled order:', shuffled.map(img => img.name));
      return shuffled;
    });
    setGameState('playing');
  }, [shuffleArray]);

  // ·Éõ·Éù·Éõ·ÉÆ·Éõ·Éê·É†·Éî·Éë·Éö·Éò·É° ·Éê·É†·É©·Éî·Éï·Éê·Éú·Éò·É° ·Éì·Éê·Éõ·É£·É®·Éê·Éï·Éî·Éë·Éê
  const handleImageClick = useCallback((image, index) => {
    if (gameState !== 'playing') return;

    setUserSequence(prev => {
      const newSequence = [...prev, { image, index }];

      // ·Éó·É£ ·É°·É¨·Éù·É†·Éò ·Éó·Éê·Éú·Éõ·Éò·Éõ·Éì·Éî·Éï·É†·Éù·Éë·Éê·Éê
      if (newSequence.length === selectedImages.length) {
        const isCorrect = selectedImages.every((img, idx) => 
          newSequence[idx].image.name === img.name
        );

        if (isCorrect) {
          updateGameProgress('memory', Date.now(), { score: 100 });
          setShowSuccess(true);
          setTimeout(() => {
            setShowSuccess(false);
            setGameState('setup');
            setUserSequence([]);
          }, 2000);
        } else {
          setShowError(true);
          setTimeout(() => {
            setShowError(false);
            setUserSequence([]);
          }, 2000);
        }
      }

      return newSequence;
    });
  }, [gameState, selectedImages, updateGameProgress]);

  // ·É¢·Éê·Éò·Éõ·Éî·É†·Éò·É° ·Éö·Éù·Éí·Éò·Éô·Éê
  useEffect(() => {
    let interval;

    if ((gameState === 'showing' || gameState === 'memorizing') && timer > 0) {
      interval = setInterval(() => {
        setTimer(prev => {
          console.log('Timer tick:', prev - 1);
          return prev - 1;
        });
      }, 1000);

      console.log('Starting timer:', timer, 'State:', gameState);
    } else if (timer === 0) {
      if (gameState === 'showing') {
        console.log('Switching to memorizing');
        setGameState('memorizing');
        setTimer(2);
      } else if (gameState === 'memorizing') {
        console.log('Switching to playing');
        shuffleImages();
      }
    }

    return () => {
      if (interval) {
        clearInterval(interval);
      }
    };
  }, [timer, gameState, shuffleImages]);

  return (
    <GameContainer>
      <h1>·Éõ·Éî·ÉÆ·É°·Éò·Éî·É†·Éî·Éë·Éò·É° ·Éó·Éê·Éõ·Éê·É®·Éò üß†</h1>

      {showSuccess && (
        <SuccessMessage show>
          ·Éí·Éò·Éö·Éù·É™·Éê·Éï! ·É°·É¨·Éù·É†·Éê·Éì ·Éí·Éê·Éò·ÉÆ·É°·Éî·Éú·Éî ·Éó·Éê·Éú·Éõ·Éò·Éõ·Éì·Éî·Éï·É†·Éù·Éë·Éê! üéâ
        </SuccessMessage>
      )}

      {showError && (
        <ErrorMessage show>
          ·É°·Éê·Éõ·É¨·É£·ÉÆ·Éê·É†·Éù·Éì ·Éó·Éê·Éú·Éõ·Éò·Éõ·Éì·Éî·Éï·É†·Éù·Éë·Éê ·Éê·É†·Éê·É°·É¨·Éù·É†·Éò·Éê. ·É°·É™·Éê·Éì·Éî ·Éó·Éê·Éï·Éò·Éì·Éê·Éú! ü§î
        </ErrorMessage>
      )}

      {gameState === 'setup' && (
        <SetupContainer>
          <h2>·Éê·Éò·É†·É©·Éò·Éî ·É†·Éê·Éõ·Éì·Éî·Éú·Éò ·É°·É£·É†·Éê·Éó·Éò ·Éí·Éò·Éú·Éì·Éê ·Éì·Éê·Éò·Éõ·Éê·ÉÆ·É°·Éù·Éï·É†·Éù:</h2>
          <NumberSelect 
            value={numberOfImages} 
            onChange={(e) => setNumberOfImages(Number(e.target.value))}
          >
            <option value={3}>3 ·É°·É£·É†·Éê·Éó·Éò</option>
            <option value={4}>4 ·É°·É£·É†·Éê·Éó·Éò</option>
            <option value={5}>5 ·É°·É£·É†·Éê·Éó·Éò</option>
          </NumberSelect>
          <StartButton onClick={startGame}>·Éì·Éê·É¨·Éß·Éî·Éë·Éê</StartButton>
        </SetupContainer>
      )}

      {gameState === 'showing' && (
        <>
          <Timer>
            <span className="hourglass">‚åõ</span>
            {timer} ·É¨·Éê·Éõ·Éò
          </Timer>
          <Message show>·Éì·Éê·Éò·Éõ·Éê·ÉÆ·É°·Éù·Éï·É†·Éî ·É°·É£·É†·Éê·Éó·Éî·Éë·Éò·É° ·Éó·Éê·Éú·Éõ·Éò·Éõ·Éì·Éî·Éï·É†·Éù·Éë·Éê!</Message>
          <ImagesGrid>
            {selectedImages.map((image, index) => (
              <ImageCard key={index}>
                <img src={image.url} alt={image.name} />
                <p>{image.name}</p>
              </ImageCard>
            ))}
          </ImagesGrid>
        </>
      )}

      {gameState === 'memorizing' && (
        <>
          <Timer>
            <span className="hourglass">‚åõ</span>
            {timer} ·É¨·Éê·Éõ·Éò
          </Timer>
          <Message show>·Éì·Éê·ÉÆ·É£·É≠·Éî ·Éó·Éï·Éê·Éö·Éî·Éë·Éò! {timer} ·É¨·Éê·Éõ·Éò ·Éì·Éê·É†·É©·Éê</Message>
        </>
      )}

      {gameState === 'playing' && (
        <>
          <Message show>·Éê·É¶·Éê·Éì·Éí·Éò·Éú·Éî ·É°·É£·É†·Éê·Éó·Éî·Éë·Éò·É° ·Éó·Éê·Éú·Éõ·Éò·Éõ·Éì·Éî·Éï·É†·Éù·Éë·Éê!</Message>
          <ImagesGrid>
            {shuffledImages.map((image, index) => (
              <ImageCard 
                key={index}
                isSelected={userSequence.some(seq => seq.index === index)}
                isPlayable={!userSequence.some(seq => seq.index === index)}
                onClick={() => !userSequence.some(seq => seq.index === index) && handleImageClick(image, index)}
              >
                <img src={image.url} alt={image.name} />
                <p>{image.name}</p>
                {userSequence.findIndex(seq => seq.index === index) > -1 && (
                  <div className="order-number">
                    {userSequence.findIndex(seq => seq.index === index) + 1}
                  </div>
                )}
              </ImageCard>
            ))}
          </ImagesGrid>
          <Message show>
            {userSequence.length < selectedImages.length 
              ? `·Éê·Éò·É†·É©·Éò·Éî ${userSequence.length + 1}-·Éî ·É°·É£·É†·Éê·Éó·Éò`
