import React, { useState, useEffect, useCallback } from 'react';
import styled from 'styled-components';
import { useNavigate } from 'react-router-dom';
import { usePlayer } from '../context/PlayerContext';

const GameContainer = styled.div`
  padding: 20px;
  text-align: center;
`;

const SetupContainer = styled.div`
  margin: 20px auto;
  max-width: 400px;
`;

const NumberSelect = styled.select`
  padding: 10px;
  margin: 10px;
  font-size: 16px;
`;

const StartButton = styled.button`
  padding: 10px 20px;
  margin: 10px;
  font-size: 16px;
  background-color: #4CAF50;
  color: white;
  border: none;
  border-radius: 5px;
  cursor: pointer;

  &:hover {
    background-color: #45a049;
  }
`;

const ImagesGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(150px, 1fr));
  gap: 20px;
  margin: 20px auto;
  max-width: 800px;
`;

const ImageCard = styled.div`
  border: 2px solid ${props => props.isSelected ? '#4CAF50' : '#ddd'};
  border-radius: 8px;
  padding: 10px;
  cursor: pointer;
  transition: all 0.3s ease;

  &:hover {
    transform: ${props => props.isPlayable ? 'scale(1.05)' : 'none'};
  }

  img {
    width: 100%;
    height: 150px;
    object-fit: cover;
    border-radius: 4px;
  }

  p {
    margin: 10px 0;
    font-size: 16px;
  }
`;

const Timer = styled.div`
  font-size: 24px;
  margin: 20px;
  color: #333;
`;

const Message = styled.div`
  font-size: 24px;
  margin: 20px;
  color: #333;
  font-weight: bold;
`;

const MemoryGame = () => {
  const [numberOfImages, setNumberOfImages] = useState(3);
  const [selectedImages, setSelectedImages] = useState([]);
  const [shuffledImages, setShuffledImages] = useState([]);
  const [gameState, setGameState] = useState('setup'); // setup, showing, memorizing, playing
  const [timer, setTimer] = useState(5);
  const [userSequence, setUserSequence] = useState([]);
  const { updateGameProgress } = usePlayer();
  const navigate = useNavigate();

  // ფოტოების ჩატვირთვა
  const loadImages = useCallback(async () => {
    try {
      const response = await fetch('/newphotos/index.json');
      const images = await response.json();
      return images;
    } catch (error) {
      console.error('Error loading images:', error);
      return [];
    }
  }, []);

  // რენდომ ფოტოების არჩევა
  const selectRandomImages = useCallback(async () => {
    const allImages = await loadImages();
    const shuffled = [...allImages].sort(() => Math.random() - 0.5);
    const selected = shuffled.slice(0, numberOfImages);
    setSelectedImages(selected);
    setShuffledImages([...selected]);
  }, [numberOfImages]);

  // თამაშის დაწყება
  const startGame = useCallback(async () => {
    await selectRandomImages();
    setGameState('showing');
    setTimer(5);
  }, [selectRandomImages]);

  // ფოტოების არევა
  const shuffleImages = useCallback(() => {
    setShuffledImages(prev => [...prev].sort(() => Math.random() - 0.5));
    setGameState('playing');
  }, []);

  // მომხმარებლის არჩევანის დამუშავება
  const handleImageClick = useCallback((image) => {
    if (gameState !== 'playing') return;

    setUserSequence(prev => {
      const newSequence = [...prev, image];

      // თუ სწორი თანმიმდევრობაა
      if (newSequence.length === selectedImages.length) {
        const isCorrect = newSequence.every((img, index) => img === selectedImages[index]);

        if (isCorrect) {
          updateGameProgress('memory', Date.now(), { score: 100 });
          setTimeout(() => {
            alert('გილოცავ! სწორად გაიხსენე თანმიმდევრობა! 🎉');
            setGameState('setup');
            setUserSequence([]);
          }, 500);
        } else {
          setTimeout(() => {
            alert('სამწუხაროდ თანმიმდევრობა არასწორია. სცადე თავიდან! 🤔');
            setUserSequence([]);
          }, 500);
        }
      }

      return newSequence;
    });
  }, [gameState, selectedImages, updateGameProgress]);

  // ტაიმერის ლოგიკა
  useEffect(() => {
    let interval;

    if (gameState === 'showing' && timer > 0) {
      interval = setInterval(() => {
        setTimer(prev => prev - 1);
      }, 1000);
    } else if (timer === 0 && gameState === 'showing') {
      setGameState('memorizing');
      setTimer(2);
    } else if (timer === 0 && gameState === 'memorizing') {
      shuffleImages();
    }

    return () => clearInterval(interval);
  }, [timer, gameState, shuffleImages]);

  return (
    <GameContainer>
      <h1>მეხსიერების თამაში 🧠</h1>

      {gameState === 'setup' && (
        <SetupContainer>
          <h2>აირჩიე რამდენი სურათი გინდა დაიმახსოვრო:</h2>
          <NumberSelect 
            value={numberOfImages} 
            onChange={(e) => setNumberOfImages(Number(e.target.value))}
          >
            <option value={3}>3 სურათი</option>
            <option value={4}>4 სურათი</option>
            <option value={5}>5 სურათი</option>
          </NumberSelect>
          <StartButton onClick={startGame}>დაწყება</StartButton>
        </SetupContainer>
      )}

      {gameState === 'showing' && (
        <>
          <Timer>{timer} წამი</Timer>
          <Message>დაიმახსოვრე სურათების თანმიმდევრობა!</Message>
          <ImagesGrid>
            {selectedImages.map((image, index) => (
              <ImageCard key={index}>
                <img src={image.url} alt={image.name} />
                <p>{image.name}</p>
              </ImageCard>
            ))}
          </ImagesGrid>
        </>
      )}

      {gameState === 'memorizing' && (
        <>
          <Timer>{timer} წამი</Timer>
          <Message>დახუჭე თვალები!</Message>
        </>
      )}

      {gameState === 'playing' && (
        <>
          <Message>აღადგინე სურათების თანმიმდევრობა!</Message>
          <ImagesGrid>
            {shuffledImages.map((image, index) => (
              <ImageCard 
                key={index}
                isSelected={userSequence.includes(image)}
                isPlayable={true}
                onClick={() => handleImageClick(image)}
              >
                <img src={image.url} alt={image.name} />
                <p>{image.name}</p>
              </ImageCard>
            ))}
          </ImagesGrid>
        </>
      )}
    </GameContainer>
  );
};

export default MemoryGame;
