import React, { useState, useEffect, useCallback } from 'react';
import styled from 'styled-components';
import { useNavigate } from 'react-router-dom';
import { usePlayer } from '../context/PlayerContext';

const GameContainer = styled.div`
  padding: 20px;
  text-align: center;
`;

const SetupContainer = styled.div`
  margin: 20px auto;
  max-width: 400px;
`;

const NumberSelect = styled.select`
  padding: 10px;
  margin: 10px;
  font-size: 16px;
`;

const StartButton = styled.button`
  padding: 10px 20px;
  margin: 10px;
  font-size: 16px;
  background-color: #4CAF50;
  color: white;
  border: none;
  border-radius: 5px;
  cursor: pointer;

  &:hover {
    background-color: #45a049;
  }
`;

const ImagesGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(150px, 1fr));
  gap: 20px;
  margin: 20px auto;
  max-width: 800px;
`;

const ImageCard = styled.div`
  border: 2px solid ${props => props.isSelected ? '#4CAF50' : '#ddd'};
  border-radius: 8px;
  padding: 10px;
  cursor: pointer;
  transition: all 0.3s ease;

  &:hover {
    transform: ${props => props.isPlayable ? 'scale(1.05)' : 'none'};
  }

  img {
    width: 100%;
    height: 150px;
    object-fit: cover;
    border-radius: 4px;
  }

  p {
    margin: 10px 0;
    font-size: 16px;
  }
`;

const Timer = styled.div`
  font-size: 24px;
  margin: 20px;
  color: #333;
`;

const Message = styled.div`
  font-size: 24px;
  margin: 20px;
  color: #333;
  font-weight: bold;
`;

const MemoryGame = () => {
  const [numberOfImages, setNumberOfImages] = useState(3);
  const [selectedImages, setSelectedImages] = useState([]);
  const [shuffledImages, setShuffledImages] = useState([]);
  const [gameState, setGameState] = useState('setup'); // setup, showing, memorizing, playing
  const [timer, setTimer] = useState(5);
  const [userSequence, setUserSequence] = useState([]);
  const { updateGameProgress } = usePlayer();
  const navigate = useNavigate();

  // ·É§·Éù·É¢·Éù·Éî·Éë·Éò·É° ·É©·Éê·É¢·Éï·Éò·É†·Éó·Éï·Éê
  const loadImages = useCallback(async () => {
    try {
      const response = await fetch('/newphotos/index.json');
      const images = await response.json();
      return images;
    } catch (error) {
      console.error('Error loading images:', error);
      return [];
    }
  }, []);

  // ·É†·Éî·Éú·Éì·Éù·Éõ ·É§·Éù·É¢·Éù·Éî·Éë·Éò·É° ·Éê·É†·É©·Éî·Éï·Éê
  const selectRandomImages = useCallback(async () => {
    const allImages = await loadImages();
    const shuffled = [...allImages].sort(() => Math.random() - 0.5);
    const selected = shuffled.slice(0, numberOfImages);
    setSelectedImages(selected);
    setShuffledImages([...selected]);
  }, [numberOfImages]);

  // ·Éó·Éê·Éõ·Éê·É®·Éò·É° ·Éì·Éê·É¨·Éß·Éî·Éë·Éê
  const startGame = useCallback(async () => {
    await selectRandomImages();
    setGameState('showing');
    setTimer(5);
  }, [selectRandomImages]);

  // ·É§·Éù·É¢·Éù·Éî·Éë·Éò·É° ·Éê·É†·Éî·Éï·Éê
  const shuffleImages = useCallback(() => {
    setShuffledImages(prev => [...prev].sort(() => Math.random() - 0.5));
    setGameState('playing');
  }, []);

  // ·Éõ·Éù·Éõ·ÉÆ·Éõ·Éê·É†·Éî·Éë·Éö·Éò·É° ·Éê·É†·É©·Éî·Éï·Éê·Éú·Éò·É° ·Éì·Éê·Éõ·É£·É®·Éê·Éï·Éî·Éë·Éê
  const handleImageClick = useCallback((image) => {
    if (gameState !== 'playing') return;

    setUserSequence(prev => {
      const newSequence = [...prev, image];

      // ·Éó·É£ ·É°·É¨·Éù·É†·Éò ·Éó·Éê·Éú·Éõ·Éò·Éõ·Éì·Éî·Éï·É†·Éù·Éë·Éê·Éê
      if (newSequence.length === selectedImages.length) {
        const isCorrect = newSequence.every((img, index) => img === selectedImages[index]);

        if (isCorrect) {
          updateGameProgress('memory', Date.now(), { score: 100 });
          setTimeout(() => {
            alert('·Éí·Éò·Éö·Éù·É™·Éê·Éï! ·É°·É¨·Éù·É†·Éê·Éì ·Éí·Éê·Éò·ÉÆ·É°·Éî·Éú·Éî ·Éó·Éê·Éú·Éõ·Éò·Éõ·Éì·Éî·Éï·É†·Éù·Éë·Éê! üéâ');
            setGameState('setup');
            setUserSequence([]);
          }, 500);
        } else {
          setTimeout(() => {
            alert('·É°·Éê·Éõ·É¨·É£·ÉÆ·Éê·É†·Éù·Éì ·Éó·Éê·Éú·Éõ·Éò·Éõ·Éì·Éî·Éï·É†·Éù·Éë·Éê ·Éê·É†·Éê·É°·É¨·Éù·É†·Éò·Éê. ·É°·É™·Éê·Éì·Éî ·Éó·Éê·Éï·Éò·Éì·Éê·Éú! ü§î');
            setUserSequence([]);
          }, 500);
        }
      }

      return newSequence;
    });
  }, [gameState, selectedImages, updateGameProgress]);

  // ·É¢·Éê·Éò·Éõ·Éî·É†·Éò·É° ·Éö·Éù·Éí·Éò·Éô·Éê
  useEffect(() => {
    let interval;

    if (gameState === 'showing' && timer > 0) {
      interval = setInterval(() => {
        setTimer(prev => prev - 1);
      }, 1000);
    } else if (timer === 0 && gameState === 'showing') {
      setGameState('memorizing');
      setTimer(2);
    } else if (timer === 0 && gameState === 'memorizing') {
      shuffleImages();
    }

    return () => clearInterval(interval);
  }, [timer, gameState, shuffleImages]);

  return (
    <GameContainer>
      <h1>·Éõ·Éî·ÉÆ·É°·Éò·Éî·É†·Éî·Éë·Éò·É° ·Éó·Éê·Éõ·Éê·É®·Éò üß†</h1>

      {gameState === 'setup' && (
        <SetupContainer>
          <h2>·Éê·Éò·É†·É©·Éò·Éî ·É†·Éê·Éõ·Éì·Éî·Éú·Éò ·É°·É£·É†·Éê·Éó·Éò ·Éí·Éò·Éú·Éì·Éê ·Éì·Éê·Éò·Éõ·Éê·ÉÆ·É°·Éù·Éï·É†·Éù:</h2>
          <NumberSelect 
            value={numberOfImages} 
            onChange={(e) => setNumberOfImages(Number(e.target.value))}
          >
            <option value={3}>3 ·É°·É£·É†·Éê·Éó·Éò</option>
            <option value={4}>4 ·É°·É£·É†·Éê·Éó·Éò</option>
            <option value={5}>5 ·É°·É£·É†·Éê·Éó·Éò</option>
          </NumberSelect>
          <StartButton onClick={startGame}>·Éì·Éê·É¨·Éß·Éî·Éë·Éê</StartButton>
        </SetupContainer>
      )}

      {gameState === 'showing' && (
        <>
          <Timer>{timer} ·É¨·Éê·Éõ·Éò</Timer>
          <Message>·Éì·Éê·Éò·Éõ·Éê·ÉÆ·É°·Éù·Éï·É†·Éî ·É°·É£·É†·Éê·Éó·Éî·Éë·Éò·É° ·Éó·Éê·Éú·Éõ·Éò·Éõ·Éì·Éî·Éï·É†·Éù·Éë·Éê!</Message>
          <ImagesGrid>
            {selectedImages.map((image, index) => (
              <ImageCard key={index}>
                <img src={image.url} alt={image.name} />
                <p>{image.name}</p>
              </ImageCard>
            ))}
          </ImagesGrid>
        </>
      )}

      {gameState === 'memorizing' && (
        <>
          <Timer>{timer} ·É¨·Éê·Éõ·Éò</Timer>
          <Message>·Éì·Éê·ÉÆ·É£·É≠·Éî ·Éó·Éï·Éê·Éö·Éî·Éë·Éò!</Message>
        </>
      )}

      {gameState === 'playing' && (
        <>
          <Message>·Éê·É¶·Éê·Éì·Éí·Éò·Éú·Éî ·É°·É£·É†·Éê·Éó·Éî·Éë·Éò·É° ·Éó·Éê·Éú·Éõ·Éò·Éõ·Éì·Éî·Éï·É†·Éù·Éë·Éê!</Message>
          <ImagesGrid>
            {shuffledImages.map((image, index) => (
              <ImageCard 
                key={index}
                isSelected={userSequence.includes(image)}
                isPlayable={true}
                onClick={() => handleImageClick(image)}
              >
                <img src={image.url} alt={image.name} />
                <p>{image.name}</p>
              </ImageCard>
            ))}
          </ImagesGrid>
        </>
      )}
    </GameContainer>
  );
};

export default MemoryGame;
