import React, { useState } from 'react';
import styled from 'styled-components';
import { usePlayer } from '../context/PlayerContext';

const DrawingGame = () => {
  const { updateGameProgress } = usePlayer();
  const [selectedColor, setSelectedColor] = useState(null);
  const [showFeedback, setShowFeedback] = useState(false);
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  const colors = [
    { value: '#FF0000' }, // წითელი
    { value: '#FFA500' }, // ნარინჯისფერი
    { value: '#FFFF00' }, // ყვითელი
    { value: '#008000' }, // მწვანე
    { value: '#0000FF' }, // ლურჯი
    { value: '#4B0082' }, // იისფერი
    { value: '#EE82EE' }  // ვარდისფერი
  ];

  const handleColorSelect = (color) => {
    setSelectedColor(color.value);
  };

  return (
    <Container>
      <Title>ხატვა</Title>
      
      <GameArea>
        <DrawingArea style={{ backgroundColor: selectedColor || 'transparent' }}>
          <OutlineImage 
            src={`/assets/images/outline-${currentImageIndex + 1}.png`} 
            alt="გასაფერადებელი სურათი" 
          />
        </DrawingArea>

        <ColorPalette>
          {colors.map((color, index) => (
            <ColorButton
              key={index}
              color={color.value}
              onClick={() => handleColorSelect(color)}
            />
          ))}
        </ColorPalette>
      </GameArea>
    </Container>
  );
};

const Container = styled.div`
  width: 100%;
  padding: 2rem;
`;

const Title = styled.h1`
  text-align: center;
  color: #333;
  margin-bottom: 2rem;
`;

const GameArea = styled.div`
  max-width: 800px;
  margin: 0 auto;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 2rem;
`;

const DrawingArea = styled.div`
  width: 100%;
  max-width: 500px;
  aspect-ratio: 1;
  border: 2px solid #e0e0e0;
  border-radius: 8px;
  display: flex;
  justify-content: center;
  align-items: center;
  background: white;
  padding: 1rem;
`;

const OutlineImage = styled.img`
  width: 100%;
  height: 100%;
  object-fit: contain;
`;

const ColorPalette = styled.div`
  display: flex;
  gap: 1rem;
  flex-wrap: wrap;
  justify-content: center;
`;

const ColorButton = styled.button`
  width: 50px;
  height: 50px;
  border: none;
  border-radius: 8px;
  background-color: ${props => props.color};
  cursor: pointer;
  transition: transform 0.2s;

  &:hover {
    transform: scale(1.1);
  }
`;

export default DrawingGame;
