import React, { useState, useRef, useEffect } from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';

const Container = styled.div`
  min-height: 100vh;
  padding: 2rem;
  background: linear-gradient(135deg, #fff6e5, #ffecf2);
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const Title = styled.h1`
  color: #FF4081;
  text-align: center;
  margin-bottom: 2rem;
  font-size: 2.5rem;
  text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.1);
`;

const GameContainer = styled.div`
  background: white;
  border-radius: 20px;
  padding: 2rem;
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1);
  max-width: 1200px;
  width: 100%;
  margin: 0 auto;
`;

const CanvasContainer = styled.div`
  position: relative;
  margin: 2rem 0;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const Canvas = styled.canvas`
  border: 2px solid #eee;
  border-radius: 10px;
  cursor: crosshair;
`;

const ColorPalette = styled.div`
  display: flex;
  gap: 1rem;
  flex-wrap: wrap;
  justify-content: center;
  margin: 2rem 0;
`;

const ColorButton = styled(motion.button)`
  width: 50px;
  height: 50px;
  border-radius: 50%;
  border: 3px solid ${props => props.isSelected ? '#333' : 'transparent'};
  background-color: ${props => props.color};
  cursor: pointer;
  transition: transform 0.2s ease;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);

  &:hover {
    transform: scale(1.1);
  }
`;

const ToolsContainer = styled.div`
  display: flex;
  gap: 1rem;
  justify-content: center;
  margin-bottom: 2rem;
`;

const ToolButton = styled.button`
  padding: 0.5rem 1rem;
  border: none;
  border-radius: 8px;
  background: ${props => props.isSelected ? '#FF4081' : '#f0f0f0'};
  color: ${props => props.isSelected ? 'white' : '#333'};
  cursor: pointer;
  transition: all 0.2s ease;
  display: flex;
  align-items: center;
  gap: 0.5rem;

  &:hover {
    background: ${props => props.isSelected ? '#FF4081' : '#e0e0e0'};
    transform: translateY(-2px);
  }
`;

const colors = [
  '#FF4081', // Pink
  '#3F51B5', // Indigo
  '#4CAF50', // Green
  '#FFC107', // Amber
  '#9C27B0', // Purple
  '#F44336', // Red
  '#2196F3', // Blue
  '#FF9800', // Orange
  '#795548', // Brown
  '#607D8B', // Blue Grey
  '#000000', // Black
  '#FFFFFF', // White
];

const ColoringGame = () => {
  const [selectedColor, setSelectedColor] = useState('#FF4081');
  const [isDrawing, setIsDrawing] = useState(false);
  const [brushSize, setBrushSize] = useState(5);
  const canvasRef = useRef(null);
  const contextRef = useRef(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    canvas.width = 800;
    canvas.height = 600;
    
    const context = canvas.getContext('2d');
    context.lineCap = 'round';
    context.strokeStyle = selectedColor;
    context.lineWidth = brushSize;
    contextRef.current = context;

    // Draw initial white background
    context.fillStyle = 'white';
    context.fillRect(0, 0, canvas.width, canvas.height);

    // Draw example shape to color
    context.beginPath();
    context.strokeStyle = '#ccc';
    context.lineWidth = 2;
    context.moveTo(200, 200);
    context.lineTo(600, 200);
    context.lineTo(400, 400);
    context.closePath();
    context.stroke();
  }, []);

  useEffect(() => {
    if (contextRef.current) {
      contextRef.current.strokeStyle = selectedColor;
      contextRef.current.lineWidth = brushSize;
    }
  }, [selectedColor, brushSize]);

  const startDrawing = ({ nativeEvent }) => {
    const { offsetX, offsetY } = nativeEvent;
    contextRef.current.beginPath();
    contextRef.current.moveTo(offsetX, offsetY);
    setIsDrawing(true);
  };

  const draw = ({ nativeEvent }) => {
    if (!isDrawing) return;
    const { offsetX, offsetY } = nativeEvent;
    contextRef.current.lineTo(offsetX, offsetY);
    contextRef.current.stroke();
  };

  const stopDrawing = () => {
    contextRef.current.closePath();
    setIsDrawing(false);
  };

  return (
    <Container>
      <Title>🎨 გააფერადე და ისწავლე</Title>
      
      <GameContainer>
        <ToolsContainer>
          <ToolButton 
            onClick={() => setBrushSize(5)}
            isSelected={brushSize === 5}
          >
            🖌️ პატარა ფუნჯი
          </ToolButton>
          <ToolButton 
            onClick={() => setBrushSize(15)}
            isSelected={brushSize === 15}
          >
            🖌️ საშუალო ფუნჯი
          </ToolButton>
          <ToolButton 
            onClick={() => setBrushSize(30)}
            isSelected={brushSize === 30}
          >
            🖌️ დიდი ფუნჯი
          </ToolButton>
        </ToolsContainer>

        <ColorPalette>
          {colors.map((color) => (
            <ColorButton
              key={color}
              color={color}
              isSelected={selectedColor === color}
              onClick={() => setSelectedColor(color)}
              whileHover={{ scale: 1.1 }}
              whileTap={{ scale: 0.95 }}
            />
          ))}
        </ColorPalette>

        <CanvasContainer>
          <Canvas
            ref={canvasRef}
            onMouseDown={startDrawing}
            onMouseMove={draw}
            onMouseUp={stopDrawing}
            onMouseLeave={stopDrawing}
          />
        </CanvasContainer>
      </GameContainer>
    </Container>
  );
};

export default ColoringGame;
