import React, { useState, useEffect, useCallback } from 'react';
import styled from 'styled-components';
import { usePlayer } from '../context/PlayerContext';

const Container = styled.div`
  padding: 2rem;
  max-width: 800px;
  margin: 0 auto;
`;

const Timer = styled.div`
  font-size: 2rem;
  text-align: center;
  margin-bottom: 2rem;
  color: #2196F3;
  font-weight: bold;
`;

const GameArea = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 2rem;
  margin-bottom: 2rem;
`;

const Column = styled.div`
  display: flex;
  flex-direction: column;
  gap: 1rem;
`;

const ColorItem = styled.div`
  padding: 1rem;
  border-radius: 0.5rem;
  cursor: pointer;
  text-align: center;
  transition: all 0.3s ease;
  user-select: none;

  ${props => props.isSelected && `
    transform: scale(1.05);
    box-shadow: 0 0 10px rgba(0,0,0,0.2);
  `}

  ${props => props.isMatched && `
    opacity: 0.5;
    pointer-events: none;
  `}
`;

const ColorBox = styled(ColorItem)`
  height: 80px;
  background-color: ${props => props.color};
  border: 2px solid #ddd;
`;

const ColorName = styled(ColorItem)`
  background: white;
  border: 2px solid #2196F3;
  font-weight: bold;
  color: #2196F3;
`;

const Score = styled.div`
  text-align: center;
  font-size: 1.5rem;
  margin-bottom: 1rem;
  color: #2196F3;
`;

const Button = styled.button`
  background: #2196F3;
  color: white;
  border: none;
  padding: 1rem 2rem;
  border-radius: 0.5rem;
  font-size: 1.2rem;
  cursor: pointer;
  margin: 0 auto;
  display: block;

  &:hover {
    background: #1976D2;
  }

  &:disabled {
    background: #ccc;
    cursor: not-allowed;
  }
`;

const COLORS = [
  { name: 'წითელი', hex: '#FF0000' },
  { name: 'ლურჯი', hex: '#0000FF' },
  { name: 'მწვანე', hex: '#00FF00' },
  { name: 'ყვითელი', hex: '#FFFF00' },
  { name: 'შავი', hex: '#000000' },
  { name: 'თეთრი', hex: '#FFFFFF' },
  { name: 'ნარინჯისფერი', hex: '#FFA500' },
  { name: 'იისფერი', hex: '#800080' },
];

const ColorMatchingGame = () => {
  const [timeLeft, setTimeLeft] = useState(60);
  const [isPlaying, setIsPlaying] = useState(false);
  const [score, setScore] = useState(0);
  const [selectedColor, setSelectedColor] = useState(null);
  const [selectedName, setSelectedName] = useState(null);
  const [matchedPairs, setMatchedPairs] = useState([]);
  const { updateStats } = usePlayer();

  const shuffledColors = useCallback(() => {
    return [...COLORS].sort(() => Math.random() - 0.5);
  }, []);

  const [colorList, setColorList] = useState(shuffledColors());
  const [nameList, setNameList] = useState(shuffledColors());

  useEffect(() => {
    let timer;
    if (isPlaying && timeLeft > 0) {
      timer = setInterval(() => {
        setTimeLeft(prev => prev - 1);
      }, 1000);
    } else if (timeLeft === 0) {
      handleGameEnd();
    }
    return () => clearInterval(timer);
  }, [isPlaying, timeLeft]);

  const handleGameEnd = () => {
    setIsPlaying(false);
    updateStats({
      colorMatching: {
        score,
        matchedPairs: matchedPairs.length,
        timestamp: new Date().toISOString(),
      }
    });
  };

  const startGame = () => {
    setTimeLeft(60);
    setScore(0);
    setMatchedPairs([]);
    setSelectedColor(null);
    setSelectedName(null);
    setColorList(shuffledColors());
    setNameList(shuffledColors());
    setIsPlaying(true);
  };

  const checkMatch = (colorHex, colorName) => {
    const matchingColor = COLORS.find(c => c.hex === colorHex && c.name === colorName);
    if (matchingColor) {
      setScore(prev => prev + 10);
      setMatchedPairs(prev => [...prev, colorHex]);
      setSelectedColor(null);
      setSelectedName(null);
    } else {
      setScore(prev => Math.max(0, prev - 5));
      setTimeout(() => {
        setSelectedColor(null);
        setSelectedName(null);
      }, 500);
    }
  };

  const handleColorClick = (color) => {
    if (!isPlaying) return;
    setSelectedColor(color.hex);
    if (selectedName) {
      checkMatch(color.hex, selectedName);
    }
  };

  const handleNameClick = (color) => {
    if (!isPlaying) return;
    setSelectedName(color.name);
    if (selectedColor) {
      checkMatch(selectedColor, color.name);
    }
  };

  return (
    <Container>
      <Timer>{Math.floor(timeLeft / 60)}:{(timeLeft % 60).toString().padStart(2, '0')}</Timer>
      <Score>ქულა: {score}</Score>

      <GameArea>
        <Column>
          {colorList.map((color, index) => (
            <ColorBox
              key={index}
              color={color.hex}
              isSelected={selectedColor === color.hex}
              isMatched={matchedPairs.includes(color.hex)}
              onClick={() => handleColorClick(color)}
            />
          ))}
        </Column>
        <Column>
          {nameList.map((color, index) => (
            <ColorName
              key={index}
              isSelected={selectedName === color.name}
              isMatched={matchedPairs.includes(color.hex)}
              onClick={() => handleNameClick(color)}
            >
              {color.name}
            </ColorName>
          ))}
        </Column>
      </GameArea>

      <Button
        onClick={startGame}
        disabled={isPlaying}
      >
        {isPlaying ? 'თამაში მიმდინარეობს...' : 'თამაშის დაწყება'}
      </Button>
    </Container>
  );
};

export default ColorMatchingGame;
