import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { usePlayer } from '../context/PlayerContext';

const DrawingGame = () => {
  const { updateGameProgress } = usePlayer();
  const [selectedColor, setSelectedColor] = useState('#FF0000');
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const [images, setImages] = useState([]);

  useEffect(() => {
    // ვტვირთავთ ყველა სურათს ფოლდერიდან
    const loadImages = async () => {
      try {
        const imageContext = require.context('../assets/png', false, /\.(png)$/);
        const imageList = imageContext.keys().map(key => ({
          src: imageContext(key),
          name: key.replace('./', '').replace('.png', '')
        }));
        setImages(imageList);
      } catch (error) {
        console.error('Error loading images:', error);
      }
    };
    
    loadImages();
  }, []);

  const nextImage = () => {
    setCurrentImageIndex((prev) => (prev + 1) % images.length);
  };

  const prevImage = () => {
    setCurrentImageIndex((prev) => (prev - 1 + images.length) % images.length);
  };

  const colors = [
    '#FF0000', // წითელი
    '#FFA500', // ნარინჯისფერი
    '#FFFF00', // ყვითელი
    '#008000', // მწვანე
    '#0000FF', // ლურჯი
    '#4B0082', // იისფერი
    '#EE82EE'  // ვარდისფერი
  ];

  return (
    <Container>
      <Title>გააფერადე</Title>
      
      <GameArea>
        {/* გაფერადებული სურათი ზედა მარჯვენა კუთხეში */}
        {images[currentImageIndex] && (
          <ExampleImage>
            <img 
              src={images[currentImageIndex].src} 
              alt="სანიმუშო"
              style={{ filter: `opacity(0.7) drop-shadow(0 0 0 ${selectedColor})` }}
            />
          </ExampleImage>
        )}

        {/* გასაფერადებელი სურათი ცენტრში */}
        <DrawingArea>
          {images[currentImageIndex] && (
            <OutlineImage
              src={images[currentImageIndex].src}
              alt="გასაფერადებელი"
              style={{ filter: `opacity(0.7) drop-shadow(0 0 0 ${selectedColor})` }}
            />
          )}
        </DrawingArea>

        {/* ფერების პალიტრა */}
        <ColorPalette>
          {colors.map((color) => (
            <ColorButton
              key={color}
              color={color}
              selected={selectedColor === color}
              onClick={() => setSelectedColor(color)}
            />
          ))}
        </ColorPalette>

        {/* სურათების გადართვის ღილაკები */}
        <NavigationButtons>
          <NavButton onClick={prevImage}>წინა</NavButton>
          <NavButton onClick={nextImage}>შემდეგი</NavButton>
        </NavigationButtons>
      </GameArea>
    </Container>
  );
};

const Container = styled.div`
  padding: 2rem;
`;

const Title = styled.h1`
  text-align: center;
  margin-bottom: 2rem;
`;

const GameArea = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 2rem;
  position: relative;
`;

const ExampleImage = styled.div`
  position: absolute;
  top: 2rem;
  right: 2rem;
  width: 150px;
  height: 150px;

  img {
    width: 100%;
    height: 100%;
    object-fit: contain;
  }
`;

const DrawingArea = styled.div`
  width: 500px;
  height: 500px;
  border: 2px solid #e0e0e0;
  border-radius: 8px;
  display: flex;
  justify-content: center;
  align-items: center;
  background: white;
`;

const OutlineImage = styled.img`
  max-width: 90%;
  max-height: 90%;
  object-fit: contain;
`;

const ColorPalette = styled.div`
  display: flex;
  gap: 1rem;
  flex-wrap: wrap;
  justify-content: center;
`;

const ColorButton = styled.button`
  width: 50px;
  height: 50px;
  border: 3px solid ${props => props.selected ? '#000' : 'transparent'};
  border-radius: 8px;
  background-color: ${props => props.color};
  cursor: pointer;
  transition: transform 0.2s;

  &:hover {
    transform: scale(1.1);
  }
`;

const NavigationButtons = styled.div`
  display: flex;
  gap: 1rem;
`;

const NavButton = styled.button`
  padding: 0.5rem 1rem;
  border: none;
  border-radius: 8px;
  background: #4CAF50;
  color: white;
  cursor: pointer;
  transition: all 0.2s;

  &:hover {
    background: #45a049;
  }
`;

export default DrawingGame;
