import React, { useState, useRef, useEffect } from 'react';
import styled from 'styled-components';

const Container = styled.div`
  min-height: 100vh;
  padding: 1rem;
  background: white;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const Title = styled.h1`
  color: #333;
  text-align: center;
  margin-bottom: 1rem;
  font-size: 2rem;
`;

const GameArea = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 100%;
  max-width: 800px;
`;

const TopSection = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 1rem;
  width: 100%;
  margin-bottom: 2rem;
`;

const ExampleHeart = styled.div`
  width: 100px;
  height: 100px;
  background-color: #FF0000;
  clip-path: path('M50,15 A25,25,0,0,1,75,50 A25,25,0,0,1,50,85 A25,25,0,0,1,25,50 A25,25,0,0,1,50,15 Z');
`;

const Canvas = styled.canvas`
  border: 2px solid #ddd;
  border-radius: 8px;
  margin: 2rem 0;
`;

const ColorPalette = styled.div`
  display: flex;
  gap: 2rem;
  justify-content: center;
  margin-top: 2rem;
`;

const ColorOption = styled.button`
  width: 100px;
  height: 50px;
  background-color: ${props => props.color};
  border: none;
  border-radius: 8px;
  cursor: pointer;
  transition: transform 0.2s;

  &:hover {
    transform: scale(1.05);
  }
`;

const Message = styled.div`
  font-size: 1.5rem;
  color: ${props => props.isCorrect ? '#4CAF50' : '#FF4081'};
  margin-top: 1rem;
  font-weight: bold;
`;

const colors = [
  '#00FF00', // Green
  '#FF6B00', // Orange
  '#FF0000', // Red
];

const ColoringGame = () => {
  const [selectedColor, setSelectedColor] = useState(null);
  const [isCorrect, setIsCorrect] = useState(null);
  const canvasRef = useRef(null);

  const drawHeart = (context, filled = false, color = '#FF0000') => {
    const width = context.canvas.width;
    const height = context.canvas.height;
    const size = Math.min(width, height) * 0.4;
    const x = width / 2;
    const y = height / 2;

    context.beginPath();
    context.moveTo(x, y + size * 0.3);
    context.bezierCurveTo(
      x + size * 0.4, y, 
      x + size * 0.5, y - size * 0.4, 
      x, y - size * 0.5
    );
    context.bezierCurveTo(
      x - size * 0.5, y - size * 0.4, 
      x - size * 0.4, y, 
      x, y + size * 0.3
    );
    
    if (filled) {
      context.fillStyle = color;
      context.fill();
    }
    context.strokeStyle = '#FF0000';
    context.lineWidth = 3;
    context.stroke();
  };

  useEffect(() => {
    const canvas = canvasRef.current;
    canvas.width = 600;
    canvas.height = 400;
    const ctx = canvas.getContext('2d');
    drawHeart(ctx, false);
  }, []);

  const handleColorSelect = (color) => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    ctx.clearRect(0, 0, canvas.width, canvas.height);
    drawHeart(ctx, true, color);
    setSelectedColor(color);
    
    if (color === '#FF0000') {
      setIsCorrect(true);
    } else {
      setIsCorrect(false);
    }
  };

  return (
    <Container>
      <Title>ხატვა</Title>
      <GameArea>
        <TopSection>
          <ExampleHeart />
        </TopSection>
        
        <Canvas ref={canvasRef} />

        <ColorPalette>
          {colors.map((color) => (
            <ColorOption
              key={color}
              color={color}
              onClick={() => handleColorSelect(color)}
            />
          ))}
        </ColorPalette>

        {isCorrect !== null && (
          <Message isCorrect={isCorrect}>
            {isCorrect ? '🎉 ყოჩაღ! სწორი ფერი აირჩიე!' : '❌ სცადე თავიდან'}
          </Message>
        )}
      </GameArea>
    </Container>
  );
};

export default ColoringGame;
