import React, { useState, useEffect } from 'react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import '../styles/ScrambleGame.css';

const animals = [
  { name: '·Éñ·Éî·Éë·É†·Éê', image: '·Éñ·Éî·Éë·É†·Éê.webp' },
  { name: '·É°·Éû·Éò·Éö·Éù', image: '·É°·Éû·Éò·Éö·Éù.webp' },
  { name: '·Éö·Éù·Éõ·Éò', image: '·Éö·Éù·Éõ·Éò.webp' },
  { name: '·Éì·Éê·Éó·Éï·Éò', image: '·Éì·Éê·Éó·Éï·Éò.webp' },
  { name: '·Éõ·Éí·Éî·Éö·Éò', image: '·Éõ·Éí·Éî·Éö·Éù.webp' },
  { name: '·Éò·É†·Éî·Éõ·Éò', image: '·Éò·É†·Éî·Éõ·Éò.webp' },
  { name: '·Éó·ÉÆ·Éê', image: '·Éó·ÉÆ·Éê.webp' },
  { name: '·Éô·É£·É†·Éì·É¶·Éî·Éö·Éò', image: '·Éô·É£·É†·Éì·É¶·Éî·Éö·Éò.webp' },
  { name: '·É§·Éö·Éê·Éõ·Éò·Éú·Éí·Éù', image: '·É§·Éö·Éê·Éõ·Éò·Éú·Éí·Éù.webp' },
  { name: '·É°·Éî·Éö·Éê·Éû·Éò', image: '·É°·Éî·Éö·Éê·Éû·Éò.webp' },
  { name: '·É™·Éò·Éß·Éï·Éò', image: '·É™·Éò·Éß·Éï·Éò.webp' },
  { name: '·Éî·Éú·Éù·É¢·Éò', image: '·Éî·Éú·Éù·É¢·Éò.webp' },
  { name: '·Éñ·Éê·É†·Éõ·Éê·É™·Éê', image: '·Éñ·Éê·É†·Éõ·Éê·É™·Éê.webp' },
  { name: '·Éñ·É¶·Éê·É†·Éë·Éò', image: '·Éñ·É¶·Éê·É†·Éë·Éò.webp' },
  { name: '·É•·Éê·Éõ·Éî·Éö·Éî·Éù·Éú·Éò', image: '·É•·Éê·Éõ·Éî·Éö·Éî·Éù·Éú·Éò.webp' },
  { name: '·Éù·Éû·Éù·É°·É£·Éõ·Éò', image: '·Éù·Éû·Éù·É°·É£·Éõ·Éò.webp' },
  { name: '·É†·Éï·Éê·É§·Éî·ÉÆ·Éê', image: '·É†·Éï·Éê·É§·Éî·ÉÆ·Éê.webp' },
  { name: '·Éó·Éê·ÉÆ·Éï·Éò', image: '·Éó·Éê·ÉÆ·Éï·Éò.webp' },
  { name: '·Éë·Éî·Éí·Éî·Éõ·Éù·É¢·Éò', image: '·Éë·Éî·Éí·Éî·Éõ·Éù·É¢·Éò.webp' },
  { name: '·Éö·Éê·Éõ·Éê', image: '·Éö·Éê·Éõ·Éê.webp' },
  { name: '·Éü·Éò·É†·Éê·É§·Éò', image: '·Éü·Éò·É†·Éê·É§·Éò.webp' },
  { name: '·Éô·Éî·Éú·Éí·É£·É†·É£', image: '·Éô·Éî·Éú·Éí·É£·É†·É£.webp' },
  { name: '·Éë·É£', image: '·Éë·É£.webp' },
  { name: '·Éû·Éê·Éú·Éì·Éê', image: '·Éû·Éê·Éú·Éì·Éê.webp' },
  { name: '·Éõ·Éî·Éö·Éò·Éê', image: '·Éõ·Éî·Éö·Éò·Éê.webp' },
  { name: '·Éì·Éî·Éö·É§·Éò·Éú·Éò', image: '·Éì·Éî·Éö·É§·Éò·Éú·Éò.webp' },
  { name: '·Éô·Éù·Éê·Éö·Éê', image: '·Éô·Éù·Éê·Éö·Éê.webp' },
  { name: '·Éû·Éò·Éú·Éí·Éï·Éò·Éú·Éò', image: '·Éû·Éú·Éí·Éï·Éò·Éú·Éò.webp' },
  { name: '·Éô·Éò·Éë·Éù·É†·É©·ÉÆ·Éê·Éö·Éê', image: '·Éô·Éò·Éë·Éù·É†·É©·ÉÆ·Éê·Éö·Éê.webp' },
  { name: '·Éù·Éë·Éù·Éë·Éê', image: '·Éù·Éë·Éù·Éë·Éê.webp' },
  { name: '·Éí·Éï·Éî·Éö·Éò', image: '·Éí·Éï·Éî·Éö·Éò.webp' },
  { name: '·ÉÆ·Éï·Éö·Éò·Éô·Éò', image: '·ÉÆ·Éï·Éö·Éò·Éô·Éò.webp' },
  { name: '·Éñ·Éï·Éò·Éí·Éî·Éú·Éò', image: '·Éñ·Éï·Éò·Éí·Éî·Éú·Éò.webp' },
  { name: '·É§·Éê·É†·É®·Éî·Éï·Éê·Éú·Éí·Éò', image: '·É§·Éê·É†·É®·Éî·Éï·Éê·Éú·Éí·Éò.webp' },
  { name: '·Éô·Éù·Éì·Éê·Éö·Éê', image: '·Éô·Éù·Éì·Éê·Éö·Éê.webp' },
  { name: '·Éê·É•·Éö·Éî·Éõ·Éò', image: '·Éê·É•·Éö·Éî·Éõ·Éò.webp' },
  { name: '·Éû·Éî·Éû·Éî·Éö·Éê', image: '·Éû·Éî·Éû·Éî·Éö·Éê.webp' },
  { name: '·Éê·É†·É¨·Éò·Éï·Éò', image: '·Éê·É†·É¨·Éò·Éï·Éò.webp' }
];

const ScrambleGame = () => {
  const [currentAnimal, setCurrentAnimal] = useState(null);
  const [scrambledLetters, setScrambledLetters] = useState([]);
  const [isCorrect, setIsCorrect] = useState(false);

  const scrambleWord = (word) => {
    return word
      .split('')
      .sort(() => Math.random() - 0.5)
      .map((letter, index) => ({
        id: `letter-${index}`,
        content: letter
      }));
  };

  const selectRandomAnimal = () => {
    const randomAnimal = animals[Math.floor(Math.random() * animals.length)];
    setCurrentAnimal(randomAnimal);
    setScrambledLetters(scrambleWord(randomAnimal.name));
    setIsCorrect(false);
  };

  useEffect(() => {
    selectRandomAnimal();
  }, []);

  const handleDragEnd = (result) => {
    if (!result.destination) return;

    const items = Array.from(scrambledLetters);
    const [reorderedItem] = items.splice(result.source.index, 1);
    items.splice(result.destination.index, 0, reorderedItem);

    setScrambledLetters(items);

    // Check if word is correct
    const currentWord = items.map(item => item.content).join('');
    if (currentWord === currentAnimal.name) {
      setIsCorrect(true);
      setTimeout(() => {
        selectRandomAnimal();
      }, 1500);
    }
  };

  if (!currentAnimal) return null;

  return (
    <div className="scramble-game">
      <img 
        src={`${process.env.PUBLIC_URL}/photos/${currentAnimal.image}`} 
        alt={currentAnimal.name}
        className="animal-image"
      />
      
      <DragDropContext onDragEnd={handleDragEnd}>
        <Droppable droppableId="letters" direction="horizontal">
          {(provided) => (
            <div
              {...provided.droppableProps}
              ref={provided.innerRef}
              className="letters-container"
            >
              {scrambledLetters.map((letter, index) => (
                <Draggable key={letter.id} draggableId={letter.id} index={index}>
                  {(provided) => (
                    <div
                      ref={provided.innerRef}
                      {...provided.draggableProps}
                      {...provided.dragHandleProps}
                      className="letter"
                    >
                      {letter.content}
                    </div>
                  )}
                </Draggable>
              ))}
              {provided.placeholder}
            </div>
          )}
        </Droppable>
      </DragDropContext>

      {isCorrect && <div className="success-message">·É°·É¨·Éù·É†·Éò·Éê! üéâ</div>}
      
      <button onClick={selectRandomAnimal} className="scramble-button">
        ·Éê·É°·Éù·Éî·Éë·Éò·É° ·Éê·É†·Éî·Éï·Éê
      </button>
    </div>
  );
};

export default ScrambleGame;
