import React, { useState } from 'react';
import styled from 'styled-components';
import { usePlayer } from '../context/PlayerContext';

const images = [
  { id: 'rainbow', name: '·É™·Éò·É°·Éê·É†·É¢·Éß·Éî·Éö·Éê' },
  // ·Éì·Éê·Éê·Éõ·Éê·É¢·Éî·Éó ·Éì·Éê·Éú·Éê·É†·É©·Éî·Éú·Éò ·É°·É£·É†·Éê·Éó·Éî·Éë·Éò ·Éê·É•
];

const DrawingGame = () => {
  const { updateGameProgress } = usePlayer();
  const [selectedImage, setSelectedImage] = useState('rainbow');
  const [selectedColor, setSelectedColor] = useState(null);
  const [showFeedback, setShowFeedback] = useState(false);

  const colors = [
    { value: '#FF0000' }, // ·É¨·Éò·Éó·Éî·Éö·Éò
    { value: '#FFA500' }, // ·Éú·Éê·É†·Éò·Éú·ÉØ·Éò·É°·É§·Éî·É†·Éò
    { value: '#FFFF00' }, // ·Éß·Éï·Éò·Éó·Éî·Éö·Éò
    { value: '#008000' }, // ·Éõ·É¨·Éï·Éê·Éú·Éî
    { value: '#0000FF' }, // ·Éö·É£·É†·ÉØ·Éò
    { value: '#4B0082' }, // ·Éò·Éò·É°·É§·Éî·É†·Éò
    { value: '#EE82EE' }  // ·Éï·Éê·É†·Éì·Éò·É°·É§·Éî·É†·Éò
  ];

  const handleColorSelect = (color) => {
    setSelectedColor(color.value);
    const isCorrect = color.value === '#FF0000';
    
    if (isCorrect) {
      setShowFeedback(true);
      updateGameProgress('drawing', Date.now(), { completed: true });
      setTimeout(() => {
        setShowFeedback(false);
      }, 1500);
    }
  };

  return (
    <Container>
      <Title>·Éí·Éê·Éê·É§·Éî·É†·Éê·Éì·Éî</Title>
      
      <GameArea>
        <DrawingArea style={{ backgroundColor: selectedColor || 'transparent' }}>
          <OutlineImage 
            src={`/assets/images/outlines/${selectedImage}.png`} 
            alt="·Éí·Éê·É°·Éê·É§·Éî·É†·Éê·Éì·Éî·Éë·Éî·Éö·Éò ·É°·É£·É†·Éê·Éó·Éò" 
          />
        </DrawingArea>

        <ColorPalette>
          {colors.map((color, index) => (
            <ColorButton
              key={index}
              color={color.value}
              onClick={() => handleColorSelect(color)}
            />
          ))}
        </ColorPalette>

        <ImageSelector>
          {images.map(image => (
            <ImageButton
              key={image.id}
              onClick={() => setSelectedImage(image.id)}
              selected={selectedImage === image.id}
            >
              <img 
                src={`/assets/images/thumbnails/${image.id}.png`} 
                alt={image.name}
                width="40"
                height="40"
              />
            </ImageButton>
          ))}
        </ImageSelector>

        {showFeedback && (
          <Feedback>·Éß·Éù·É©·Éê·É¶! ·É°·É¨·Éù·É†·Éê·Éì ·Éí·Éê·Éê·É§·Éî·É†·Éê·Éì·Éî! üéâ</Feedback>
        )}
      </GameArea>
    </Container>
  );
};

const Container = styled.div`
  width: 100%;
  min-height: 100vh;
  padding: 2rem;
  background-color: #fff;
`;

const Title = styled.h1`
  text-align: center;
  color: #333;
  margin-bottom: 2rem;
`;

const GameArea = styled.div`
  max-width: 800px;
  margin: 0 auto;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 2rem;
`;

const DrawingArea = styled.div`
  width: 100%;
  max-width: 500px;
  aspect-ratio: 1;
  border: 2px solid #e0e0e0;
  border-radius: 8px;
  display: flex;
  justify-content: center;
  align-items: center;
  background: white;
`;

const ColorPalette = styled.div`
  display: flex;
  gap: 1rem;
  margin-top: 1rem;
`;

const ColorButton = styled.button`
  width: 60px;
  height: 60px;
  border: none;
  border-radius: 8px;
  background-color: ${props => props.color};
  cursor: pointer;
  transition: transform 0.2s;

  &:hover {
    transform: scale(1.05);
  }
`;

const Feedback = styled.div`
  font-size: 1.5rem;
  color: #4CAF50;
  text-align: center;
`;

const ImageSelector = styled.div`
  display: flex;
  gap: 1rem;
  flex-wrap: wrap;
  margin-top: 2rem;
  justify-content: center;
`;

const ImageButton = styled.button`
  padding: 0.5rem;
  border: 2px solid ${props => props.selected ? '#4CAF50' : '#ccc'};
  border-radius: 8px;
  background: white;
  cursor: pointer;
  transition: all 0.2s;

  img {
    width: 40px;
    height: 40px;
    object-fit: contain;
  }

  &:hover {
    transform: scale(1.1);
  }
`;

const OutlineImage = styled.img`
  width: 100%;
  height: 100%;
  object-fit: contain;
`;

export default DrawingGame;
