import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { tasks } from '../data/tasks';
import { Sticker } from 'react-stickers';
import { createGlobalStyle } from 'styled-components';

const TasksContainer = styled.div`
  max-width: 800px;
  margin: 0 auto;
  padding: 20px;
  min-height: 80vh;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const TaskCard = styled.div`
  background: white;
  border-radius: 15px;
  padding: 30px;
  margin-bottom: 20px;
  box-shadow: 0 8px 16px rgba(0, 0, 0, 0.1);
  width: 100%;
  max-width: 600px;
  transition: all 0.3s ease;
  opacity: ${props => props.isVisible ? 1 : 0};
  transform: ${props => props.isVisible ? 'translateY(0)' : 'translateY(20px)'};
`;

const TaskTitle = styled.h2`
  color: #2196F3;
  margin-bottom: 20px;
  text-align: center;
  font-size: 2rem;
`;

const TaskDescription = styled.p`
  color: #666;
  margin-bottom: 25px;
  text-align: center;
  font-size: 1.2rem;
`;

const MatchingGame = styled.div`
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: 20px;
  margin-top: 20px;
`;

const MatchingItem = styled.div`
  padding: 20px;
  border-radius: 15px;
  text-align: center;
  cursor: pointer;
  user-select: none;
  background-color: ${props => props.isSelected ? '#e3f2fd' : 'white'};
  border: 3px solid ${props => props.color || '#ddd'};
  font-size: 2rem;
  transition: all 0.2s ease;

  &:hover {
    transform: translateY(-3px);
    box-shadow: 0 5px 15px rgba(33, 150, 243, 0.2);
  }

  &:active {
    transform: translateY(0);
  }
`;

const MathGame = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 25px;
`;

const VisualizationArea = styled.div`
  display: flex;
  gap: 30px;
  justify-content: center;
  margin: 25px 0;
`;

const Box = styled.div`
  width: 120px;
  height: 120px;
  border: 3px solid #2196F3;
  border-radius: 15px;
  display: flex;
  flex-wrap: wrap;
  padding: 15px;
  gap: 8px;
  background: white;
  transition: transform 0.3s ease;

  &:hover {
    transform: scale(1.05);
  }
`;

const Item = styled.div`
  width: 25px;
  height: 25px;
  border-radius: 50%;
  background-color: ${props => props.color || '#FF4444'};
  animation: pop 0.3s ease-out;

  @keyframes pop {
    0% { transform: scale(0); }
    100% { transform: scale(1); }
  }
`;

const Input = styled.input`
  padding: 15px;
  font-size: 1.5rem;
  border: 3px solid #2196F3;
  border-radius: 10px;
  width: 120px;
  text-align: center;
  margin: 0 10px;

  &:focus {
    outline: none;
    box-shadow: 0 0 0 3px rgba(33, 150, 243, 0.3);
  }
`;

const Button = styled.button`
  background-color: #4CAF50;
  color: white;
  border: none;
  padding: 15px 30px;
  border-radius: 10px;
  cursor: pointer;
  font-size: 1.2rem;
  transition: all 0.3s ease;
  margin: 10px;

  &:hover {
    background-color: #388E3C;
    transform: translateY(-3px);
    box-shadow: 0 5px 15px rgba(76, 175, 80, 0.3);
  }

  &:active {
    transform: translateY(0);
  }
`;

const Message = styled.div`
  color: ${props => props.isCorrect ? '#4CAF50' : '#f44336'};
  font-size: 1.3rem;
  margin-top: 15px;
  text-align: center;
  animation: fadeIn 0.5s ease-out;

  @keyframes fadeIn {
    0% { opacity: 0; transform: translateY(10px); }
    100% { opacity: 1; transform: translateY(0); }
  }
`;

const NavigationButtons = styled.div`
  display: flex;
  gap: 20px;
  margin-top: 30px;
`;

const StickerContainer = styled.div`
  position: fixed;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  z-index: 1000;
  pointer-events: none;
`;

const celebrationStickers = [
  '🌟', '🎉', '🎈', '🎊', '🏆', '⭐', '💫', '✨'
];

const GlobalStyle = createGlobalStyle`
  @keyframes celebrate {
    0% {
      transform: translate(-50%, -50%) scale(0.1);
      opacity: 0;
    }
    50% {
      transform: translate(-50%, -50%) scale(1.2);
      opacity: 1;
    }
    100% {
      transform: translate(-50%, -50%) scale(1);
      opacity: 0;
    }
  }
`;

const Tasks = () => {
  const [currentTaskIndex, setCurrentTaskIndex] = useState(0);
  const [selectedNumbers, setSelectedNumbers] = useState([]);
  const [selectedColors, setSelectedColors] = useState([]);
  const [answers, setAnswers] = useState({});
  const [feedback, setFeedback] = useState({});
  const [isVisible, setIsVisible] = useState(true);

  useEffect(() => {
    const shuffledTasks = [...tasks].sort(() => Math.random() - 0.5);
    tasks.splice(0, tasks.length, ...shuffledTasks);
  }, []);

  const handleNumberClick = (id) => {
    if (selectedNumbers.includes(id)) {
      setSelectedNumbers(selectedNumbers.filter(num => num !== id));
    } else if (selectedNumbers.length < 1) {
      setSelectedNumbers([...selectedNumbers, id]);
    }
  };

  const handleColorClick = (id) => {
    if (selectedColors.includes(id)) {
      setSelectedColors(selectedColors.filter(num => num !== id));
    } else if (selectedColors.length < 1) {
      setSelectedColors([...selectedColors, id]);
    }

    if (selectedNumbers.length === 1 && !selectedColors.includes(id)) {
      const isCorrect = selectedNumbers[0] === id;
      setFeedback(prev => ({
        ...prev,
        [selectedNumbers[0]]: isCorrect
      }));

      if (isCorrect) {
        const task = tasks[currentTaskIndex];
        const randomPraise = task.praise[Math.floor(Math.random() * task.praise.length)];
        setTimeout(() => {
          alert(randomPraise);
        }, 500);
      }

      setSelectedNumbers([]);
      setSelectedColors([]);
    }
  };

  const handleAnswerSubmit = (taskId) => {
    const task = tasks.find(t => t.id === taskId);
    const isCorrect = parseInt(answers[taskId]) === task.correctAnswer;
    setFeedback(prev => ({
      ...prev,
      [taskId]: isCorrect
    }));

    if (isCorrect) {
      const randomSticker = celebrationStickers[Math.floor(Math.random() * celebrationStickers.length)];
      const sticker = document.createElement('div');
      sticker.style.position = 'fixed';
      sticker.style.fontSize = '100px';
      sticker.style.zIndex = '1000';
      sticker.style.top = '50%';
      sticker.style.left = '50%';
      sticker.style.transform = 'translate(-50%, -50%)';
      sticker.style.animation = 'celebrate 1s ease-out forwards';
      sticker.textContent = randomSticker;

      document.body.appendChild(sticker);

      setTimeout(() => {
        document.body.removeChild(sticker);
      }, 1000);

      const randomPraise = task.praise[Math.floor(Math.random() * task.praise.length)];
      setTimeout(() => {
        alert(randomPraise);
      }, 1000);
    }
  };

  const handleNextTask = () => {
    setIsVisible(false);
    setTimeout(() => {
      setCurrentTaskIndex((prev) => (prev + 1) % tasks.length);
      setSelectedNumbers([]);
      setSelectedColors([]);
      setAnswers({});
      setFeedback({});
      setIsVisible(true);
    }, 300);
  };

  const handlePreviousTask = () => {
    setIsVisible(false);
    setTimeout(() => {
      setCurrentTaskIndex((prev) => (prev - 1 + tasks.length) % tasks.length);
      setSelectedNumbers([]);
      setSelectedColors([]);
      setAnswers({});
      setFeedback({});
      setIsVisible(true);
    }, 300);
  };

  const currentTask = tasks[currentTaskIndex];

  const renderMatchingGame = (task) => (
    <MatchingGame>
      <div>
        {task.items.map(item => (
          <MatchingItem
            key={item.id}
            isSelected={selectedNumbers.includes(item.id)}
            onClick={() => handleNumberClick(item.id)}
            color={feedback[item.id] ? '#4CAF50' : '#ddd'}
          >
            {item.text}
          </MatchingItem>
        ))}
      </div>
      <div>
        {task.items.map(item => (
          <MatchingItem
            key={item.id}
            isSelected={selectedColors.includes(item.id)}
            onClick={() => handleColorClick(item.id)}
            color={item.color}
          >
            {item.colorName}
          </MatchingItem>
        ))}
      </div>
    </MatchingGame>
  );

  const renderMathGame = (task) => (
    <MathGame>
      <TaskDescription>{task.question}</TaskDescription>
      <VisualizationArea>
        {task.visualization.box1 && (
          <Box>
            {[...Array(task.visualization.box1)].map((_, i) => (
              <Item 
                key={i} 
                color={
                  task.id === 'apples' ? '#ff6b6b' : 
                  task.id === 'butterflies' ? '#FF69B4' :
                  task.id === 'icecream' ? '#FFB6C1' : '#4CAF50'
                } 
              />
            ))}
          </Box>
        )}
        {task.visualization.box2 && (
          <Box>
            {[...Array(task.visualization.box2)].map((_, i) => (
              <Item 
                key={i} 
                color={
                  task.id === 'apples' ? '#ff6b6b' : 
                  task.id === 'butterflies' ? '#FF69B4' :
                  task.id === 'icecream' ? '#FFB6C1' : '#4CAF50'
                } 
              />
            ))}
          </Box>
        )}
      </VisualizationArea>
      <div>
        <Input
          type="number"
          value={answers[task.id] || ''}
          onChange={(e) => setAnswers(prev => ({
            ...prev,
            [task.id]: e.target.value
          }))}
          placeholder="პასუხი"
        />
        <Button onClick={() => handleAnswerSubmit(task.id)}>შემოწმება</Button>
      </div>
      {feedback[task.id] !== undefined && (
        <Message isCorrect={feedback[task.id]}>
          {feedback[task.id] ? '🎉 სწორია!' : '❌ სცადე თავიდან'}
          {!feedback[task.id] && <div style={{fontSize: '1rem', marginTop: '10px'}}>{task.hint}</div>}
        </Message>
      )}
    </MathGame>
  );

  return (
    <TasksContainer>
      <GlobalStyle />
      <TaskCard isVisible={isVisible}>
        <TaskTitle>{currentTask.title}</TaskTitle>
        <TaskDescription>{currentTask.description}</TaskDescription>
        {currentTask.type === 'matching' ? renderMatchingGame(currentTask) : renderMathGame(currentTask)}
      </TaskCard>
      <NavigationButtons>
        <Button onClick={handlePreviousTask}>← წინა</Button>
        <Button onClick={handleNextTask}>შემდეგი →</Button>
      </NavigationButtons>
    </TasksContainer>
  );
};

export default Tasks;
