import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { tasks } from '../data/tasks';
import { createGlobalStyle } from 'styled-components';

const TasksContainer = styled.div`
  max-width: 800px;
  margin: 0 auto;
  padding: 20px;
  min-height: 80vh;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const TaskCard = styled.div`
  background: white;
  border-radius: 15px;
  padding: 30px;
  margin-bottom: 20px;
  box-shadow: 0 8px 16px rgba(0, 0, 0, 0.1);
  width: 100%;
  max-width: 600px;
  transition: all 0.3s ease;
  opacity: ${props => props.isVisible ? 1 : 0};
  transform: ${props => props.isVisible ? 'translateY(0)' : 'translateY(20px)'};
`;

const TaskTitle = styled.h2`
  color: #2196F3;
  margin-bottom: 20px;
  text-align: center;
  font-size: 2rem;
`;

const TaskDescription = styled.p`
  color: #666;
  margin-bottom: 25px;
  text-align: center;
  font-size: 1.2rem;
`;

const MatchingGame = styled.div`
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: 20px;
  margin-top: 20px;
`;

const MatchingItem = styled.div`
  padding: 20px;
  margin: 10px;
  border-radius: 15px;
  cursor: ${props => props.disabled ? 'default' : 'pointer'};
  background-color: ${props => {
    if (props.isCorrect && props.matchColor) {
      return props.matchColor;
    }
    if (props.isSelected) {
      return '#FFD700';
    }
    return props.color || '#fff';
  }};
  border: 3px solid ${props => props.isSelected ? '#FFD700' : '#ddd'};
  transition: all 0.3s ease;
  font-size: 1.5rem;
  min-width: 120px;
  text-align: center;
  opacity: ${props => props.disabled ? 0.7 : 1};

  &:hover {
    transform: ${props => props.disabled ? 'none' : 'scale(1.05)'};
    border-color: ${props => props.disabled ? '#ddd' : '#FFD700'};
  }
`;

const MathGame = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 25px;
`;

const StickersContainer = styled.div`
  display: flex;
  gap: 30px;
  justify-content: center;
  margin: 25px 0;
`;

const StickersGroup = styled.div`
  display: flex;
  gap: 10px;
  padding: 20px;
  background: #f8f9fa;
  border-radius: 15px;
  box-shadow: 0 2px 8px rgba(0,0,0,0.1);
  min-width: 120px;
  justify-content: center;
  transition: transform 0.3s ease;

  &:hover {
    transform: scale(1.05);
  }
`;

const Sticker = styled.div`
  font-size: 2rem;
  animation: popIn 0.5s ease-out;

  @keyframes popIn {
    0% { transform: scale(0); }
    50% { transform: scale(1.2); }
    100% { transform: scale(1); }
  }
`;

const Input = styled.input`
  padding: 15px;
  font-size: 1.5rem;
  border: 3px solid #2196F3;
  border-radius: 10px;
  width: 120px;
  text-align: center;
  margin: 0 10px;

  &:focus {
    outline: none;
    box-shadow: 0 0 0 3px rgba(33, 150, 243, 0.3);
  }
`;

const Button = styled.button`
  background-color: #4CAF50;
  color: white;
  border: none;
  padding: 15px 30px;
  border-radius: 10px;
  cursor: pointer;
  font-size: 1.2rem;
  transition: all 0.3s ease;
  margin: 10px;

  &:hover {
    background-color: #388E3C;
    transform: translateY(-3px);
    box-shadow: 0 5px 15px rgba(76, 175, 80, 0.3);
  }

  &:active {
    transform: translateY(0);
  }
`;

const Message = styled.div`
  color: ${props => props.isCorrect ? '#4CAF50' : '#f44336'};
  font-size: 1.3rem;
  margin-top: 15px;
  text-align: center;
  animation: fadeIn 0.5s ease-out;

  @keyframes fadeIn {
    0% { opacity: 0; transform: translateY(10px); }
    100% { opacity: 1; transform: translateY(0); }
  }
`;

const NavigationButtons = styled.div`
  display: flex;
  gap: 20px;
  margin-top: 30px;
`;

const GlobalStyle = createGlobalStyle`
  @keyframes celebrate {
    0% {
      transform: translate(-50%, -50%) scale(0.1);
      opacity: 0;
    }
    50% {
      transform: translate(-50%, -50%) scale(1.2);
      opacity: 1;
    }
    100% {
      transform: translate(-50%, -50%) scale(1);
      opacity: 0;
    }
  }
`;

const celebrationStickers = ['🌟', '🎉', '🎈', '🎊', '🏆', '⭐', '💫', '✨'];

const Tasks = () => {
  const [currentTaskIndex, setCurrentTaskIndex] = useState(0);
  const [selectedNumbers, setSelectedNumbers] = useState([]);
  const [selectedColors, setSelectedColors] = useState([]);
  const [answers, setAnswers] = useState({});
  const [feedback, setFeedback] = useState({});
  const [isVisible, setIsVisible] = useState(true);
  const [shuffledItems, setShuffledItems] = useState([]);
  const [shuffledColors, setShuffledColors] = useState([]);

  const shuffleArray = (array) => {
    const newArray = [...array];
    for (let i = newArray.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [newArray[i], newArray[j]] = [newArray[j], newArray[i]];
    }
    return newArray;
  };

  useEffect(() => {
    const currentTask = tasks[currentTaskIndex];
    if (currentTask.type === 'matching') {
      setShuffledItems(shuffleArray(currentTask.items));
      setShuffledColors(shuffleArray([...currentTask.items]));
    }
  }, [currentTaskIndex]);

  const handleNumberClick = (id) => {
    if (selectedNumbers.includes(id)) {
      setSelectedNumbers(selectedNumbers.filter(num => num !== id));
    } else if (selectedNumbers.length < 1) {
      setSelectedNumbers([...selectedNumbers, id]);
    }
  };

  const handleColorClick = (id) => {
    if (selectedColors.includes(id)) {
      setSelectedColors(selectedColors.filter(num => num !== id));
    } else if (selectedColors.length < 1) {
      setSelectedColors([...selectedColors, id]);
    }

    if (selectedNumbers.length === 1 && !selectedColors.includes(id)) {
      const isCorrect = selectedNumbers[0] === id;
      setFeedback(prev => ({
        ...prev,
        [selectedNumbers[0]]: isCorrect
      }));

      if (isCorrect) {
        const task = tasks[currentTaskIndex];
        const randomPraise = task.praise[Math.floor(Math.random() * task.praise.length)];
        setTimeout(() => {
          alert(randomPraise);
        }, 500);
      }

      setSelectedNumbers([]);
      setSelectedColors([]);
    }
  };

  const handleAnswerSubmit = (taskId) => {
    const task = tasks.find(t => t.id === taskId);
    const isCorrect = parseInt(answers[taskId]) === task.correctAnswer;
    setFeedback(prev => ({
      ...prev,
      [taskId]: isCorrect
    }));

    if (isCorrect) {
      const randomSticker = celebrationStickers[Math.floor(Math.random() * celebrationStickers.length)];
      const sticker = document.createElement('div');
      sticker.style.position = 'fixed';
      sticker.style.fontSize = '100px';
      sticker.style.zIndex = '1000';
      sticker.style.top = '50%';
      sticker.style.left = '50%';
      sticker.style.transform = 'translate(-50%, -50%)';
      sticker.style.animation = 'celebrate 1s ease-out forwards';
      sticker.textContent = randomSticker;

      document.body.appendChild(sticker);

      setTimeout(() => {
        document.body.removeChild(sticker);
      }, 1000);

      const randomPraise = task.praise[Math.floor(Math.random() * task.praise.length)];
      setTimeout(() => {
        alert(randomPraise);
      }, 1000);
    }
  };

  const handleNextTask = () => {
    setIsVisible(false);
    setTimeout(() => {
      setCurrentTaskIndex((prev) => (prev + 1) % tasks.length);
      setSelectedNumbers([]);
      setSelectedColors([]);
      setAnswers({});
      setFeedback({});
      setIsVisible(true);
    }, 300);
  };

  const handlePreviousTask = () => {
    setIsVisible(false);
    setTimeout(() => {
      setCurrentTaskIndex((prev) => (prev - 1 + tasks.length) % tasks.length);
      setSelectedNumbers([]);
      setSelectedColors([]);
      setAnswers({});
      setFeedback({});
      setIsVisible(true);
    }, 300);
  };

  const currentTask = tasks[currentTaskIndex];

  const renderMatchingGame = (task) => (
    <MatchingGame>
      <div>
        {shuffledItems.map(item => {
          const isCorrect = feedback[item.id] === true;
          const matchingColor = isCorrect ? item.color : undefined;

          return (
            <MatchingItem
              key={item.id}
              isSelected={selectedNumbers.includes(item.id)}
              onClick={() => !feedback[item.id] && handleNumberClick(item.id)}
              disabled={feedback[item.id]}
              isCorrect={isCorrect}
              matchColor={matchingColor}
            >
              {item.text}
            </MatchingItem>
          );
        })}
      </div>
      <div>
        {shuffledColors.map(item => {
          const isCorrect = feedback[item.id] === true;
          const matchingColor = isCorrect ? item.color : undefined;

          return (
            <MatchingItem
              key={item.id}
              isSelected={selectedColors.includes(item.id)}
              onClick={() => !feedback[item.id] && handleColorClick(item.id)}
              disabled={feedback[item.id]}
              color={item.color}
              isCorrect={isCorrect}
              matchColor={matchingColor}
            >
              {item.colorName}
            </MatchingItem>
          );
        })}
      </div>
    </MatchingGame>
  );

  const renderMathGame = (task) => (
    <MathGame>
      <TaskDescription>{task.question}</TaskDescription>
      <StickersContainer>
        <StickersGroup>
          {task.stickers.map((sticker, index) => (
            <Sticker key={index}>{sticker}</Sticker>
          ))}
        </StickersGroup>
        <StickersGroup>
          {task.stickers2.map((sticker, index) => (
            <Sticker key={index}>{sticker}</Sticker>
          ))}
        </StickersGroup>
      </StickersContainer>
      <div>
        <Input
          type="number"
          value={answers[task.id] || ''}
          onChange={(e) => setAnswers(prev => ({
            ...prev,
            [task.id]: e.target.value
          }))}
          placeholder="პასუხი"
        />
        <Button onClick={() => handleAnswerSubmit(task.id)}>შემოწმება</Button>
      </div>
      {feedback[task.id] !== undefined && (
        <Message isCorrect={feedback[task.id]}>
          {feedback[task.id] ? '🎉 სწორია!' : '❌ სცადე თავიდან'}
          {!feedback[task.id] && <div style={{fontSize: '1rem', marginTop: '10px'}}>{task.hint}</div>}
        </Message>
      )}
    </MathGame>
  );

  return (
    <TasksContainer>
      <GlobalStyle />
      <TaskCard isVisible={isVisible}>
        <TaskTitle>{currentTask.title}</TaskTitle>
        <TaskDescription>{currentTask.description}</TaskDescription>
        {currentTask.type === 'matching' ? renderMatchingGame(currentTask) : renderMathGame(currentTask)}
      </TaskCard>
      <NavigationButtons>
        <Button onClick={handlePreviousTask}>← წინა</Button>
        <Button onClick={handleNextTask}>შემდეგი →</Button>
      </NavigationButtons>
    </TasksContainer>
  );
};

export default Tasks;
