import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { usePlayer } from '../context/PlayerContext';

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  min-height: 100vh;
  padding: 20px;
  background: #f5f5f5;
`;

const GameArea = styled.div`
  background: white;
  padding: 2rem;
  border-radius: 15px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 2rem;
`;

const Title = styled.h1`
  color: #333;
  margin-bottom: 1rem;
`;

const ColorDisplay = styled.div`
  width: 200px;
  height: 200px;
  border-radius: 10px;
  margin: 1rem 0;
  background-color: ${props => props.color};
`;

const ColorSlider = styled.input`
  width: 200px;
  margin: 0.5rem 0;
`;

const Button = styled.button`
  padding: 1rem 2rem;
  font-size: 1.1rem;
  border: none;
  border-radius: 8px;
  background: #1565C0;
  color: white;
  cursor: pointer;
  transition: all 0.3s ease;

  &:hover {
    background: #1976D2;
  }
`;

const Score = styled.div`
  font-size: 1.2rem;
  margin-top: 1rem;
`;

const ColorGame = () => {
  const [targetColor, setTargetColor] = useState({ r: 0, g: 0, b: 0 });
  const [userColor, setUserColor] = useState({ r: 0, g: 0, b: 0 });
  const [score, setScore] = useState(0);
  const [gameStarted, setGameStarted] = useState(false);
  const { updateGameProgress } = usePlayer();

  const generateRandomColor = () => {
    return {
      r: Math.floor(Math.random() * 256),
      g: Math.floor(Math.random() * 256),
      b: Math.floor(Math.random() * 256)
    };
  };

  const startNewRound = () => {
    setTargetColor(generateRandomColor());
    setUserColor({ r: 0, g: 0, b: 0 });
    setGameStarted(true);
  };

  const calculateScore = () => {
    const diff = Math.sqrt(
      Math.pow(targetColor.r - userColor.r, 2) +
      Math.pow(targetColor.g - userColor.g, 2) +
      Math.pow(targetColor.b - userColor.b, 2)
    );
    
    // Convert difference to a 0-100 score
    const newScore = Math.max(0, Math.round(100 - (diff / 4.42)));
    setScore(newScore);
    updateGameProgress('color', 'match_color', newScore);
    setGameStarted(false);
  };

  const rgbToString = (color) => `rgb(${color.r}, ${color.g}, ${color.b})`;

  return (
    <Container>
      <GameArea>
        <Title>ფერების შეხამება</Title>
        
        {gameStarted ? (
          <>
            <div style={{ display: 'flex', gap: '2rem' }}>
              <div>
                <h3>სამიზნე ფერი</h3>
                <ColorDisplay color={rgbToString(targetColor)} />
              </div>
              <div>
                <h3>თქვენი ფერი</h3>
                <ColorDisplay color={rgbToString(userColor)} />
                <div>
                  <label>წითელი:</label>
                  <ColorSlider
                    type="range"
                    min="0"
                    max="255"
                    value={userColor.r}
                    onChange={(e) => setUserColor({ ...userColor, r: parseInt(e.target.value) })}
                  />
                  <label>მწვანე:</label>
                  <ColorSlider
                    type="range"
                    min="0"
                    max="255"
                    value={userColor.g}
                    onChange={(e) => setUserColor({ ...userColor, g: parseInt(e.target.value) })}
                  />
                  <label>ლურჯი:</label>
                  <ColorSlider
                    type="range"
                    min="0"
                    max="255"
                    value={userColor.b}
                    onChange={(e) => setUserColor({ ...userColor, b: parseInt(e.target.value) })}
                  />
                </div>
              </div>
            </div>
            <Button onClick={calculateScore}>შემოწმება</Button>
          </>
        ) : (
          <>
            {score > 0 && <Score>თქვენი ქულა: {score}</Score>}
            <Button onClick={startNewRound}>
              {score > 0 ? 'ხელახლა თამაში' : 'თამაშის დაწყება'}
            </Button>
          </>
        )}
      </GameArea>
    </Container>
  );
};

export default ColorGame;
