import React, { useState } from 'react';
import styled from 'styled-components';
import { usePlayer } from '../context/PlayerContext';

const shapes = {
  heart: {
    path: "M50 80 C 25 60, 0 40, 0 20 C 0 0, 20 0, 25 10 C 30 0, 50 0, 50 20 C 50 0, 70 0, 75 10 C 80 0, 100 0, 100 20 C 100 40, 75 60, 50 80",
    viewBox: "0 0 100 100"
  },
  flower: {
    path: "M50 50 L50 20 M50 50 L80 50 M50 50 L50 80 M50 50 L20 50 M60 60 A 10 10 0 1 0 40 40 A 10 10 0 1 0 60 60",
    viewBox: "0 0 100 100"
  },
  tree: {
    path: "M50 90 L50 40 M30 90 L70 90 M20 40 C35 20 65 20 80 40 C65 30 35 30 20 40",
    viewBox: "0 0 100 100"
  },
  house: {
    path: "M10 50 L50 10 L90 50 L90 90 L10 90 L10 50 M40 90 L40 70 L60 70 L60 90",
    viewBox: "0 0 100 100"
  }
};

const coloringImages = [
  { id: 'rainbow', src: '/assets/images/rainbow-outline.png', correctColor: '#FF0000' },
  { id: 'butterfly', src: '/assets/images/butterfly-outline.png', correctColor: '#FFA500' },
  { id: 'flower', src: '/assets/images/flower-outline.png', correctColor: '#FF69B4' },
  // ... დანარჩენი სურათები
];

const DrawingGame = () => {
  const { updateGameProgress } = usePlayer();
  const [currentImage, setCurrentImage] = useState(coloringImages[0]);
  const [selectedColor, setSelectedColor] = useState(null);
  const [showFeedback, setShowFeedback] = useState(false);

  const colors = [
    { value: '#FFA500' }, // ნარინჯისფერი
    { value: '#90EE90' }, // მწვანე
    { value: '#FF0000' }  // წითელი
  ];

  const handleColorSelect = (color) => {
    setSelectedColor(color.value);
    const isCorrect = color.value === currentImage.correctColor;
    
    if (isCorrect) {
      setShowFeedback(true);
      updateGameProgress('coloring', currentImage.id, { 
        completed: true,
        selectedColor: color.value 
      });
      setTimeout(() => {
        setShowFeedback(false);
      }, 1500);
    }
  };

  return (
    <Container>
      <Title>ხატვა</Title>
      
      <GameArea>
        {/* სანიმუშო ფიგურა */}
        <ExampleShape>
          <svg width="150" height="150" viewBox={shapes[selectedShape].viewBox}>
            <path
              d={shapes[selectedShape].path}
              fill="#FF0000"
              stroke="none"
            />
          </svg>
        </ExampleShape>

        {/* გასაფერადებელი ფიგურა */}
        <DrawingArea>
          <svg width="300" height="300" viewBox={shapes[selectedShape].viewBox}>
            <path
              d={shapes[selectedShape].path}
              fill={selectedColor || 'transparent'}
              stroke="#000000"
              strokeWidth="2"
            />
          </svg>
        </DrawingArea>

        {/* ფერების პალიტრა */}
        <ColorPalette>
          {colors.map((color, index) => (
            <ColorButton
              key={index}
              color={color.value}
              onClick={() => handleColorSelect(color)}
            />
          ))}
        </ColorPalette>

        {/* ფიგურების არჩევა */}
        <ShapeSelector>
          {Object.keys(shapes).map(shape => (
            <ShapeButton
              key={shape}
              onClick={() => setSelectedShape(shape)}
              selected={selectedShape === shape}
            >
              <svg width="40" height="40" viewBox={shapes[shape].viewBox}>
                <path
                  d={shapes[shape].path}
                  fill="none"
                  stroke="#000"
                  strokeWidth="2"
                />
              </svg>
            </ShapeButton>
          ))}
        </ShapeSelector>

        {showFeedback && (
          <Feedback>ყოჩაღ! სწორად გააფერადე! 🎉</Feedback>
        )}
      </GameArea>
    </Container>
  );
};

const Container = styled.div`
  width: 100%;
  min-height: 100vh;
  padding: 2rem;
  background-color: #fff;
`;

const Title = styled.h1`
  text-align: center;
  color: #333;
  margin-bottom: 2rem;
`;

const GameArea = styled.div`
  max-width: 800px;
  margin: 0 auto;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 2rem;
`;

const ExampleShape = styled.div`
  position: absolute;
  top: 2rem;
  right: 2rem;
`;

const DrawingArea = styled.div`
  width: 100%;
  max-width: 500px;
  aspect-ratio: 1;
  border: 2px solid #e0e0e0;
  border-radius: 8px;
  display: flex;
  justify-content: center;
  align-items: center;
  background: white;
`;

const ColorPalette = styled.div`
  display: flex;
  gap: 1rem;
  margin-top: 1rem;
`;

const ColorButton = styled.button`
  width: 60px;
  height: 60px;
  border: none;
  border-radius: 8px;
  background-color: ${props => props.color};
  cursor: pointer;
  transition: transform 0.2s;

  &:hover {
    transform: scale(1.05);
  }
`;

const Feedback = styled.div`
  font-size: 1.5rem;
  color: #4CAF50;
  text-align: center;
`;

const ShapeSelector = styled.div`
  display: flex;
  gap: 1rem;
  margin-top: 2rem;
`;

const ShapeButton = styled.button`
  padding: 0.5rem;
  border: 2px solid ${props => props.selected ? '#4CAF50' : '#ccc'};
  border-radius: 8px;
  background: white;
  cursor: pointer;
  transition: all 0.2s;

  &:hover {
    transform: scale(1.1);
  }
`;

export default DrawingGame;
