import React, { useState, useEffect } from 'react';
import styled from 'styled-components';

const GameContainer = styled.div`
  display: flex;
  gap: 40px;
  align-items: flex-start;
  padding: 20px;
`;

const OriginalImage = styled.div`
  width: 200px;
  height: 200px;
  background-image: url(${props => props.image});
  background-size: cover;
  border-radius: 10px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.2);
  margin-bottom: 20px;
`;

const HintButton = styled.button`
  background-color: #2196F3;
  color: white;
  padding: 10px 20px;
  border: none;
  border-radius: 8px;
  cursor: pointer;
  margin-top: 10px;
  transition: all 0.3s ease;

  &:hover {
    background-color: #1976D2;
    transform: scale(1.05);
  }

  &:active {
    transform: scale(0.95);
  }
`;

const BoardsContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: 20px;
`;

const Board = styled.div`
  display: grid;
  grid-template-columns: repeat(${props => props.size}, 1fr);
  gap: 2px;
  background-color: ${props => props.isTarget ? '#f5f5f5' : '#fff'};
  padding: 10px;
  border-radius: 10px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.2);
  width: 400px;
  height: 400px;
  box-sizing: border-box;
`;

const PuzzlePiece = styled.div`
  width: 100%;
  height: 100%;
  background-image: ${props => props.image ? `url(${props.image})` : 'none'};
  background-size: ${props => props.size * 100}% ${props => props.size * 100}%;
  background-position: ${props => props.bgPosition};
  border: 2px solid ${props => props.isWrongAttempt ? '#ff4444' : '#ddd'};
  cursor: ${props => props.isPlaced ? 'default' : 'pointer'};
  transition: all 0.2s;
  display: flex;
  align-items: center;
  justify-content: center;
  background-color: ${props => {
    if (props.isWrongAttempt) return 'rgba(255, 68, 68, 0.1)';
    return props.isTarget ? '#f0f0f0' : 'transparent';
  }};
  background-repeat: no-repeat;
  position: relative;

  &:hover {
    transform: ${props => props.isPlaced ? 'none' : 'scale(1.05)'};
    z-index: 1;
  }
`;

const Message = styled.div`
  color: #666;
  font-size: 14px;
  text-align: center;
`;

const Title = styled.h2`
  color: #2196F3;
  margin: 0 0 10px 0;
  font-size: 1.2rem;
`;

const HintOverlay = styled.div`
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  background-color: rgba(33, 150, 243, 0.9);
  color: white;
  padding: 5px 10px;
  border-radius: 4px;
  font-size: 14px;
  pointer-events: none;
  z-index: 2;
`;

const DraggedPiece = styled.div`
  position: fixed;
  width: 100px;
  height: 100px;
  pointer-events: none;
  z-index: 1000;
  border: 2px solid #2196F3;
  background-repeat: no-repeat;
  transform: translate(-50%, -50%);
`;

const PuzzleBoard = ({ image, difficulty, onProgress, onComplete }) => {
  const [pieces, setPieces] = useState([]);
  const [selectedPiece, setSelectedPiece] = useState(null);
  const [targetBoard, setTargetBoard] = useState([]);
  const [correctPositions, setCorrectPositions] = useState(new Set());
  const [showHints, setShowHints] = useState(false);
  const [wrongAttempts, setWrongAttempts] = useState(new Set());
  const [draggedPiece, setDraggedPiece] = useState(null);
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });

  useEffect(() => {
    const totalPieces = difficulty * difficulty;
    const initialPieces = Array.from({ length: totalPieces }, (_, index) => ({
      id: String(index),
      bgPosition: `${(index % difficulty) * (100 / (difficulty - 1))}% ${Math.floor(index / difficulty) * (100 / (difficulty - 1))}%`,
      correctIndex: index
    })).sort(() => Math.random() - 0.5);

    const initialTargetBoard = Array(totalPieces).fill(null);

    setPieces(initialPieces);
    setTargetBoard(initialTargetBoard);
    setCorrectPositions(new Set());
    setSelectedPiece(null);
    setWrongAttempts(new Set());
    setDraggedPiece(null);
  }, [difficulty]);

  useEffect(() => {
    const handleMouseMove = (e) => {
      if (draggedPiece !== null) {
        setMousePosition({ x: e.clientX, y: e.clientY });
      }
    };

    window.addEventListener('mousemove', handleMouseMove);
    return () => window.removeEventListener('mousemove', handleMouseMove);
  }, [draggedPiece]);

  const handleSourcePieceClick = (index) => {
    if (draggedPiece === index) {
      setDraggedPiece(null);
    } else {
      setDraggedPiece(index);
      setWrongAttempts(new Set()); // ·Éí·Éê·Éï·Éê·É°·É£·É§·Éó·Éê·Éï·Éù·Éó ·É¨·Éò·Éó·Éî·Éö·Éò ·Éò·Éú·Éì·Éò·Éô·Éê·É¢·Éù·É†·Éî·Éë·Éò ·Éê·ÉÆ·Éê·Éö·Éò ·Éú·Éê·É¨·Éò·Éö·Éò·É° ·Éê·É†·É©·Éî·Éï·Éò·É°·Éê·É°
    }
    setSelectedPiece(index);
  };

  const handleTargetPieceClick = (targetIndex) => {
    if (draggedPiece === null || targetBoard[targetIndex] !== null) return;

    const selectedPieceData = pieces[draggedPiece];
    if (parseInt(selectedPieceData.id) === targetIndex) {
      const newTargetBoard = [...targetBoard];
      newTargetBoard[targetIndex] = selectedPieceData;
      setTargetBoard(newTargetBoard);

      const newPieces = [...pieces];
      newPieces[draggedPiece] = null;
      setPieces(newPieces);

      const newCorrectPositions = new Set(correctPositions);
      newCorrectPositions.add(targetIndex);
      setCorrectPositions(newCorrectPositions);
    } else {
      const newWrongAttempts = new Set(wrongAttempts);
      newWrongAttempts.add(targetIndex);
      setWrongAttempts(newWrongAttempts);
    }

    setDraggedPiece(null);
    setSelectedPiece(null);
  };

  const toggleHints = () => {
    setShowHints(!showHints);
  };

  useEffect(() => {
    const progress = (correctPositions.size / (difficulty * difficulty)) * 100;
    onProgress(Math.round(progress));

    if (correctPositions.size === difficulty * difficulty) {
      onComplete();
    }
  }, [correctPositions, difficulty, onComplete, onProgress]);

  return (
    <GameContainer>
      <div>
        <Title>·Éù·É†·Éò·Éí·Éò·Éú·Éê·Éö·Éò ·É°·É£·É†·Éê·Éó·Éò</Title>
        <OriginalImage image={image} />
        <HintButton onClick={toggleHints}>
          {showHints ? 'üîç ·Éì·Éê·Éõ·Éê·Éö·Éî ·Éõ·Éò·Éú·Éò·É®·Éú·Éî·Éë·Éî·Éë·Éò' : 'üí° ·Éõ·Éê·É©·Éï·Éî·Éú·Éî ·Éõ·Éò·Éú·Éò·É®·Éú·Éî·Éë·Éî·Éë·Éò'}
        </HintButton>
      </div>

      <BoardsContainer>
        <div>
          <Title>·Éû·Éê·Éñ·Éö·Éò·É° ·Éú·Éê·É¨·Éò·Éö·Éî·Éë·Éò</Title>
          <Board size={difficulty} isTarget={false}>
            {pieces.map((piece, index) => piece && (
              <PuzzlePiece
                key={piece.id}
                image={image}
                size={difficulty}
                bgPosition={piece.bgPosition}
                onClick={() => handleSourcePieceClick(index)}
              >
                {showHints && (
                  <HintOverlay>
                    ·Éê·Éì·Éí·Éò·Éö·Éò: {parseInt(piece.id) + 1}
                  </HintOverlay>
                )}
              </PuzzlePiece>
            ))}
          </Board>
        </div>

        <div>
          <Title>·É°·Éê·Éõ·Éò·Éñ·Éú·Éî ·É©·Éê·É†·É©·Éù</Title>
          <Board size={difficulty} isTarget={true}>
            {targetBoard.map((piece, index) => (
              <PuzzlePiece
                key={index}
                image={piece ? image : null}
                size={difficulty}
                bgPosition={piece ? piece.bgPosition : ''}
                isTarget={!piece}
                isPlaced={piece !== null}
                isWrongAttempt={wrongAttempts.has(index)}
                onClick={() => handleTargetPieceClick(index)}
              >
                {!piece && showHints && (
                  <Message>
                    {index + 1}
                  </Message>
                )}
              </PuzzlePiece>
            ))}
          </Board>
        </div>
      </BoardsContainer>

      {draggedPiece !== null && pieces[draggedPiece] && (
        <DraggedPiece
          style={{
            left: `${mousePosition.x - 50}px`,
            top: `${mousePosition.y - 50}px`,
            backgroundImage: `url(${image})`,
            backgroundSize: `${difficulty * 100}% ${difficulty * 100}%`,
            backgroundPosition: pieces[draggedPiece].bgPosition
          }}
        />
      )}
    </GameContainer>
  );
};

export default PuzzleBoard;
