import React, { createContext, useContext, useState, useEffect } from 'react';

const PlayerContext = createContext();

const initialGameProgress = {
  puzzles: [],
  completedScrambleTasks: [],
  taskScores: {},
  completedTasks: [],
  scrambleScores: {},
  lastPlayed: null
};

export const PlayerProvider = ({ children }) => {
  const [gameProgress, setGameProgress] = useState(() => {
    const savedProgress = localStorage.getItem('gameProgress');
    return savedProgress ? JSON.parse(savedProgress) : initialGameProgress;
  });

  useEffect(() => {
    try {
      localStorage.setItem('gameProgress', JSON.stringify(gameProgress));
    } catch (error) {
      console.error('Error saving progress:', error);
    }
  }, [gameProgress]);

  const updateGameProgress = (gameType, timestamp, data) => {
    setGameProgress(prevProgress => {
      const newProgress = { ...prevProgress };

      switch (gameType) {
        case 'puzzle':
          if (!newProgress.puzzles.includes(data.puzzleId)) {
            newProgress.puzzles = [...(newProgress.puzzles || []), data.puzzleId];
          }
          break;

        case 'scramble':
          if (!newProgress.completedScrambleTasks.includes(data.taskId)) {
            newProgress.completedScrambleTasks = [...(newProgress.completedScrambleTasks || []), data.taskId];
          }
          if (data.score !== undefined) {
            newProgress.scrambleScores = { ...(newProgress.scrambleScores || {}), [data.taskId]: data.score };
          }
          break;

        case 'task':
          if (!newProgress.completedTasks.includes(data.taskId)) {
            newProgress.completedTasks = [...(newProgress.completedTasks || []), data.taskId];
          }
          if (data.score !== undefined) {
            newProgress.taskScores = { ...(newProgress.taskScores || {}), [data.taskId]: data.score };
          }
          break;
      }

      newProgress.lastPlayed = timestamp || new Date().toISOString();
      return newProgress;
    });
  };

  const resetProgress = () => {
    setGameProgress(initialGameProgress);
    localStorage.setItem('gameProgress', JSON.stringify(initialGameProgress));
  };

  const value = {
    gameProgress,
    updateGameProgress,
    resetProgress
  };

  return (
    <PlayerContext.Provider value={value}>
      {children}
    </PlayerContext.Provider>
  );
};

export const usePlayer = () => {
  const context = useContext(PlayerContext);
  if (!context) {
    throw new Error('usePlayer must be used within a PlayerProvider');
  }
  return context;
};
