import React, { useState } from 'react';
import styled from 'styled-components';
import { tasks } from '../data/tasks';

const TasksContainer = styled.div`
  max-width: 800px;
  margin: 0 auto;
  padding: 20px;
`;

const TaskCard = styled.div`
  background: white;
  border-radius: 15px;
  padding: 20px;
  margin-bottom: 20px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  transition: transform 0.3s ease;

  &:hover {
    transform: translateY(-5px);
  }
`;

const TaskTitle = styled.h2`
  color: #2196F3;
  margin-bottom: 15px;
`;

const TaskDescription = styled.p`
  color: #666;
  margin-bottom: 20px;
`;

const MatchingGame = styled.div`
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: 20px;
  margin-top: 20px;
`;

const MatchingItem = styled.div`
  padding: 15px;
  border-radius: 10px;
  text-align: center;
  cursor: pointer;
  user-select: none;
  background-color: ${props => props.isSelected ? '#e3f2fd' : 'white'};
  border: 2px solid ${props => props.color || '#ddd'};
  font-size: 1.5rem;

  &:hover {
    background-color: #e3f2fd;
  }
`;

const MathGame = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 20px;
`;

const VisualizationArea = styled.div`
  display: flex;
  gap: 20px;
  justify-content: center;
  margin: 20px 0;
`;

const Box = styled.div`
  width: 100px;
  height: 100px;
  border: 2px solid #2196F3;
  border-radius: 10px;
  display: flex;
  flex-wrap: wrap;
  padding: 10px;
  gap: 5px;
  background: white;
`;

const Item = styled.div`
  width: 20px;
  height: 20px;
  border-radius: 50%;
  background-color: ${props => props.color || '#FF4444'};
`;

const Input = styled.input`
  padding: 10px;
  font-size: 1.2rem;
  border: 2px solid #2196F3;
  border-radius: 5px;
  width: 100px;
  text-align: center;
`;

const Button = styled.button`
  background-color: #4CAF50;
  color: white;
  border: none;
  padding: 10px 20px;
  border-radius: 5px;
  cursor: pointer;
  font-size: 1.1rem;
  transition: background-color 0.3s ease;

  &:hover {
    background-color: #388E3C;
  }
`;

const Message = styled.div`
  color: ${props => props.isCorrect ? '#4CAF50' : '#f44336'};
  font-size: 1.1rem;
  margin-top: 10px;
`;

const Tasks = () => {
  const [selectedNumbers, setSelectedNumbers] = useState([]);
  const [selectedColors, setSelectedColors] = useState([]);
  const [answers, setAnswers] = useState({});
  const [feedback, setFeedback] = useState({});

  const handleNumberClick = (id) => {
    if (selectedNumbers.includes(id)) {
      setSelectedNumbers(selectedNumbers.filter(num => num !== id));
    } else if (selectedNumbers.length < 1) {
      setSelectedNumbers([...selectedNumbers, id]);
    }
  };

  const handleColorClick = (id) => {
    if (selectedColors.includes(id)) {
      setSelectedColors(selectedColors.filter(num => num !== id));
    } else if (selectedColors.length < 1) {
      setSelectedColors([...selectedColors, id]);
    }

    if (selectedNumbers.length === 1 && !selectedColors.includes(id)) {
      // შევამოწმოთ თანხვედრა
      const isCorrect = selectedNumbers[0] === id;
      setFeedback(prev => ({
        ...prev,
        [selectedNumbers[0]]: isCorrect
      }));
      setSelectedNumbers([]);
      setSelectedColors([]);
    }
  };

  const handleAnswerSubmit = (taskId) => {
    const task = tasks.find(t => t.id === taskId);
    const isCorrect = parseInt(answers[taskId]) === task.correctAnswer;
    setFeedback(prev => ({
      ...prev,
      [taskId]: isCorrect
    }));
  };

  const renderMatchingGame = (task) => (
    <MatchingGame>
      <div>
        {task.items.map(item => (
          <MatchingItem
            key={item.id}
            isSelected={selectedNumbers.includes(item.id)}
            onClick={() => handleNumberClick(item.id)}
            color={feedback[item.id] ? '#4CAF50' : '#ddd'}
          >
            {item.text}
          </MatchingItem>
        ))}
      </div>
      <div>
        {task.items.map(item => (
          <MatchingItem
            key={item.id}
            isSelected={selectedColors.includes(item.id)}
            onClick={() => handleColorClick(item.id)}
            color={item.color}
          >
            {item.colorName}
          </MatchingItem>
        ))}
      </div>
    </MatchingGame>
  );

  const renderMathGame = (task) => (
    <MathGame>
      <TaskDescription>{task.question}</TaskDescription>
      <VisualizationArea>
        {task.visualization.box1 && (
          <Box>
            {[...Array(task.visualization.box1)].map((_, i) => (
              <Item key={i} color={task.id === 'apples' ? '#ff6b6b' : '#4CAF50'} />
            ))}
          </Box>
        )}
        {task.visualization.box2 && (
          <Box>
            {[...Array(task.visualization.box2)].map((_, i) => (
              <Item key={i} color={task.id === 'apples' ? '#ff6b6b' : '#4CAF50'} />
            ))}
          </Box>
        )}
      </VisualizationArea>
      <div>
        <Input
          type="number"
          value={answers[task.id] || ''}
          onChange={(e) => setAnswers(prev => ({
            ...prev,
            [task.id]: e.target.value
          }))}
          placeholder="პასუხი"
        />
        <Button onClick={() => handleAnswerSubmit(task.id)}>შემოწმება</Button>
      </div>
      {feedback[task.id] !== undefined && (
        <Message isCorrect={feedback[task.id]}>
          {feedback[task.id] ? '🎉 სწორია!' : '❌ სცადე თავიდან'}
        </Message>
      )}
    </MathGame>
  );

  return (
    <TasksContainer>
      {tasks.map(task => (
        <TaskCard key={task.id}>
          <TaskTitle>{task.title}</TaskTitle>
          <TaskDescription>{task.description}</TaskDescription>
          {task.type === 'matching' ? renderMatchingGame(task) : renderMathGame(task)}
        </TaskCard>
      ))}
    </TasksContainer>
  );
};

export default Tasks;
