const Tasks = () => {
  const { playerName, showNameModal, updatePlayerName } = usePlayer();
  const [currentTaskIndex, setCurrentTaskIndex] = useState(0);
  const [selectedNumbers, setSelectedNumbers] = useState([]);
  const [selectedColors, setSelectedColors] = useState([]);
  const [isVisible, setIsVisible] = useState(true);
  const navigate = useNavigate();

  const currentTask = useMemo(() => tasks[currentTaskIndex], [currentTaskIndex]);

  const handleNumberClick = useCallback((number) => {
    setSelectedNumbers(prev => {
      const newSelection = [...prev];
      const index = newSelection.indexOf(number);
      if (index === -1) {
        newSelection.push(number);
      } else {
        newSelection.splice(index, 1);
      }
      return newSelection;
    });
  }, []);

  const handleNextTask = useCallback(() => {
    setIsVisible(false);
    setTimeout(() => {
      setCurrentTaskIndex((prev) => (prev + 1) % tasks.length);
      setSelectedNumbers([]);
      setSelectedColors([]);
      setIsVisible(true);
    }, 300);
  }, []);

  const handlePreviousTask = useCallback(() => {
    setIsVisible(false);
    setTimeout(() => {
      setCurrentTaskIndex((prev) => (prev - 1 + tasks.length) % tasks.length);
      setSelectedNumbers([]);
      setSelectedColors([]);
      setIsVisible(true);
    }, 300);
  }, []);

  const handleColorClick = useCallback((color) => {
    setSelectedColors(prev => {
      const newSelection = [...prev];
      const index = newSelection.indexOf(color);
      if (index === -1) {
        newSelection.push(color);
      } else {
        newSelection.splice(index, 1);
      }
      return newSelection;
    });
  }, []);

  return (
    <Container>
      <HomeButton onClick={() => navigate('/')}>🏠</HomeButton>
      <TaskCard>
        <Title>
          {currentTask.icon} {currentTask.title}
        </Title>
        <Subtitle>{currentTask.description}</Subtitle>

        {isVisible && (
          <>
            {currentTask.type === 'matching' && (
              <MatchingGame>
                <ItemsContainer>
                  {currentTask.items.map((item, index) => (
                    <Item
                      key={index}
                      onClick={() => handleNumberClick(index)}
                      selected={selectedNumbers.includes(index)}
                    >
                      {item}
                    </Item>
                  ))}
                </ItemsContainer>
                <ItemsContainer>
                  {currentTask.colors.map((color, index) => (
                    <Item
                      key={index}
                      color={color}
                      onClick={() => handleColorClick(index)}
                      selected={selectedColors.includes(index)}
                    >
                      {currentTask.colorNames[index]}
                    </Item>
                  ))}
                </ItemsContainer>
              </MatchingGame>
            )}
          </>
        )}

        <ButtonsContainer>
          {currentTaskIndex > 0 && (
            <NavigationButton prev onClick={handlePreviousTask}>
              ⬅️ წინა ამოცანა
            </NavigationButton>
          )}
          <NavigationButton onClick={handleNextTask}>
            შემდეგი ამოცანა ➡️
          </NavigationButton>
        </ButtonsContainer>
      </TaskCard>
      {showNameModal && <NameModal onSubmit={updatePlayerName} />}
    </Container>
  );
};

export default Tasks;
