import React, { useState } from 'react';
import styled from 'styled-components';
import { usePlayer } from '../context/PlayerContext';

const DrawingGame = () => {
  const { updateGameProgress } = usePlayer();
  const [selectedColor, setSelectedColor] = useState(null);
  const [showFeedback, setShowFeedback] = useState(false);

  const colors = [
    { value: '#FF0000' }, // წითელი
    { value: '#FFA500' }, // ნარინჯისფერი
    { value: '#FFFF00' }, // ყვითელი
    { value: '#008000' }, // მწვანე
    { value: '#0000FF' }, // ლურჯი
    { value: '#4B0082' }, // იისფერი
    { value: '#EE82EE' }  // ვარდისფერი
  ];

  return (
    <PageContainer>
      <Header>
        <HomeLink href="/">🏠</HomeLink>
        <Title>გააფერადე</Title>
      </Header>

      <GameContainer>
        <DrawingArea>
          <OutlineImage 
            src="/assets/images/outlines/1.png"
            alt="გასაფერადებელი სურათი" 
          />
          <ColorOverlay style={{ backgroundColor: selectedColor || 'transparent' }} />
        </DrawingArea>

        <ColorPalette>
          {colors.map((color, index) => (
            <ColorOption
              key={index}
              color={color.value}
              onClick={() => setSelectedColor(color.value)}
            />
          ))}
        </ColorPalette>

        <ClearButton onClick={() => setSelectedColor(null)}>
          გასუფთავება
        </ClearButton>
      </GameContainer>
    </PageContainer>
  );
};

const PageContainer = styled.div`
  min-height: 100vh;
  padding: 20px;
  background-color: #f5f5f5;
`;

const Header = styled.div`
  display: flex;
  align-items: center;
  margin-bottom: 20px;
`;

const HomeLink = styled.a`
  font-size: 24px;
  text-decoration: none;
  margin-right: 20px;
`;

const Title = styled.h1`
  color: #333;
  margin: 0;
`;

const GameContainer = styled.div`
  max-width: 800px;
  margin: 0 auto;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 20px;
`;

const DrawingArea = styled.div`
  position: relative;
  width: 100%;
  max-width: 500px;
  aspect-ratio: 1;
  border: 2px solid #e0e0e0;
  border-radius: 8px;
  overflow: hidden;
`;

const OutlineImage = styled.img`
  width: 100%;
  height: 100%;
  object-fit: contain;
`;

const ColorOverlay = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  mix-blend-mode: multiply;
  pointer-events: none;
`;

const ColorPalette = styled.div`
  display: flex;
  gap: 10px;
  flex-wrap: wrap;
  justify-content: center;
`;

const ColorOption = styled.button`
  width: 50px;
  height: 50px;
  border: none;
  border-radius: 8px;
  background-color: ${props => props.color};
  cursor: pointer;
  transition: transform 0.2s;

  &:hover {
    transform: scale(1.1);
  }
`;

const ClearButton = styled.button`
  padding: 10px 20px;
  border: none;
  border-radius: 8px;
  background-color: #f44336;
  color: white;
  cursor: pointer;
  transition: transform 0.2s;

  &:hover {
    transform: scale(1.05);
  }
`;

export default DrawingGame;
