import React, { createContext, useContext, useState, useEffect } from 'react';

const PlayerContext = createContext();

export const PlayerProvider = ({ children }) => {
  const [gameProgress, setGameProgress] = useState(() => {
    const savedProgress = localStorage.getItem('gameProgress');
    return savedProgress ? JSON.parse(savedProgress) : {
      puzzles: [],
      completedScrambleTasks: [],
      taskScores: {},
      completedTasks: [],
      scrambleScores: {},
      lastPlayed: null
    };
  });

  useEffect(() => {
    localStorage.setItem('gameProgress', JSON.stringify(gameProgress));
  }, [gameProgress]);

  const updateGameProgress = (gameType, timestamp, data) => {
    setGameProgress(prevProgress => {
      const newProgress = { ...prevProgress };

      switch (gameType) {
        case 'task':
          if (!newProgress.completedTasks) {
            newProgress.completedTasks = [];
          }
          if (!newProgress.taskScores) {
            newProgress.taskScores = {};
          }

          if (data?.taskId && !newProgress.completedTasks.includes(data.taskId)) {
            newProgress.completedTasks = [...newProgress.completedTasks, data.taskId];
          }
          if (data?.taskId && data?.score) {
            newProgress.taskScores[data.taskId] = data.score;
          }
          break;

        case -1: // Reset
          return {
            puzzles: [],
            completedScrambleTasks: [],
            taskScores: {},
            completedTasks: [],
            scrambleScores: {},
            lastPlayed: null
          };
      }

      newProgress.lastPlayed = timestamp;
      return newProgress;
    });
  };

  const resetProgress = () => {
    setGameProgress({
      puzzles: [],
      completedScrambleTasks: [],
      taskScores: {},
      completedTasks: [],
      scrambleScores: {},
      lastPlayed: null
    });
    localStorage.setItem('gameProgress', JSON.stringify({
      puzzles: [],
      completedScrambleTasks: [],
      taskScores: {},
      completedTasks: [],
      scrambleScores: {},
      lastPlayed: null
    }));
  };

  const value = {
    gameProgress,
    updateGameProgress,
    resetProgress
  };

  return (
    <PlayerContext.Provider value={value}>
      {children}
    </PlayerContext.Provider>
  );
};

export const usePlayer = () => {
  const context = useContext(PlayerContext);
  if (!context) {
    throw new Error('usePlayer must be used within a PlayerProvider');
  }
  return context;
};
