import React from 'react';
import styled from 'styled-components';

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 20px;
  margin-bottom: 30px;
`;

const Title = styled.h2`
  color: #2196F3;
  margin: 0;
  font-size: 1.5rem;
`;

const ButtonGroup = styled.div`
  display: flex;
  gap: 10px;
`;

const DifficultyButton = styled.button`
  padding: 10px 20px;
  border: 2px solid #2196F3;
  border-radius: 8px;
  background-color: ${props => props.isSelected ? '#2196F3' : 'white'};
  color: ${props => props.isSelected ? 'white' : '#2196F3'};
  cursor: pointer;
  transition: all 0.3s ease;
  font-size: 1.1rem;

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 2px 8px rgba(33, 150, 243, 0.3);
  }

  &:active {
    transform: translateY(0);
  }
`;

const difficulties = [
  { value: 3, label: 'მარტივი' },
  { value: 4, label: 'საშუალო' },
  { value: 5, label: 'რთული' }
];

const DifficultySelector = ({ difficulty, onDifficultyChange }) => {
  return (
    <Container>
      <Title>აირჩიე სირთულის დონე</Title>
      <ButtonGroup>
        {difficulties.map(({ value, label }) => (
          <DifficultyButton
            key={value}
            isSelected={difficulty === value}
            onClick={() => onDifficultyChange(value)}
          >
            {label}
          </DifficultyButton>
        ))}
      </ButtonGroup>
    </Container>
  );
};

export default DifficultySelector;
