  opacity: ${props => props.disabled ? 0.5 : 1};
  transform: ${props => props.selected ? 'scale(1.02)' : 'none'};
  border-color: ${props => props.selected ? '#4CAF50' : '#ddd'};
  margin: 0;
  min-width: 200px;
  flex: 1;
  transition: all 0.2s ease;

  &:hover {
    transform: ${props => props.disabled ? 'none' : 'scale(1.02)'};
