  }
`;

const OptionsGrid = styled.div`
  display: flex;
  flex-direction: row;
  gap: 15px;
  margin: 20px auto;
  max-width: 1000px;
  background: rgba(255, 255, 255, 0.9);
  padding: 20px;
  border-radius: 12px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
  overflow-x: auto;
`;

const MemoryGame = () => {
  const [gameState, setGameState] = useState('setup');
  const [numberOfImages, setNumberOfImages] = useState(3);
  const [selectedImages, setSelectedImages] = useState([]);
  const [shuffledImages, setShuffledImages] = useState([]);
  const [timer, setTimer] = useState(5);
  const [userSequence, setUserSequence] = useState([]);
  const [showSuccess, setShowSuccess] = useState(false);
  const [showError, setShowError] = useState(false);
  const [gameMode, setGameMode] = useState('sequence'); // sequence or missing
  const [missingImage, setMissingImage] = useState(null);
  const [selectedOption, setSelectedOption] = useState(null);
  const [optionImages, setOptionImages] = useState([]);
  const { updateGameProgress } = usePlayer();
  const navigate = useNavigate();

  // áƒ¤áƒáƒ¢áƒáƒ”áƒ‘áƒ˜áƒ¡ áƒ©áƒáƒ¢áƒ•áƒ˜áƒ áƒ—áƒ•áƒ
  const loadImages = useCallback(async () => {
    try {
      const images = [
        'áƒáƒšáƒ£áƒ‘áƒáƒšáƒ˜.webp', 'áƒáƒ áƒ¬áƒ˜áƒ•áƒ˜.webp', 'áƒáƒ¢áƒáƒ›áƒ˜.webp', 'áƒáƒ¥áƒšáƒ”áƒ›áƒ˜.webp', 'áƒ‘áƒáƒœáƒáƒœáƒ˜.webp',
        'áƒ‘áƒ áƒáƒ¬áƒ”áƒ£áƒšáƒ˜.webp', 'áƒ‘áƒ£.webp', 'áƒ’áƒ•áƒ”áƒšáƒ˜.webp', 'áƒ“áƒáƒ—áƒ•áƒ˜.webp', 'áƒ“áƒ”áƒšáƒ¤áƒ˜áƒœáƒ˜.webp',
        'áƒ•áƒáƒ¨áƒšáƒ˜.webp', 'áƒ–áƒ”áƒ‘áƒ áƒ.webp', 'áƒ–áƒ•áƒ˜áƒ’áƒ”áƒœáƒ˜.webp', 'áƒ–áƒ¦áƒáƒ áƒ‘áƒ˜.webp', 'áƒ—áƒ®áƒ (2).webp',
        'áƒ˜áƒ áƒ”áƒ›áƒ˜ (2).webp', 'áƒ™áƒáƒ¥áƒ¢áƒ£áƒ¡áƒ˜.webp', 'áƒ™áƒ”áƒœáƒ’áƒ£áƒ áƒ£.webp', 'áƒ™áƒ˜áƒ¢áƒ áƒ˜.webp', 'áƒ™áƒáƒ“áƒáƒšáƒ.webp',
        'áƒ™áƒ£áƒ áƒ“áƒ¦áƒ”áƒšáƒ˜.webp', 'áƒšáƒáƒ›áƒ˜.webp', 'áƒ›áƒ’áƒ”áƒšáƒ.webp', 'áƒ›áƒ”áƒšáƒ˜áƒ.webp', 'áƒ›áƒ–áƒ”áƒ¡áƒ£áƒ›áƒ–áƒ˜áƒ áƒ.webp',
        'áƒ›áƒ¡áƒ®áƒáƒšáƒ˜.webp', 'áƒœáƒáƒ§áƒ˜áƒœáƒ˜.webp', 'áƒœáƒ˜áƒáƒ áƒ˜.webp', 'áƒáƒ‘áƒáƒ‘áƒ.webp', 'áƒáƒ”áƒáƒ”áƒšáƒ.webp',
        'áƒáƒáƒ›áƒ˜áƒ“áƒáƒ áƒ˜.webp', 'áƒŸáƒ˜áƒ áƒáƒ¤áƒ˜.webp', 'áƒ áƒ™áƒ.webp', 'áƒ¡áƒ˜áƒ›áƒ˜áƒœáƒ“áƒ˜.webp', 'áƒ¡áƒáƒ˜áƒšáƒ.webp',
        'áƒ¢áƒ˜áƒ¢áƒ.webp', 'áƒ¤áƒáƒ áƒ—áƒáƒ®áƒáƒšáƒ˜.webp', 'áƒ¥áƒšáƒ˜áƒáƒ•áƒ˜.webp', 'áƒ§áƒ£áƒ áƒ«áƒ”áƒœáƒ˜.webp', 'áƒ®áƒáƒ®áƒ•áƒ˜.webp'
      ];

      return images.map(filename => ({
        name: filename.replace('.webp', ''),
        url: `${process.env.PUBLIC_URL}/context/newphotos/${filename}`
      }));
    } catch (error) {
      console.error('Error loading images:', error);
      return [];
    }
  }, []);

  // Fisher-Yates shuffle áƒáƒšáƒ’áƒáƒ áƒ˜áƒ—áƒ›áƒ˜
  const shuffleArray = useCallback((array) => {
    const shuffled = [...array];
    for (let i = shuffled.length - 1; i > 0; i--) {
      // áƒ•áƒ˜áƒ§áƒ”áƒœáƒ”áƒ‘áƒ— crypto.getRandomValues áƒ£áƒ¤áƒ áƒ áƒ«áƒšáƒ˜áƒ”áƒ áƒ˜ áƒ¨áƒ”áƒ›áƒ—áƒ®áƒ•áƒ”áƒ•áƒ˜áƒ—áƒáƒ‘áƒ˜áƒ¡áƒ—áƒ•áƒ˜áƒ¡
      const j = Math.floor(crypto.getRandomValues(new Uint32Array(1))[0] / (0xffffffff + 1) * (i + 1));
      [shuffled[i], shuffled[j]] = [shuffled[j], shuffled[i]];
    }

    // áƒ•áƒáƒ›áƒáƒ¬áƒ›áƒ”áƒ‘áƒ— áƒ áƒáƒ› áƒáƒ®áƒáƒšáƒ˜ áƒ›áƒáƒ¡áƒ˜áƒ•áƒ˜ áƒáƒ  áƒ˜áƒ§áƒáƒ¡ áƒ˜áƒ’áƒ˜áƒ•áƒ” áƒ áƒáƒª áƒáƒ áƒ˜áƒ’áƒ˜áƒœáƒáƒšáƒ˜ áƒáƒœ áƒ£áƒ™áƒ£áƒ¦áƒ›áƒ áƒ“áƒáƒšáƒáƒ’áƒ”áƒ‘áƒ£áƒšáƒ˜
    const isReverse = shuffled.every((item, index) => item === array[array.length - 1 - index]);
    const isSame = shuffled.every((item, index) => item === array[index]);

    if (isReverse || isSame) {
      // áƒ—áƒ£ áƒ£áƒ™áƒ£áƒ¦áƒ›áƒ áƒáƒœ áƒ˜áƒ’áƒ˜áƒ•áƒ” áƒ—áƒáƒœáƒ›áƒ˜áƒ›áƒ“áƒ”áƒ•áƒ áƒáƒ‘áƒáƒ, áƒ™áƒ˜áƒ“áƒ”áƒ• áƒ”áƒ áƒ—áƒ®áƒ”áƒš áƒ•áƒ£áƒ áƒ”áƒ•áƒ—
      return shuffleArray(array);
    }

    return shuffled;
  }, []);

  // áƒ áƒ”áƒœáƒ“áƒáƒ› áƒ¤áƒáƒ¢áƒáƒ”áƒ‘áƒ˜áƒ¡ áƒáƒ áƒ©áƒ”áƒ•áƒ
  const selectRandomImages = useCallback(async () => {
    const allImages = await loadImages();
    console.log('All images:', allImages);
    const shuffled = shuffleArray(allImages);
    const selected = shuffled.slice(0, numberOfImages);
    console.log('Selected images:', selected);
    setSelectedImages(selected);
    setShuffledImages([...selected]);

    if (gameMode === 'missing') {
      // áƒ“áƒáƒ›áƒáƒ¢áƒ”áƒ‘áƒ˜áƒ—áƒ˜ áƒ¤áƒáƒ¢áƒáƒ”áƒ‘áƒ˜áƒ¡ áƒáƒ áƒ©áƒ”áƒ•áƒ áƒáƒ¤áƒªáƒ˜áƒ”áƒ‘áƒ˜áƒ¡áƒ—áƒ•áƒ˜áƒ¡
      const remainingImages = shuffled.slice(numberOfImages, numberOfImages + 3);
      const randomIndex = Math.floor(Math.random() * selected.length);
      const missingImg = selected[randomIndex];
      setMissingImage(missingImg);

      // áƒáƒ¤áƒªáƒ˜áƒ”áƒ‘áƒ˜áƒ¡ áƒ¨áƒ”áƒ¥áƒ›áƒœáƒ
      const options = shuffleArray([...remainingImages, missingImg]);
      setOptionImages(options);
    }
  }, [numberOfImages, gameMode]);

  // áƒ—áƒáƒ›áƒáƒ¨áƒ˜áƒ¡ áƒ“áƒáƒ¬áƒ§áƒ”áƒ‘áƒ
  const startGame = useCallback(async () => {
    await selectRandomImages();
    setGameState('showing');
    setTimer(5);
    setSelectedOption(null);
  }, [selectRandomImages]);

  // áƒ›áƒáƒ›áƒ®áƒ›áƒáƒ áƒ”áƒ‘áƒšáƒ˜áƒ¡ áƒáƒ áƒ©áƒ”áƒ•áƒáƒœáƒ˜áƒ¡ áƒ“áƒáƒ›áƒ£áƒ¨áƒáƒ•áƒ”áƒ‘áƒ
  const handleImageClick = useCallback((image, index) => {
    if (gameState !== 'playing') return;

    if (gameMode === 'sequence') {
      setUserSequence(prev => {
        const newSequence = [...prev, { image, index }];

        if (newSequence.length === selectedImages.length) {
          const isCorrect = selectedImages.every((img, idx) => 
            newSequence[idx].image.name === img.name
          );

          if (isCorrect) {
            updateGameProgress('memory', Date.now(), { score: 100 });
            setShowSuccess(true);
            setTimeout(() => {
              setShowSuccess(false);
              setGameState('setup');
              setUserSequence([]);
            }, 2000);
          } else {
            setShowError(true);
            setTimeout(() => {
              setShowError(false);
              setUserSequence([]);
            }, 2000);
          }
        }

        return newSequence;
      });
    }
  }, [gameState, gameMode, selectedImages, updateGameProgress]);

  // áƒáƒ¤áƒªáƒ˜áƒ˜áƒ¡ áƒáƒ áƒ©áƒ”áƒ•áƒ
  const handleOptionSelect = useCallback((image) => {
    if (gameState !== 'playing') return;

    setSelectedOption(image);
    const isCorrect = image.name === missingImage.name;

    if (isCorrect) {
      updateGameProgress('memory', Date.now(), { score: 100 });
      setShowSuccess(true);
      setTimeout(() => {
        setShowSuccess(false);
        setGameState('setup');
        setSelectedOption(null);
      }, 2000);
    } else {
      setShowError(true);
      setTimeout(() => {
        setShowError(false);
        setSelectedOption(null);
      }, 2000);
    }
  }, [gameState, missingImage, updateGameProgress]);

  // áƒ¤áƒáƒ¢áƒáƒ”áƒ‘áƒ˜áƒ¡ áƒáƒ áƒ”áƒ•áƒ
  const shuffleImages = useCallback(() => {
    setShuffledImages(prev => {
      const shuffled = shuffleArray(prev);
      console.log('Original order:', prev.map(img => img.name));
      console.log('Shuffled order:', shuffled.map(img => img.name));
      return shuffled;
    });
    setGameState('playing');
  }, [shuffleArray]);

  // áƒ¢áƒáƒ˜áƒ›áƒ”áƒ áƒ˜áƒ¡ áƒšáƒáƒ’áƒ˜áƒ™áƒ
  useEffect(() => {
    let interval;

    if ((gameState === 'showing' || gameState === 'memorizing') && timer > 0) {
      interval = setInterval(() => {
        setTimer(prev => {
          console.log('Timer tick:', prev - 1);
          return prev - 1;
        });
      }, 1000);

      console.log('Starting timer:', timer, 'State:', gameState);
    } else if (timer === 0) {
      if (gameState === 'showing') {
        console.log('Switching to memorizing');
        setGameState('memorizing');
        setTimer(2);
      } else if (gameState === 'memorizing') {
        console.log('Switching to playing');
        shuffleImages();
      }
    }

    return () => {
      if (interval) {
        clearInterval(interval);
      }
    };
  }, [timer, gameState, shuffleImages]);

  return (
    <>
      <GlobalStyle />
      <GameContainer>
        {gameState === 'setup' && (
          <SetupContainer>
            <GameTitle>áƒ›áƒ”áƒ®áƒ¡áƒ˜áƒ”áƒ áƒ”áƒ‘áƒ˜áƒ¡ áƒ—áƒáƒ›áƒáƒ¨áƒ˜ <span>ğŸ§ </span></GameTitle>
            <h2>áƒáƒ˜áƒ áƒ©áƒ˜áƒ” áƒ áƒáƒ›áƒ“áƒ”áƒœáƒ˜ áƒ¡áƒ£áƒ áƒáƒ—áƒ˜ áƒ’áƒ˜áƒœáƒ“áƒ áƒ“áƒáƒ˜áƒ›áƒáƒ®áƒ¡áƒáƒ•áƒ áƒ:</h2>
            <NumberSelect 
              value={numberOfImages} 
              onChange={(e) => setNumberOfImages(Number(e.target.value))}
            >
              <option value={3}>3 áƒ¡áƒ£áƒ áƒáƒ—áƒ˜</option>
              <option value={4}>4 áƒ¡áƒ£áƒ áƒáƒ—áƒ˜</option>
              <option value={5}>5 áƒ¡áƒ£áƒ áƒáƒ—áƒ˜</option>
            </NumberSelect>

            <GameModeToggle>
              <ToggleButton 
                active={gameMode === 'sequence'} 
                onClick={() => setGameMode('sequence')}
              >
                áƒ—áƒáƒœáƒ›áƒ˜áƒ›áƒ“áƒ”áƒ•áƒ áƒáƒ‘áƒ
              </ToggleButton>
              <ToggleButton 
                active={gameMode === 'missing'} 
                onClick={() => setGameMode('missing')}
              >
                áƒ¤áƒáƒ¢áƒáƒ¡ áƒáƒ›áƒáƒ™áƒšáƒ”áƒ‘áƒ
              </ToggleButton>
            </GameModeToggle>

            <StartButton onClick={startGame}>áƒ“áƒáƒ¬áƒ§áƒ”áƒ‘áƒ</StartButton>
          </SetupContainer>
        )}

        {showSuccess && (
          <SuccessMessage show>
            áƒ’áƒ˜áƒšáƒáƒªáƒáƒ•! {gameMode === 'sequence' ? 'áƒ¡áƒ¬áƒáƒ áƒáƒ“ áƒ’áƒáƒ˜áƒ®áƒ¡áƒ”áƒœáƒ” áƒ—áƒáƒœáƒ›áƒ˜áƒ›áƒ“áƒ”áƒ•áƒ áƒáƒ‘áƒ!' : 'áƒ¡áƒ¬áƒáƒ áƒáƒ“ áƒ’áƒáƒ›áƒáƒ˜áƒªáƒáƒœáƒ˜ áƒ áƒáƒ›áƒ”áƒšáƒ˜ áƒ¤áƒáƒ¢áƒ áƒáƒ™áƒšáƒ“áƒ!'} ğŸ‰
          </SuccessMessage>
        )}

        {showError && (
          <ErrorMessage show>
            áƒ¡áƒáƒ›áƒ¬áƒ£áƒ®áƒáƒ áƒáƒ“ {gameMode === 'sequence' ? 'áƒ—áƒáƒœáƒ›áƒ˜áƒ›áƒ“áƒ”áƒ•áƒ áƒáƒ‘áƒ áƒáƒ áƒáƒ¡áƒ¬áƒáƒ áƒ˜áƒ' : 'áƒ•áƒ”áƒ  áƒ’áƒáƒ›áƒáƒ˜áƒªáƒáƒœáƒ˜ áƒ áƒáƒ›áƒ”áƒšáƒ˜ áƒ¤áƒáƒ¢áƒ áƒáƒ™áƒšáƒ“áƒ'}. áƒ¡áƒªáƒáƒ“áƒ” áƒ—áƒáƒ•áƒ˜áƒ“áƒáƒœ! ğŸ¤”
          </ErrorMessage>
        )}

        {gameState === 'showing' && (
          <>
            <Timer>
              <span className="hourglass">âŒ›</span>
              {timer} áƒ¬áƒáƒ›áƒ˜
            </Timer>
            <Message show>áƒ“áƒáƒ˜áƒ›áƒáƒ®áƒ¡áƒáƒ•áƒ áƒ” áƒ¡áƒ£áƒ áƒáƒ—áƒ”áƒ‘áƒ˜!</Message>
            <ImagesGrid>
              {selectedImages.map((image, index) => (
                <ImageCard key={index}>
                  <img src={image.url} alt={image.name} />
                  <p>{image.name}</p>
                </ImageCard>
              ))}
            </ImagesGrid>
          </>
        )}

        {gameState === 'memorizing' && (
          <>
            <Timer>
              <span className="hourglass">âŒ›</span>
              {timer} áƒ¬áƒáƒ›áƒ˜
            </Timer>
            <Message show>áƒ“áƒáƒ®áƒ£áƒ­áƒ” áƒ—áƒ•áƒáƒšáƒ”áƒ‘áƒ˜! {timer} áƒ¬áƒáƒ›áƒ˜ áƒ“áƒáƒ áƒ©áƒ</Message>
          </>
        )}

        {gameState === 'playing' && gameMode === 'sequence' && (
          <>
            <Message show>áƒáƒ¦áƒáƒ“áƒ’áƒ˜áƒœáƒ” áƒ¡áƒ£áƒ áƒáƒ—áƒ”áƒ‘áƒ˜áƒ¡ áƒ—áƒáƒœáƒ›áƒ˜áƒ›áƒ“áƒ”áƒ•áƒ áƒáƒ‘áƒ!</Message>
            <ImagesGrid>
              {shuffledImages.map((image, index) => (
                <ImageCard 
                  key={index}
                  isSelected={userSequence.some(seq => seq.index === index)}
                  isPlayable={!userSequence.some(seq => seq.index === index)}
                  onClick={() => !userSequence.some(seq => seq.index === index) && handleImageClick(image, index)}
                >
                  <img src={image.url} alt={image.name} />
                  <p>{image.name}</p>
                  {userSequence.findIndex(seq => seq.index === index) > -1 && (
                    <div className="order-number">
                      {userSequence.findIndex(seq => seq.index === index) + 1}
                    </div>
                  )}
                </ImageCard>
              ))}
            </ImagesGrid>
            <Message show>
              {userSequence.length < selectedImages.length 
                ? `áƒáƒ˜áƒ áƒ©áƒ˜áƒ” ${userSequence.length + 1}-áƒ” áƒ¡áƒ£áƒ áƒáƒ—áƒ˜`
                : 'áƒ§áƒ•áƒ”áƒšáƒ áƒ¡áƒ£áƒ áƒáƒ—áƒ˜ áƒáƒ áƒ©áƒ”áƒ£áƒšáƒ˜áƒ'}
            </Message>
          </>
        )}

        {gameState === 'playing' && gameMode === 'missing' && (
          <>
            <Message show>áƒ áƒáƒ›áƒ”áƒšáƒ˜ áƒ¤áƒáƒ¢áƒ áƒáƒ™áƒšáƒ˜áƒ?</Message>
            <ImagesGrid>
              {shuffledImages.filter(img => img.name !== missingImage.name).map((image, index) => (
                <ImageCard key={index}>
                  <img src={image.url} alt={image.name} />
                  <p>{image.name}</p>
                </ImageCard>
              ))}
            </ImagesGrid>
            <Message show>áƒáƒ˜áƒ áƒ©áƒ˜áƒ” áƒ¡áƒ¬áƒáƒ áƒ˜ áƒáƒáƒ¡áƒ£áƒ®áƒ˜:</Message>
            <OptionsGrid>
              {optionImages.map((image, index) => (
                <OptionCard
                  key={index}
                  selected={selectedOption?.name === image.name}
                  onClick={() => handleOptionSelect(image)}
                >
                  <img src={image.url} alt={image.name} />
                  <p>{image.name}</p>
                </OptionCard>
              ))}
            </OptionsGrid>
          </>
        )}
      </GameContainer>
    </>
  );
};

export default MemoryGame;
