import React, { useState, useEffect } from 'react';
import { usePlayer } from '../context/PlayerContext';
import { motion } from 'framer-motion';
import { Book, AlignLeft, PuzzleIcon as PuzzlePiece } from 'lucide-react';
import styled from 'styled-components';

const DashboardContainer = styled.div`
  min-height: 100vh;
  width: 100%;
  height: 100vh;
  position: fixed;
  top: 0;
  left: 0;
  background: linear-gradient(to bottom right, #1a237e, #4a148c, #880e4f);
  padding: 2rem;
  color: white;
  overflow-y: auto;
  z-index: 1000;
  box-sizing: border-box;

  * {
    box-sizing: border-box;
  }

  &::-webkit-scrollbar {
    display: none;
  }

  -ms-overflow-style: none;
  scrollbar-width: none;

  @media (max-width: 768px) {
    padding: 1rem;
  }
`;

const ContentWrapper = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  width: 100%;
`;

const Title = styled.h1`
  font-size: 2.5rem;
  font-weight: bold;
  margin-bottom: 2rem;
  text-align: center;

  @media (max-width: 768px) {
    font-size: 2rem;
    margin-bottom: 1.5rem;
  }

  @media (max-width: 480px) {
    font-size: 1.75rem;
    margin-bottom: 1rem;
  }
`;

const TabContainer = styled.div`
  display: flex;
  justify-content: flex-end;
  gap: 0.5rem;
  margin-bottom: 1rem;

  @media (max-width: 768px) {
    justify-content: center;
    flex-wrap: wrap;
  }
`;

const TabButton = styled.button`
  display: flex;
  align-items: center;
  gap: 0.5rem;
  padding: 0.5rem 1rem;
  border-radius: 9999px;
  transition: all 0.2s;
  background: ${props => props.active ? 'white' : 'rgba(255, 255, 255, 0.2)'};
  color: ${props => props.active ? '#4a148c' : 'white'};
  white-space: nowrap;

  &:hover {
    background: ${props => props.active ? 'white' : 'rgba(255, 255, 255, 0.3)'};
  }

  @media (max-width: 480px) {
    padding: 0.4rem 0.8rem;
    font-size: 0.9rem;

    svg {
      width: 16px;
      height: 16px;
    }
  }
`;

const StatsCard = styled(motion.div)`
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(10px);
  border-radius: 0.5rem;
  padding: 1.5rem;
  margin-bottom: 2rem;

  @media (max-width: 768px) {
    padding: 1.25rem;
  }

  @media (max-width: 480px) {
    padding: 1rem;
  }
`;

const StatsHeader = styled.h2`
  font-size: 1.5rem;
  font-weight: bold;
  margin-bottom: 1rem;
`;

const StatsGrid = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;

  @media (max-width: 768px) {
    flex-direction: column;
    gap: 1.5rem;
    align-items: stretch;
    text-align: center;
  }
`;

const StatValue = styled.p`
  font-size: 3rem;
  font-weight: bold;

  span {
    font-size: 1.5rem;
    font-weight: normal;
  }

  @media (max-width: 768px) {
    font-size: 2.5rem;

    span {
      font-size: 1.25rem;
    }
  }

  @media (max-width: 480px) {
    font-size: 2rem;

    span {
      font-size: 1rem;
    }
  }
`;

const StatLabel = styled.p`
  font-size: 1.125rem;
  margin-top: 0.5rem;

  @media (max-width: 768px) {
    font-size: 1rem;
  }
`;

const ProgressBar = styled.div`
  background: rgba(255, 255, 255, 0.2);
  height: 1rem;
  border-radius: 9999px;
  overflow: hidden;
  margin-top: 1.5rem;
`;

const Progress = styled.div`
  height: 100%;
  border-radius: 9999px;
  background: ${props => {
    switch (props.type) {
      case 'tasks':
        return '#4caf50';
      case 'scramble':
        return '#2196f3';
      case 'puzzle':
        return '#ffc107';
      default:
        return '#4caf50';
    }
  }};
  width: ${props => props.percent}%;
`;

const ProgressText = styled.p`
  text-align: right;
  margin-top: 0.5rem;
`;

const Dashboard = () => {
  const { gameProgress } = usePlayer();
  const [activeTab, setActiveTab] = useState('tasks');

  return (
    <DashboardContainer>
      <ContentWrapper>
        <Title>მოგესალმები, {gameProgress.playerName}! 👋</Title>

        <TabContainer>
          <TabButton
            active={activeTab === 'tasks'}
            onClick={() => setActiveTab('tasks')}
          >
            <Book size={20} />
            დავალებები
          </TabButton>
          <TabButton
            active={activeTab === 'scramble'}
            onClick={() => setActiveTab('scramble')}
          >
            <AlignLeft size={20} />
            არეული სიტყვები
          </TabButton>
          <TabButton
            active={activeTab === 'puzzle'}
            onClick={() => setActiveTab('puzzle')}
          >
            <PuzzlePiece size={20} />
            თავსატეხი
          </TabButton>
        </TabContainer>

        <StatsCard
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
        >
          <StatsHeader>
            {activeTab === 'tasks' && 'დავალებების პროგრესი'}
            {activeTab === 'scramble' && 'არეული სიტყვების პროგრესი'}
            {activeTab === 'puzzle' && 'თავსატეხის პროგრესი'}
          </StatsHeader>

          <StatsGrid>
            <div>
              <StatValue>
                {activeTab === 'tasks' && `${gameProgress.tasksCompleted || 0}`}
                {activeTab === 'scramble' && `${gameProgress.scramblesCompleted || 0}`}
                {activeTab === 'puzzle' && `${gameProgress.puzzlesCompleted || 0}`}
                <span> / {activeTab === 'tasks' ? '100' : '50'}</span>
              </StatValue>
              <StatLabel>დასრულებული დავალებები</StatLabel>
            </div>
          </StatsGrid>

          <ProgressBar>
            <Progress
              type={activeTab}
              percent={
                activeTab === 'tasks'
                  ? ((gameProgress.tasksCompleted || 0) / 100) * 100
                  : ((gameProgress[`${activeTab}sCompleted`] || 0) / 50) * 100
              }
            />
          </ProgressBar>
          <ProgressText>
            {activeTab === 'tasks'
              ? `${Math.round(((gameProgress.tasksCompleted || 0) / 100) * 100)}%`
              : `${Math.round(((gameProgress[`${activeTab}sCompleted`] || 0) / 50) * 100)}%`}
          </ProgressText>
        </StatsCard>
      </ContentWrapper>
    </DashboardContainer>
  );
};

export default Dashboard;
