import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { tasks } from '../data/tasks';
import { createGlobalStyle } from 'styled-components';
import NameModal from './NameModal';
import { usePlayer } from '../context/PlayerContext';

const TasksContainer = styled.div`
  max-width: 800px;
  margin: 0 auto;
  padding: 20px;
  min-height: 80vh;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const TaskCard = styled.div`
  background: white;
  border-radius: 15px;
  padding: 30px;
  margin-bottom: 20px;
  box-shadow: 0 8px 16px rgba(0, 0, 0, 0.1);
  width: 100%;
  max-width: 600px;
  transition: all 0.3s ease;
  opacity: ${props => props.isVisible ? 1 : 0};
  transform: ${props => props.isVisible ? 'translateY(0)' : 'translateY(20px)'};
`;

const TaskTitle = styled.h2`
  color: #2196F3;
  margin-bottom: 20px;
  text-align: center;
  font-size: 2rem;
`;

const TaskDescription = styled.p`
  color: #666;
  margin-bottom: 25px;
  text-align: center;
  font-size: 1.2rem;
`;

const MatchingGame = styled.div`
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: 20px;
  margin-top: 20px;
`;

const MatchingItem = styled.div`
  padding: 20px;
  margin: 10px;
  border-radius: 15px;
  cursor: ${props => props.disabled ? 'default' : 'pointer'};
  background-color: ${props => props.correct ? props.correctColor : props.isSelected ? '#e3f2fd' : 'white'};
  border: 3px solid ${props => props.color};
  font-size: 2rem;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: all 0.3s ease;
  opacity: ${props => props.disabled ? 0.7 : 1};

  &:hover {
    transform: ${props => props.disabled ? 'none' : 'scale(1.05)'};
    box-shadow: ${props => props.disabled ? 'none' : '0 4px 8px rgba(0,0,0,0.1)'};
  }
`;

const MathGame = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 25px;
`;

const StickersContainer = styled.div`
  display: flex;
  gap: 30px;
  justify-content: center;
  margin: 25px 0;
`;

const StickersGroup = styled.div`
  display: flex;
  gap: 10px;
  padding: 20px;
  background: #f8f9fa;
  border-radius: 15px;
  box-shadow: 0 2px 8px rgba(0,0,0,0.1);
  min-width: 120px;
  justify-content: center;
  transition: transform 0.3s ease;

  &:hover {
    transform: scale(1.05);
  }
`;

const Sticker = styled.div`
  font-size: 2rem;
  animation: popIn 0.5s ease-out;

  @keyframes popIn {
    0% { transform: scale(0); }
    50% { transform: scale(1.2); }
    100% { transform: scale(1); }
  }
`;

const Input = styled.input`
  padding: 15px;
  font-size: 1.5rem;
  border: 3px solid #2196F3;
  border-radius: 10px;
  width: 120px;
  text-align: center;
  margin: 0 10px;

  &:focus {
    outline: none;
    box-shadow: 0 0 0 3px rgba(33, 150, 243, 0.3);
  }
`;

const Button = styled.button`
  background-color: #4CAF50;
  color: white;
  border: none;
  padding: 15px 30px;
  border-radius: 10px;
  cursor: pointer;
  font-size: 1.2rem;
  transition: all 0.3s ease;
  margin: 10px;

  &:hover {
    background-color: #388E3C;
    transform: translateY(-3px);
    box-shadow: 0 5px 15px rgba(76, 175, 80, 0.3);
  }

  &:active {
    transform: translateY(0);
  }
`;

const Message = styled.div`
  color: ${props => props.isCorrect ? '#4CAF50' : '#f44336'};
  font-size: 1.3rem;
  margin-top: 15px;
  text-align: center;
  animation: fadeIn 0.5s ease-out;

  @keyframes fadeIn {
    0% { opacity: 0; transform: translateY(10px); }
    100% { opacity: 1; transform: translateY(0); }
  }
`;

const NavigationButtons = styled.div`
  display: flex;
  gap: 20px;
  margin-top: 30px;
`;

const HomeButton = styled.button`
  position: fixed;
  top: 20px;
  left: 20px;
  font-size: 2rem;
  background: none;
  border: none;
  cursor: pointer;
  transition: transform 0.3s ease;
  z-index: 100;

  &:hover {
    transform: scale(1.1);
  }
`;

const GlobalStyle = createGlobalStyle`
  @keyframes celebrate {
    0% {
      transform: translate(-50%, -50%) scale(0.1);
      opacity: 0;
    }
    50% {
      transform: translate(-50%, -50%) scale(1.2);
      opacity: 1;
    }
    100% {
      transform: translate(-50%, -50%) scale(1);
      opacity: 0;
    }
  }
`;

const celebrationStickers = ['üåü', 'üéâ', 'üéà', 'üéä', 'üèÜ', '‚≠ê', 'üí´', '‚ú®'];

const Tasks = () => {
  const { playerName, showNameModal, updatePlayerName } = usePlayer();
  const [currentTaskIndex, setCurrentTaskIndex] = useState(0);
  const [selectedNumbers, setSelectedNumbers] = useState([]);
  const [selectedColors, setSelectedColors] = useState([]);
  const [feedback, setFeedback] = useState({});
  const [answers, setAnswers] = useState({});
  const [isVisible, setIsVisible] = useState(true);
  const [shuffledItems, setShuffledItems] = useState([]);
  const [shuffledColors, setShuffledColors] = useState([]);

  useEffect(() => {
    const currentTask = tasks[currentTaskIndex];
    if (currentTask && currentTask.type === 'matching' && currentTask.items) {
      const shuffleArray = (array) => {
        if (!Array.isArray(array) || array.length === 0) return [];
        const newArray = [...array];
        for (let i = newArray.length - 1; i > 0; i--) {
          const j = Math.floor(Math.random() * (i + 1));
          [newArray[i], newArray[j]] = [newArray[j], newArray[i]];
        }
        return newArray;
      };

      setShuffledItems(shuffleArray(currentTask.items));
      setShuffledColors(shuffleArray([...currentTask.items]));
    }
  }, [currentTaskIndex]);

  const handleNumberClick = (id) => {
    if (selectedNumbers.includes(id)) {
      setSelectedNumbers(selectedNumbers.filter(num => num !== id));
    } else if (selectedNumbers.length < 1) {
      setSelectedNumbers([...selectedNumbers, id]);
    }
  };

  const handleColorClick = (id) => {
    if (selectedNumbers.length === 0) return;

    const selectedNumber = selectedNumbers[0];
    const selectedItem = shuffledItems.find(item => item.id === selectedNumber);
    const selectedColorItem = shuffledColors.find(item => item.id === id);

    if (selectedItem && selectedColorItem) {
      const isCorrect = selectedItem.id === selectedColorItem.id;

      setSelectedNumbers([]);
      setSelectedColors([]);

      const newFeedback = { ...feedback };
      if (isCorrect) {
        newFeedback[`${selectedNumber}-${id}`] = true;

        // ·Éï·Éê·Éõ·Éù·É¨·Éõ·Éî·Éë·Éó ·Éê·É†·Éò·É° ·Éó·É£ ·Éê·É†·Éê ·Éß·Éï·Éî·Éö·Éê ·É¨·Éß·Éï·Éò·Éö·Éò ·É°·É¨·Éù·É†·Éê·Éì ·Éì·Éê·Éô·Éê·Éï·É®·Éò·É†·Éî·Éë·É£·Éö·Éò
        const allPairs = shuffledItems.every(item => 
          Object.keys(newFeedback).some(key => 
            key.startsWith(item.id.toString()) && newFeedback[key]
          )
        );

        if (allPairs) {
          const randomPraise = currentTask.praise[Math.floor(Math.random() * currentTask.praise.length)];
          setTimeout(() => {
            alert(`${playerName}, ${randomPraise} üåü`);
          }, 500);
        }
      } else {
        newFeedback[`${selectedNumber}-${id}`] = false;
      }
      setFeedback(newFeedback);
    }
  };

  const handleAnswerSubmit = (taskId) => {
    const currentTask = tasks[currentTaskIndex];
    const userAnswer = parseInt(answers[taskId]);
    const isCorrect = userAnswer === currentTask.correctAnswer;

    setFeedback(prev => ({
      ...prev,
      [taskId]: isCorrect
    }));

    if (isCorrect) {
      const randomPraise = currentTask.praise[Math.floor(Math.random() * currentTask.praise.length)];
      setTimeout(() => {
        alert(`${playerName}, ${randomPraise} üåü`);
      }, 500);
    }
  };

  const handleNextTask = () => {
    setIsVisible(false);
    setTimeout(() => {
      setCurrentTaskIndex((prev) => (prev + 1) % tasks.length);
      setSelectedNumbers([]);
      setSelectedColors([]);
      setAnswers({});
      setFeedback({});
      setIsVisible(true);
    }, 300);
  };

  const handlePreviousTask = () => {
    setIsVisible(false);
    setTimeout(() => {
      setCurrentTaskIndex((prev) => (prev - 1 + tasks.length) % tasks.length);
      setSelectedNumbers([]);
      setSelectedColors([]);
      setAnswers({});
      setFeedback({});
      setIsVisible(true);
    }, 300);
  };

  const currentTask = tasks[currentTaskIndex];

  const renderMatchingGame = (task) => (
    <MatchingGame>
      <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '20px' }}>
        <div>
          {shuffledItems.map((item) => (
            <MatchingItem
              key={item.id}
              onClick={() => handleNumberClick(item.id)}
              isSelected={selectedNumbers.includes(item.id)}
              disabled={Object.keys(feedback).some(key => 
                key.startsWith(item.id.toString()) && feedback[key]
              )}
              correct={Object.keys(feedback).some(key => 
                key.startsWith(item.id.toString()) && feedback[key]
              )}
              color={item.color}
              correctColor={Object.keys(feedback).some(key => 
                key.startsWith(item.id.toString()) && feedback[key]
              ) ? item.color : undefined}
            >
              {item.text}
            </MatchingItem>
          ))}
        </div>
        <div>
          {shuffledColors.map((item) => (
            <MatchingItem
              key={item.id}
              onClick={() => handleColorClick(item.id)}
              isSelected={selectedColors.includes(item.id)}
              disabled={Object.keys(feedback).some(key => 
                key.endsWith(`-${item.id}`) && feedback[key]
              )}
              correct={Object.keys(feedback).some(key => 
                key.endsWith(`-${item.id}`) && feedback[key]
              )}
              color={item.color}
              correctColor={Object.keys(feedback).some(key => 
                key.endsWith(`-${item.id}`) && feedback[key]
              ) ? item.color : undefined}
            >
              {item.colorName}
            </MatchingItem>
          ))}
        </div>
      </div>
    </MatchingGame>
  );

  const renderMathGame = (task) => (
    <MathGame>
      <TaskDescription>{task.question}</TaskDescription>
      <StickersContainer>
        <StickersGroup>
          {task.stickers.map((sticker, index) => (
            <Sticker key={index}>{sticker}</Sticker>
          ))}
        </StickersGroup>
        <StickersGroup>
          {task.stickers2.map((sticker, index) => (
            <Sticker key={index}>{sticker}</Sticker>
          ))}
        </StickersGroup>
      </StickersContainer>
      <div>
        <Input
          type="number"
          value={answers[task.id] || ''}
          onChange={(e) => setAnswers(prev => ({
            ...prev,
            [task.id]: e.target.value
          }))}
          placeholder="·Éû·Éê·É°·É£·ÉÆ·Éò"
        />
        <Button onClick={() => handleAnswerSubmit(task.id)}>·É®·Éî·Éõ·Éù·É¨·Éõ·Éî·Éë·Éê</Button>
      </div>
      {feedback[task.id] !== undefined && (
        <Message isCorrect={feedback[task.id]}>
          {feedback[task.id] ? '‚úì' : '‚ùå'}
          {!feedback[task.id] && <div style={{fontSize: '1rem', marginTop: '10px'}}>{task.hint}</div>}
        </Message>
      )}
    </MathGame>
  );

  if (showNameModal) {
    return <NameModal onSubmit={updatePlayerName} />;
  }

  return (
    <TasksContainer>
      <GlobalStyle />
      <HomeButton onClick={() => window.location.href = '/'}>üè†</HomeButton>
      <TaskCard isVisible={isVisible}>
        <TaskTitle>·Éí·Éê·Éõ·Éê·É†·ÉØ·Éù·Éë·Éê, {playerName}!</TaskTitle>
        <TaskDescription>{currentTask.description}</TaskDescription>
        {currentTask.type === 'matching' ? renderMatchingGame(currentTask) : renderMathGame(currentTask)}
      </TaskCard>
      <NavigationButtons>
        <Button onClick={handlePreviousTask}>‚Üê ·É¨·Éò·Éú·Éê</Button>
        <Button onClick={handleNextTask}>·É®·Éî·Éõ·Éì·Éî·Éí·Éò ‚Üí</Button>
      </NavigationButtons>
    </TasksContainer>
  );
};

export default Tasks;
