import React, { createContext, useState, useContext, useEffect } from 'react';

const PlayerContext = createContext();

export const PlayerProvider = ({ children }) => {
  const [playerName, setPlayerName] = useState(localStorage.getItem('playerName') || '');
  const [showNameModal, setShowNameModal] = useState(!playerName);
  const [gameProgress, setGameProgress] = useState(() => {
    const savedProgress = localStorage.getItem('gameProgress');
    return savedProgress ? JSON.parse(savedProgress) : {
      puzzles: [],
      completedTasks: [],
      taskScores: {},
      completedScrambleTasks: [],
      scrambleScores: {},
      lastPlayed: null
    };
  });

  const updatePlayerName = (name) => {
    setPlayerName(name);
    localStorage.setItem('playerName', name);
    setShowNameModal(false);
  };

  const updateGameProgress = (gameType, taskIndex, score) => {
    setGameProgress(prev => {
      let newProgress = { ...prev };

      if (gameType === 'tasks') {
        // Initialize arrays if they don't exist
        if (!newProgress.completedTasks) newProgress.completedTasks = [];
        if (!newProgress.taskScores) newProgress.taskScores = {};

        // Add task to completed tasks if not already there
        if (!newProgress.completedTasks.includes(taskIndex)) {
          newProgress.completedTasks = [...newProgress.completedTasks, taskIndex];
        }

        // Update task score
        newProgress.taskScores = {
          ...newProgress.taskScores,
          [taskIndex]: score
        };
      } else if (gameType === 'scramble') {
        // Initialize arrays if they don't exist
        if (!newProgress.completedScrambleTasks) newProgress.completedScrambleTasks = [];
        if (!newProgress.scrambleScores) newProgress.scrambleScores = {};

        // Add task to completed scramble tasks if not already there
        if (!newProgress.completedScrambleTasks.includes(taskIndex)) {
          newProgress.completedScrambleTasks = [...newProgress.completedScrambleTasks, taskIndex];
        }

        // Update scramble score
        newProgress.scrambleScores = {
          ...newProgress.scrambleScores,
          [taskIndex]: score
        };
      } else if (gameType === -1) {
        // Reset progress
        newProgress = {
          puzzles: [],
          completedTasks: [],
          taskScores: {},
          completedScrambleTasks: [],
          scrambleScores: {},
          lastPlayed: null
        };
      } else {
        // Handle puzzles
        if (!newProgress[gameType]) newProgress[gameType] = [];
        newProgress[gameType] = [...newProgress[gameType], taskIndex];
        newProgress.lastPlayed = {
          type: gameType,
          date: new Date().toISOString()
        };
      }

      localStorage.setItem('gameProgress', JSON.stringify(newProgress));
      return newProgress;
    });
  };

  const value = {
    playerName,
    showNameModal,
    gameProgress,
    updatePlayerName,
    updateGameProgress
  };

  return (
    <PlayerContext.Provider value={value}>
      {children}
    </PlayerContext.Provider>
  );
};

export const usePlayer = () => {
  const context = useContext(PlayerContext);
  if (!context) {
    throw new Error('usePlayer must be used within a PlayerProvider');
  }
  return context;
};
