import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';

const Board = styled.div`
  display: grid;
  grid-template-columns: repeat(${props => props.size}, 1fr);
  gap: 2px;
  background-color: #fff;
  padding: 10px;
  border-radius: 10px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.2);
  margin: 20px;
`;

const PuzzlePiece = styled.div`
  width: ${props => 400 / props.size}px;
  height: ${props => 400 / props.size}px;
  background-image: url(${props => props.image});
  background-size: ${props => props.size * 100}% ${props => props.size * 100}%;
  background-position: ${props => props.bgPosition};
  border: 1px solid #ddd;
  cursor: ${props => props.isCorrect ? 'default' : 'grab'};
  transition: transform 0.2s;

  &:hover {
    transform: ${props => props.isCorrect ? 'none' : 'scale(1.05)'};
  }
`;

const PuzzleBoard = ({ image, difficulty, onProgress, onComplete }) => {
  const [pieces, setPieces] = useState([]);
  const [correctPositions, setCorrectPositions] = useState(new Set());

  useEffect(() => {
    const totalPieces = difficulty * difficulty;
    const initialPieces = Array.from({ length: totalPieces }, (_, index) => ({
      id: String(index),
      bgPosition: `${((index % difficulty) * 100) / (difficulty - 1)}% ${(Math.floor(index / difficulty) * 100) / (difficulty - 1)}%`,
      correctIndex: index
    })).sort(() => Math.random() - 0.5);

    setPieces(initialPieces);
    setCorrectPositions(new Set());
  }, [difficulty, image]);

  useEffect(() => {
    const progress = (correctPositions.size / (difficulty * difficulty)) * 100;
    onProgress(Math.round(progress));

    if (correctPositions.size === difficulty * difficulty) {
      onComplete();
    }
  }, [correctPositions, difficulty, onComplete, onProgress]);

  const handleDragEnd = (result) => {
    if (!result.destination) return;

    const newPieces = Array.from(pieces);
    const [removed] = newPieces.splice(result.source.index, 1);
    newPieces.splice(result.destination.index, 0, removed);

    const newCorrectPositions = new Set();
    newPieces.forEach((piece, index) => {
      if (parseInt(piece.id) === index) {
        newCorrectPositions.add(index);
      }
    });

    setPieces(newPieces);
    setCorrectPositions(newCorrectPositions);
  };

  return (
    <DragDropContext onDragEnd={handleDragEnd}>
      <Droppable droppableId="puzzle-board" direction="horizontal">
        {(provided) => (
          <Board
            ref={provided.innerRef}
            {...provided.droppableProps}
            size={difficulty}
          >
            {pieces.map((piece, index) => (
              <Draggable
                key={piece.id}
                draggableId={piece.id}
                index={index}
                isDragDisabled={correctPositions.has(index)}
              >
                {(provided) => (
                  <PuzzlePiece
                    ref={provided.innerRef}
                    {...provided.draggableProps}
                    {...provided.dragHandleProps}
                    image={image}
                    size={difficulty}
                    bgPosition={piece.bgPosition}
                    isCorrect={correctPositions.has(index)}
                  />
                )}
              </Draggable>
            ))}
            {provided.placeholder}
          </Board>
        )}
      </Droppable>
    </DragDropContext>
  );
};

export default PuzzleBoard;
