const Tasks = () => {
  const { playerName, showNameModal, updatePlayerName } = usePlayer();
  const [currentTaskIndex, setCurrentTaskIndex] = useState(0);
  const [selectedNumbers, setSelectedNumbers] = useState([]);
  const [selectedColors, setSelectedColors] = useState([]);
  const [feedback, setFeedback] = useState({});
  const [answers, setAnswers] = useState({});
  const [isVisible, setIsVisible] = useState(true);
  const [shuffledItems, setShuffledItems] = useState([]);
  const [shuffledColors, setShuffledColors] = useState([]);

  // Memoize current task to prevent unnecessary re-renders
  const currentTask = useMemo(() => tasks[currentTaskIndex], [currentTaskIndex]);

  // Memoize navigation handlers
  const handleNextTask = useCallback(() => {
    setIsVisible(false);
    setTimeout(() => {
      setCurrentTaskIndex((prev) => (prev + 1) % tasks.length);
      setSelectedNumbers([]);
      setSelectedColors([]);
      setAnswers({});
      setFeedback({});
      setIsVisible(true);
    }, 300);
  }, []);

  const handlePreviousTask = useCallback(() => {
    setIsVisible(false);
    setTimeout(() => {
      setCurrentTaskIndex((prev) => (prev - 1 + tasks.length) % tasks.length);
      setSelectedNumbers([]);
      setSelectedColors([]);
      setAnswers({});
      setFeedback({});
      setIsVisible(true);
    }, 300);
  }, []);

  // Optimize handlers with useCallback
  const handleNumberClick = useCallback((index) => {
    setSelectedNumbers(prev => {
      const newSelection = prev.includes(index) 
        ? prev.filter(i => i !== index)
        : [index];
      return newSelection;
    });
    setSelectedColors([]);
  }, []);

  const handleColorClick = useCallback((index) => {
    setSelectedColors(prev => {
      if (prev.includes(index)) {
        return prev.filter(i => i !== index);
      }

      if (selectedNumbers.length === 0) return prev;

      const numberIndex = selectedNumbers[0];
      if (currentTask.items[numberIndex].color === currentTask.items[index].color) {
        // Correct match
        const praise = currentTask.praise[Math.floor(Math.random() * currentTask.praise.length)];
        const allMatched = currentTask.items.every((item, idx) => 
          idx === numberIndex || idx === index || item.matched
        );

        if (allMatched) {
          showCelebration(currentTask.stickers);
          setTimeout(handleNextTask, 1500);
        } else {
          alert(praise);
        }

        currentTask.items[numberIndex].matched = true;
        currentTask.items[index].matched = true;
      }

      setSelectedNumbers([]);
      return [];
    });
  }, [currentTask, selectedNumbers, handleNextTask]);

  const handleAnswerSubmit = useCallback((answer) => {
    if (answer === currentTask.answer) {
      const praise = currentTask.praise[Math.floor(Math.random() * currentTask.praise.length)];
      alert(praise);
      showCelebration(currentTask.stickers);
      setTimeout(handleNextTask, 1500);
    } else {
      alert(currentTask.hint);
    }
  }, [currentTask, handleNextTask]);

  // Memoize game renderers
  const renderMatchingGame = useCallback((task) => (
    <MatchingGame>
      <ItemsContainer>
        {task.items.map((item, index) => (
          <Item
            key={index}
            color={item.color}
            selected={selectedNumbers.includes(index)}
            onClick={() => handleNumberClick(index)}
            disabled={item.matched}
          >
            {item.number}
          </Item>
        ))}
      </ItemsContainer>
      <ItemsContainer>
        {task.items.map((item, index) => (
          <Item
            key={index}
            color={item.color}
            selected={selectedColors.includes(index)}
            onClick={() => handleColorClick(index)}
            disabled={item.matched}
          >
            {item.color}
          </Item>
        ))}
      </ItemsContainer>
    </MatchingGame>
  ), [selectedNumbers, selectedColors, handleNumberClick, handleColorClick]);

  const renderMathGame = useCallback((task) => (
    <MathGame>
      <MathProblem>
        {task.problem}
      </MathProblem>
      <NumberGrid>
        {Array.from({ length: 9 }, (_, i) => (
          <Item
            key={i}
            onClick={() => handleAnswerSubmit(i + 1)}
            color="#4CAF50"
          >
            {i + 1}
          </Item>
        ))}
      </NumberGrid>
    </MathGame>
  ), [handleAnswerSubmit]);

  // Memoize celebration function
  const showCelebration = useCallback((stickers) => {
    const container = document.createElement('div');
    container.style.position = 'fixed';
    container.style.top = '0';
    container.style.left = '0';
    container.style.width = '100%';
    container.style.height = '100%';
    container.style.pointerEvents = 'none';
    container.style.zIndex = '1000';
    document.body.appendChild(container);

    stickers.forEach((sticker, index) => {
      const element = document.createElement('div');
      element.textContent = sticker;
      element.style.position = 'absolute';
      element.style.fontSize = '3rem';
      element.style.left = `${Math.random() * 100}%`;
      element.style.top = '100%';
      element.style.animation = `float ${2 + Math.random()}s ease-out forwards`;
      element.style.animationDelay = `${index * 0.1}s`;
      container.appendChild(element);
    });

    setTimeout(() => {
      document.body.removeChild(container);
    }, 3000);
  }, []);

  if (showNameModal) {
    return <NameModal onSubmit={updatePlayerName} />;
  }

  return (
    <Container>
      <GlobalStyle />
      <HomeButton onClick={() => window.location.href = '/'}>🏠</HomeButton>
      <TaskCard isVisible={isVisible}>
        <Title>გამარჯობა, {playerName}!</Title>
        <Subtitle>{currentTask.description}</Subtitle>
        {currentTask.type === 'matching' ? renderMatchingGame(currentTask) : renderMathGame(currentTask)}
      </TaskCard>
      <ButtonsContainer>
        <NavigationButton prev onClick={handlePreviousTask}>← წინა</NavigationButton>
        <NavigationButton onClick={handleNextTask}>შემდეგი →</NavigationButton>
      </ButtonsContainer>
    </Container>
  );
};

export default React.memo(Tasks);
