import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { tasks } from '../data/tasks';
import { createGlobalStyle } from 'styled-components';
import NameModal from './NameModal';
import { usePlayer } from '../context/PlayerContext';

const TasksContainer = styled.div`
  width: 100%;
  height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 20px;
  position: relative;
  overflow: hidden;
`;

const TaskCard = styled.div`
  background: white;
  border-radius: 20px;
  padding: 20px;
  box-shadow: 0 8px 16px rgba(0, 0, 0, 0.1);
  width: 100%;
  max-width: 800px;
  height: calc(100vh - 140px);
  display: flex;
  flex-direction: column;
  transition: all 0.3s ease;
  opacity: ${props => props.isVisible ? 1 : 0};
  transform: ${props => props.isVisible ? 'translateY(0)' : 'translateY(20px)'};
  overflow: hidden;
`;

const TaskTitle = styled.h2`
  color: #2196F3;
  margin-bottom: 10px;
  text-align: center;
  font-size: 1.8rem;
`;

const TaskDescription = styled.p`
  color: #666;
  margin-bottom: 15px;
  text-align: center;
  font-size: 1.2rem;
`;

const MatchingGame = styled.div`
  flex: 1;
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 10px;
  overflow: hidden;
`;

const MathGame = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  gap: 20px;
  padding: 10px;
`;

const StickersContainer = styled.div`
  display: flex;
  justify-content: center;
  gap: 40px;
  margin: 20px 0;
`;

const StickersGroup = styled.div`
  display: flex;
  gap: 10px;
  align-items: center;
`;

const MatchingItem = styled.div`
  padding: 15px;
  margin: 8px;
  border-radius: 15px;
  cursor: ${props => props.disabled ? 'default' : 'pointer'};
  background-color: ${props => props.correct ? props.correctColor : props.isSelected ? '#e3f2fd' : 'white'};
  border: 3px solid ${props => props.color};
  font-size: 1.8rem;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: all 0.3s ease;
  opacity: ${props => props.disabled ? 0.7 : 1};
  min-width: 100px;
  min-height: 60px;

  &:hover {
    transform: ${props => props.disabled ? 'none' : 'scale(1.05)'};
    box-shadow: ${props => props.disabled ? 'none' : '0 4px 8px rgba(0,0,0,0.1)'};
  }
`;

const NavigationButtons = styled.div`
  display: flex;
  gap: 20px;
  margin-top: 20px;
  position: absolute;
  bottom: 20px;
  left: 50%;
  transform: translateX(-50%);
`;

const HomeButton = styled.button`
  position: absolute;
  top: 20px;
  left: 20px;
  font-size: 2rem;
  background: none;
  border: none;
  cursor: pointer;
  transition: transform 0.3s ease;
  z-index: 100;

  &:hover {
    transform: scale(1.1);
  }
`;

const Input = styled.input`
  padding: 12px;
  font-size: 1.5rem;
  border: 3px solid #2196F3;
