import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import PuzzleBoard from './PuzzleBoard';
import DifficultySelector from './DifficultySelector';
import NameModal from './NameModal';
import { usePlayer } from '../context/PlayerContext';

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 20px;
  max-width: 1200px;
  margin: 0 auto;
  gap: 20px;
`;

const Title = styled.h1`
  color: #2196F3;
  margin-bottom: 20px;
  text-align: center;
  font-size: 2rem;
`;

const GameContainer = styled.div`
  display: flex;
  gap: 40px;
  align-items: flex-start;
  justify-content: center;
  flex-wrap: wrap;
  width: 100%;
`;

const SidePanel = styled.div`
  display: flex;
  flex-direction: column;
  gap: 20px;
  min-width: 300px;
`;

const MainPanel = styled.div`
  flex: 1;
  min-width: 400px;
  max-width: 800px;
`;

const PuzzleGame = () => {
  const navigate = useNavigate();
  const { playerName, showNameModal, updatePlayerName } = usePlayer();
  const [difficulty, setDifficulty] = useState(3);
  const [progress, setProgress] = useState(0);
  const [image, setImage] = useState('https://picsum.photos/400/400');

  const handleComplete = () => {
    alert(`·Éí·Éò·Éö·Éù·É™·Éê·Éï·Éó ${playerName}! ·Éó·É•·Éï·Éî·Éú ·É¨·Éê·É†·Éõ·Éê·É¢·Éî·Éë·Éò·Éó ·Éì·Éê·Éê·É°·É†·É£·Éö·Éî·Éó ·Éû·Éê·Éñ·Éö·Éò! üéâ`);
  };

  const handleBackToMenu = () => {
    navigate('/');
  };

  if (showNameModal) {
    return <NameModal onSubmit={updatePlayerName} />;
  }

  return (
    <Container>
      <Title>·Éí·Éê·Éõ·Éê·É†·ÉØ·Éù·Éë·Éê, {playerName}! üëã</Title>
      <GameContainer>
        <SidePanel>
          <DifficultySelector difficulty={difficulty} setDifficulty={setDifficulty} />
        </SidePanel>
        <MainPanel>
          <PuzzleBoard
            image={image}
            difficulty={difficulty}
            onProgress={setProgress}
            onComplete={handleComplete}
            onBackToMenu={handleBackToMenu}
          />
        </MainPanel>
      </GameContainer>
    </Container>
  );
};

export default PuzzleGame;
