import React, { useState, useEffect, useMemo, useCallback } from 'react';
import styled from 'styled-components';
import { tasks } from '../data/tasks';
import { createGlobalStyle } from 'styled-components';
import NameModal from './NameModal';
import { usePlayer } from '../context/PlayerContext';
import { useNavigate } from 'react-router-dom';

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  min-height: 100vh;
  padding: 1rem;
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: linear-gradient(to bottom, #EBF3FE, #F3EFFE);
  overflow-y: auto;
`;

const TaskCard = styled.div`
  background: rgba(255, 255, 255, 0.8);
  backdrop-filter: blur(10px);
  border-radius: 1rem;
  padding: 2rem;
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1);
  width: 100%;
  max-width: 500px;
  text-align: center;
`;

const Title = styled.h1`
  font-size: 1.5rem;
  color: #1a237e;
  margin-bottom: 1rem;
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 0.5rem;
  text-shadow: 1px 1px 0 rgba(255, 255, 255, 0.5);

  @media (min-width: 640px) {
    font-size: 1.875rem;
  }
`;

const ProgressText = styled.p`
  color: #666;
  margin-bottom: 2rem;
  font-size: 1rem;
`;

const TaskContent = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 2rem;
  margin: 2rem 0;
`;

const TaskDescription = styled.p`
  font-size: 1rem;
  color: #666;
  margin-bottom: 2rem;
  text-align: center;
`;

const ButtonsContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 1rem;
  justify-content: center;
  margin-top: 2rem;
`;

const Button = styled.button`
  background: #4CAF50;
  color: white;
  padding: 0.75rem 1.5rem;
  border: none;
  border-radius: 0.75rem;
  cursor: pointer;
  font-size: 1rem;
  transition: all 0.2s;
  display: flex;
  align-items: center;
  gap: 0.5rem;
  font-weight: bold;
  text-shadow: 1px 1px 0 rgba(0, 0, 0, 0.2);

  &:hover {
    background: #388E3C;
    transform: translateY(-2px);
  }

  &:active {
    transform: translateY(0);
  }
`;

const Toast = styled.div`
  position: fixed;
  top: 20px;
  left: 50%;
  transform: translateX(-50%);
  background: rgba(255, 255, 255, 0.95);
  padding: 1rem 2rem;
  border-radius: 1rem;
  box-shadow: 0 6px 16px rgba(0, 0, 0, 0.15);
  z-index: 1000;
  animation: slideIn 0.3s ease-out forwards;
  font-size: 1.4rem;
  color: #2196F3;
  min-width: 300px;
  text-align: center;

  @keyframes slideIn {
    from {
      transform: translate(-50%, -100%);
      opacity: 0;
    }
    to {
      transform: translate(-50%, 0);
      opacity: 1;
    }
  }
`;

const GlobalStyle = createGlobalStyle`
  @keyframes celebrate {
    0% {
      transform: translate(-50%, -50%) scale(0);
      opacity: 0;
    }
    50% {
      transform: translate(-50%, -50%) scale(1.5);
      opacity: 1;
    }
    100% {
      transform: translate(-50%, -50%) scale(1);
      opacity: 0;
    }
  }

  @keyframes float {
    0% {
      transform: translateY(0) rotate(0deg);
      opacity: 1;
    }
    100% {
      transform: translateY(-100vh) rotate(360deg);
      opacity: 0;
    }
  }
`;

const celebrationStickers = ['üåü', 'üéâ', 'üéà', 'üéä', 'üèÜ', '‚≠ê', 'üí´', '‚ú®'];

const showCelebration = (stickers) => {
  const container = document.createElement('div');
  container.style.position = 'fixed';
  container.style.top = '0';
  container.style.left = '0';
  container.style.width = '100%';
  container.style.height = '100%';
  container.style.pointerEvents = 'none';
  container.style.zIndex = '1000';
  document.body.appendChild(container);

  // ·É™·Éî·Éú·É¢·É†·Éê·Éö·É£·É†·Éò ·É°·É¢·Éò·Éô·Éî·É†·Éò
  const mainSticker = document.createElement('div');
  mainSticker.style.position = 'fixed';
  mainSticker.style.fontSize = '100px';
  mainSticker.style.zIndex = '1000';
  mainSticker.style.top = '50%';
  mainSticker.style.left = '50%';
  mainSticker.style.transform = 'translate(-50%, -50%)';
  mainSticker.style.animation = 'celebrate 1s ease-out forwards';
  mainSticker.textContent = stickers[Math.floor(Math.random() * stickers.length)];
  container.appendChild(mainSticker);

  // ·Éõ·É§·É†·Éò·Éú·Éê·Éï·Éò ·É°·É¢·Éò·Éô·Éî·É†·Éî·Éë·Éò
  for (let i = 0; i < 15; i++) {
    const sticker = document.createElement('div');
    sticker.style.position = 'fixed';
    sticker.style.fontSize = '40px';
    sticker.style.zIndex = '999';
    sticker.style.left = Math.random() * 100 + 'vw';
    sticker.style.top = '100vh';
    sticker.style.animation = `float ${2 + Math.random() * 2}s linear forwards`;
    sticker.textContent = stickers[Math.floor(Math.random() * stickers.length)];
    container.appendChild(sticker);
  }

  setTimeout(() => {
    document.body.removeChild(container);
  }, 3000);
};

const Tasks = () => {
  const { player, updatePlayer } = usePlayer();
  const [currentTaskIndex, setCurrentTaskIndex] = useState(0);
  const [progress, setProgress] = useState(0);
  const [selectedItems, setSelectedItems] = useState([]);
  const [matchedPairs, setMatchedPairs] = useState([]);
  const [answer, setAnswer] = useState('');
  const [showSuccess, setShowSuccess] = useState(false);
  const [stickers, setStickers] = useState([]);
  const [selectedNumbers, setSelectedNumbers] = useState([]);
  const [selectedColors, setSelectedColors] = useState([]);
  const [feedback, setFeedback] = useState({ show: false });
  const [answers, setAnswers] = useState({});
  const [isVisible, setIsVisible] = useState(true);
  const [shuffledItems, setShuffledItems] = useState([]);
  const [shuffledColors, setShuffledColors] = useState([]);
  const [toast, setToast] = useState({ show: false, message: '' });
  const [completedTasksCount, setCompletedTasksCount] = useState(0);
  const navigate = useNavigate();

  const currentTask = tasks[currentTaskIndex];
  const totalTasks = tasks.length;
  const completedTasks = player?.gameProgress?.completedTasks || [];

  useEffect(() => {
    if (player?.gameProgress?.completedTasks) {
      setCompletedTasksCount(player.gameProgress.completedTasks.length);
    }
  }, [player]);

  const handleSuccess = useCallback(() => {
    const timestamp = Date.now();

    // Update player progress
    if (player && updatePlayer) {
      const updatedPlayer = {
        ...player,
        gameProgress: {
          ...player.gameProgress,
          completedTasks: [...(player.gameProgress?.completedTasks || []), currentTaskIndex],
          taskScores: {
            ...(player.gameProgress?.taskScores || {}),
            [currentTaskIndex]: 100
          },
          lastPlayedDate: new Date().toISOString()
        }
      };

      updatePlayer(updatedPlayer);

      // Update local state
      setCompletedTasksCount(prev => prev + 1);
      setProgress(prev => Math.min(prev + 10, 100));

      // Show success message
      setToast({
        show: true,
        message: '·Éß·Éù·É©·Éê·É¶! ·Éì·Éê·Éï·Éê·Éö·Éî·Éë·Éê ·É®·Éî·É°·É†·É£·Éö·Éî·Éë·É£·Éö·Éò·Éê! üéâ'
      });

      // Clear success message and move to next task
      setTimeout(() => {
        setToast({ show: false, message: '' });
        if (currentTaskIndex < tasks.length - 1) {
          setCurrentTaskIndex(prev => prev + 1);
        }
      }, 2000);
    }
  }, [currentTaskIndex, player, updatePlayer]);

  const handlePrevTask = useCallback(() => {
    if (currentTaskIndex > 0) {
      setCurrentTaskIndex(currentTaskIndex - 1);
    }
  }, [currentTaskIndex]);

  const handleNextTask = useCallback(() => {
    setIsVisible(false);
    setTimeout(() => {
      const nextIndex = currentTaskIndex + 1;
      setCurrentTaskIndex(nextIndex);
      setIsVisible(true);
    }, 300);
  }, [currentTaskIndex]);

  const handleRestart = useCallback(() => {
    const shouldRestart = window.confirm('·Éú·Éê·Éõ·Éì·Éï·Éò·Éö·Éê·Éì ·Éí·É°·É£·É†·Éó ·Éó·Éê·Éï·Éò·Éì·Éê·Éú ·Éì·Éê·É¨·Éß·Éî·Éë·Éê? ·Éû·É†·Éù·Éí·É†·Éî·É°·Éò ·Éí·Éê·Éú·É£·Éö·Éì·Éî·Éë·Éê.');
    if (shouldRestart) {
      updatePlayer({
        ...player,
        gameProgress: {
          completedTasks: [],
          taskScores: {},
          lastPlayedDate: null
        }
      });
      setCurrentTaskIndex(0);
      setSelectedNumbers([]);
      setSelectedColors([]);
      setAnswers({});
      setFeedback({});
      window.location.reload(); // ·Éí·Éï·Éî·É†·Éì·Éò·É° ·Éí·Éê·Éì·Éê·É¢·Éï·Éò·É†·Éó·Éï·Éê ·Éû·É†·Éù·Éí·É†·Éî·É°·Éò·É° ·Éí·Éê·Éú·É£·Éö·Éî·Éë·Éò·É° ·É®·Éî·Éõ·Éì·Éî·Éí
    }
  }, [updatePlayer, player]);

  useEffect(() => {
    if (currentTask?.type === 'matching') {
      // ·É®·Éî·Éï·É•·Éõ·Éú·Éê·Éó ·Éê·ÉÆ·Éê·Éö·Éò ·Éõ·Éê·É°·Éò·Éï·Éî·Éë·Éò ·É®·Éî·Éõ·Éó·ÉÆ·Éï·Éî·Éï·Éò·Éó·Éò ·Éí·Éê·Éú·Éö·Éê·Éí·Éî·Éë·Éò·É°·Éó·Éï·Éò·É°
      const shuffleArray = (array) => {
        const newArray = [...array];
        for (let i = newArray.length - 1; i > 0; i--) {
          const j = Math.floor(Math.random() * (i + 1));
          [newArray[i], newArray[j]] = [newArray[j], newArray[i]];
        }
        return newArray;
      };

      // ·É®·Éî·Éï·É£·É†·Éò·Éù·Éó ·É§·Éî·É†·Éî·Éë·Éò ·Éì·Éê ·Éï·Éê·É†·Éò·Éê·Éú·É¢·Éî·Éë·Éò
      if (currentTask.colorOptions) {
        setShuffledColors(shuffleArray(currentTask.colorOptions));
      }
      if (currentTask.items) {
        setShuffledItems(shuffleArray(currentTask.items));
      }
    }
  }, [currentTask]);

  return (
    <Container>
      {player?.playerName ? (
        <>
          <TaskCard>
            <Title>
              <span>üìö</span> ·É°·Éê·É°·É¨·Éê·Éï·Éö·Éù ·Éì·Éê·Éï·Éê·Éö·Éî·Éë·Éî·Éë·Éò
            </Title>
            <ProgressText>·É®·Éî·É°·É†·É£·Éö·Éî·Éë·É£·Éö·Éò·Éê: {completedTasksCount} / {totalTasks}</ProgressText>

            {currentTask && (
              <TaskContent>
                <TaskDescription>{currentTask.description}</TaskDescription>
                {/* Task content rendering */}
              </TaskContent>
            )}

            <ButtonsContainer>
              <Button 
                onClick={() => handlePrevTask()}
                disabled={currentTaskIndex === 0}
              >
                ·É¨·Éò·Éú·Éê
              </Button>
              <Button 
                onClick={() => handleNextTask()}
                disabled={currentTaskIndex === tasks.length - 1}
              >
                ·É®·Éî·Éõ·Éì·Éî·Éí·Éò
              </Button>
            </ButtonsContainer>
          </TaskCard>

          {toast.show && (
            <Toast>
              {toast.message}
            </Toast>
          )}
        </>
      ) : (
        <NameModal />
      )}
    </Container>
  );
};

export default React.memo(Tasks);
