import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import styled, { keyframes } from 'styled-components';
import PuzzleBoard from './PuzzleBoard';
import NameModal from './NameModal';
import { usePlayer } from '../context/PlayerContext';

const Container = styled.div`
  min-height: 100vh;
  min-width: 100vw;
  background: linear-gradient(to bottom, #EBF3FE, #F3EFFE);
  padding: 0.5rem;
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  overflow-y: auto;

  @media (min-width: 640px) {
    padding: 1rem;
  }
`;

const Header = styled.header`
  display: flex;
  align-items: center;
  justify-content: space-between;
  margin-bottom: 1rem;
  max-width: 64rem;
  margin-left: auto;
  margin-right: auto;
  padding: 0 1rem;
  gap: 1rem;
`;

const HeaderButtons = styled.div`
  display: flex;
  gap: 1rem;
  align-items: center;
`;

const Title = styled.h1`
  font-size: 1.5rem;
  font-weight: bold;
  color: #2196F3;
  display: flex;
  align-items: center;
  gap: 0.5rem;

  @media (min-width: 640px) {
    font-size: 1.875rem;
  }
`;

const GameIcon = styled.span`
  font-size: 1.5rem;

  @media (min-width: 640px) {
    font-size: 2rem;
  }
`;

const Main = styled.main`
  max-width: 64rem;
  margin: 0 auto;
  display: flex;
  flex-direction: column;
  gap: 1rem;
  padding: 0 1rem;
  padding-bottom: 2rem;
`;

const Card = styled.div`
  background: rgba(255, 255, 255, 0.8);
  backdrop-filter: blur(10px);
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1);
  border-radius: 1rem;
  padding: 1rem;
`;

const DifficultyCard = styled(Card)`
  text-align: center;
`;

const DifficultyTitle = styled.h2`
  font-size: 1.125rem;
  font-weight: 600;
  margin-bottom: 0.5rem;
`;

const DifficultyButtons = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  gap: 0.5rem;
`;

const DifficultyButton = styled.button`
  height: 2.5rem;
  width: 4rem;
  font-size: 1rem;
  border-radius: 0.75rem;
  transition: transform 0.2s;
  background: ${props => props.isSelected ? '#2196F3' : 'transparent'};
  color: ${props => props.isSelected ? 'white' : '#2196F3'};
  border: 2px solid ${props => props.isSelected ? 'transparent' : '#2196F3'};
  cursor: ${props => props.disabled ? 'not-allowed' : 'pointer'};
  opacity: ${props => props.disabled ? 0.7 : 1};

  &:hover {
    transform: ${props => props.disabled ? 'none' : 'scale(1.05)'};
  }

  &:disabled {
    background: ${props => props.isSelected ? '#90CAF9' : 'rgba(33, 150, 243, 0.1)'};
    border-color: ${props => props.isSelected ? 'transparent' : '#90CAF9'};
    color: ${props => props.isSelected ? 'white' : '#90CAF9'};
  }
`;

const UploadButton = styled.label`
  display: inline-flex;
  align-items: center;
  gap: 0.5rem;
  background: #2196F3;
  color: white;
  padding: 0.75rem 1.5rem;
  border-radius: 0.75rem;
  cursor: pointer;
  transition: all 0.2s;
  font-size: 1rem;
  margin-left: auto;

  &:hover {
    background: #1976D2;
    transform: translateY(-2px);
  }

  &:active {
    transform: translateY(0);
  }

  input {
    display: none;
  }
`;

const PasteButton = styled.button`
  display: inline-flex;
  align-items: center;
  gap: 0.5rem;
  background: #2196F3;
  color: white;
  padding: 0.75rem 1.5rem;
  border-radius: 0.75rem;
  border: none;
  cursor: pointer;
  transition: all 0.2s;
  font-size: 1rem;

  &:hover {
    background: #1976D2;
    transform: translateY(-2px);
  }

  &:active {
    transform: translateY(0);
  }
`;

const spin = keyframes`
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
`;

const LoadingSpinner = styled.div`
  width: 40px;
  height: 40px;
  border: 4px solid #f3f3f3;
  border-top: 4px solid #2196F3;
  border-radius: 50%;
  animation: ${spin} 1s linear infinite;
  margin: 0 auto;
`;

const LoadingOverlay = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(255, 255, 255, 0.8);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 10;
`;

const PuzzleGame = () => {
  const navigate = useNavigate();
  const { playerName, showNameModal, updatePlayerName, gameProgress, updateGameProgress } = usePlayer();
  const [difficulty, setDifficulty] = useState(3);
  const [progress, setProgress] = useState(0);
  const [image, setImage] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [errors, setErrors] = useState(0);

  const handleComplete = () => {
    // Save progress when puzzle is completed
    const puzzleName = `puzzle_${difficulty}x${difficulty}`;
    updateGameProgress('puzzle', 
      [...gameProgress.completedPuzzles, puzzleName],
      { ...gameProgress.puzzleScores, [puzzleName]: errors }
    );

    alert(`გილოცავთ ${playerName}! თქვენ წარმატებით დაასრულეთ პაზლი ${errors} შეცდომით! 🎉`);
  };

  const handleError = () => {
    setErrors(prev => prev + 1);
  };

  const handleBackToMenu = () => {
    navigate('/');
  };

  const handleDifficultyChange = async (newDifficulty) => {
    setIsLoading(true);
    await new Promise(resolve => setTimeout(resolve, 500));
    setDifficulty(newDifficulty);
    setIsLoading(false);
  };

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        setImage(e.target.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleImageChange = (newImage) => {
    setImage(newImage);
  };

  const handlePaste = () => {
    navigator.clipboard.read().then(async (clipboardItems) => {
      for (const clipboardItem of clipboardItems) {
        for (const type of clipboardItem.types) {
          if (type.startsWith('image/')) {
            const blob = await clipboardItem.getType(type);
            const reader = new FileReader();
            reader.onload = (e) => {
              setImage(e.target.result);
            };
            reader.readAsDataURL(blob);
            return;
          }
        }
      }
    }).catch((err) => {
      console.error('Failed to read clipboard:', err);
    });
  };

  if (showNameModal) {
    return <NameModal onSubmit={updatePlayerName} />;
  }

  return (
    <Container>
      <Header>
        <Title>
          <GameIcon>🎮</GameIcon>
          გამარჯობა, {playerName}! 👋
        </Title>
        <HeaderButtons>
          <PasteButton onClick={handlePaste}>
            <span>📋</span>
            ჩასმა (Ctrl+V)
          </PasteButton>
          <UploadButton>
            <span>📤</span>
            სურათის ატვირთვა
            <input
              type="file"
              accept="image/*"
              onChange={handleImageUpload}
            />
          </UploadButton>
        </HeaderButtons>
      </Header>

      <Main>
        <DifficultyCard>
          <DifficultyTitle>აირჩიე სირთულის დონე</DifficultyTitle>
          <DifficultyButtons>
            {[3, 4, 5].map((size) => (
              <DifficultyButton
                key={size}
                isSelected={difficulty === size}
                onClick={() => handleDifficultyChange(size)}
                disabled={isLoading}
              >
                {size}x{size}
              </DifficultyButton>
            ))}
          </DifficultyButtons>
        </DifficultyCard>

        <div style={{ position: 'relative' }}>
          {isLoading && (
            <LoadingOverlay>
              <LoadingSpinner />
            </LoadingOverlay>
          )}
          {image && (
            <PuzzleBoard
              image={image}
              difficulty={difficulty}
              onProgress={setProgress}
              onComplete={handleComplete}
              onBackToMenu={handleBackToMenu}
              onImageChange={handleImageChange}
              onError={handleError}
            />
          )}
        </div>
      </Main>
    </Container>
  );
};

export default PuzzleGame;
