import React, { useState, useEffect } from 'react';
import { usePlayer } from '../context/PlayerContext';
import { motion } from 'framer-motion';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from 'recharts';
import { Book, AlignLeft, PuzzleIcon as PuzzlePiece } from 'lucide-react';
import styled from 'styled-components';

const DashboardContainer = styled.div`
  min-height: 100vh;
  width: 100vw;
  height: 100vh;
  position: fixed;
  top: 0;
  left: 0;
  background: linear-gradient(to bottom right, #1a237e, #4a148c, #880e4f);
  padding: 2rem;
  color: white;
  overflow-y: auto;
  z-index: 1000;

  @media (max-width: 768px) {
    padding: 1rem;
  }
`;

const Title = styled.h1`
  font-size: 2.5rem;
  font-weight: bold;
  margin-bottom: 2rem;
  text-align: center;

  @media (max-width: 768px) {
    font-size: 2rem;
    margin-bottom: 1.5rem;
  }

  @media (max-width: 480px) {
    font-size: 1.75rem;
    margin-bottom: 1rem;
  }
`;

const TabContainer = styled.div`
  display: flex;
  justify-content: flex-end;
  gap: 0.5rem;
  margin-bottom: 1rem;

  @media (max-width: 768px) {
    justify-content: center;
    flex-wrap: wrap;
  }
`;

const TabButton = styled.button`
  display: flex;
  align-items: center;
  gap: 0.5rem;
  padding: 0.5rem 1rem;
  border-radius: 9999px;
  transition: all 0.2s;
  background: ${props => props.active ? 'white' : 'rgba(255, 255, 255, 0.2)'};
  color: ${props => props.active ? '#4a148c' : 'white'};
  white-space: nowrap;

  &:hover {
    background: ${props => props.active ? 'white' : 'rgba(255, 255, 255, 0.3)'};
  }

  @media (max-width: 480px) {
    padding: 0.4rem 0.8rem;
    font-size: 0.9rem;

    svg {
      width: 16px;
      height: 16px;
    }
  }
`;

const StatsCard = styled(motion.div)`
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(10px);
  border-radius: 0.5rem;
  padding: 1.5rem;
  margin-bottom: 2rem;

  @media (max-width: 768px) {
    padding: 1.25rem;
  }

  @media (max-width: 480px) {
    padding: 1rem;
  }
`;

const StatsHeader = styled.h2`
  font-size: 1.5rem;
  font-weight: bold;
  margin-bottom: 1rem;
`;

const StatsGrid = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;

  @media (max-width: 768px) {
    flex-direction: column;
    gap: 1.5rem;
    align-items: stretch;
    text-align: center;
  }
`;

const StatValue = styled.p`
  font-size: 3rem;
  font-weight: bold;

  span {
    font-size: 1.5rem;
    font-weight: normal;
  }

  @media (max-width: 768px) {
    font-size: 2.5rem;

    span {
      font-size: 1.25rem;
    }
  }

  @media (max-width: 480px) {
    font-size: 2rem;

    span {
      font-size: 1rem;
    }
  }
`;

const StatLabel = styled.p`
  font-size: 1.125rem;
  margin-top: 0.5rem;

  @media (max-width: 768px) {
    font-size: 1rem;
  }
`;

const ProgressBar = styled.div`
  background: rgba(255, 255, 255, 0.2);
  height: 1rem;
  border-radius: 9999px;
  overflow: hidden;
  margin-top: 1.5rem;
`;

const Progress = styled.div`
  height: 100%;
  border-radius: 9999px;
  background: ${props => {
    switch (props.type) {
      case 'tasks':
        return '#4caf50';
      case 'scramble':
        return '#2196f3';
      case 'puzzle':
        return '#ffc107';
      default:
        return '#4caf50';
    }
  }};
  width: ${props => props.percent}%;
`;

const ProgressText = styled.p`
  text-align: right;
  margin-top: 0.5rem;
`;

const ChartContainer = styled.div`
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(10px);
  border-radius: 0.5rem;
  padding: 1rem;
  height: 400px;
  margin-top: 2rem;

  @media (max-width: 768px) {
    height: 300px;
  }

  @media (max-width: 480px) {
    height: 250px;
    padding: 0.75rem;
  }
`;

const Dashboard = () => {
  const { playerName, gameProgress } = usePlayer();
  const [activeTab, setActiveTab] = useState('tasks');
  const [chartData, setChartData] = useState([]);

  useEffect(() => {
    // Generate chart data based on game progress
    const weeks = 4;
    const newChartData = [];

    for (let i = 1; i <= weeks; i++) {
      newChartData.push({
        name: `კვირა ${i}`,
        სახალისო: gameProgress.completedTasks.length,
        ასოები: gameProgress.completedScrambleTasks.length,
        პაზლები: gameProgress.completedPuzzles.length
      });
    }

    setChartData(newChartData);
  }, [gameProgress]);

  const calculateProgress = (completed, total) => {
    return Math.round((completed / total) * 100);
  };

  const getTotalTasks = () => {
    return {
      tasks: 15,
      scramble: 38,
      puzzles: 10
    };
  };

  const totals = getTotalTasks();

  const renderStats = () => {
    switch (activeTab) {
      case 'tasks':
        return (
          <StatsCard
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -20 }}
            transition={{ duration: 0.5 }}
          >
            <StatsHeader>სახალისო ამოცანები</StatsHeader>
            <StatsGrid>
              <div>
                <StatValue>
                  {gameProgress.completedTasks.length} <span>/ {totals.tasks}</span>
                </StatValue>
                <StatLabel>შესრულებული ამოცანები</StatLabel>
              </div>
              <div>
                <StatValue>
                  {Object.values(gameProgress.taskScores).length > 0
                    ? (Object.values(gameProgress.taskScores).reduce((a, b) => a + b, 0) / Object.values(gameProgress.taskScores).length).toFixed(1)
                    : '0.0'}
                </StatValue>
                <StatLabel>საშუალო ქულა</StatLabel>
              </div>
            </StatsGrid>
            <ProgressBar>
              <Progress
                type="tasks"
                percent={calculateProgress(gameProgress.completedTasks.length, totals.tasks)}
              />
            </ProgressBar>
            <ProgressText>
              {calculateProgress(gameProgress.completedTasks.length, totals.tasks)}% დასრულებული
            </ProgressText>
          </StatsCard>
        );
      case 'scramble':
        return (
          <StatsCard
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -20 }}
            transition={{ duration: 0.5 }}
          >
            <StatsHeader>ასოების დალაგება</StatsHeader>
            <StatsGrid>
              <div>
                <StatValue>
                  {gameProgress.completedScrambleTasks.length} <span>/ {totals.scramble}</span>
                </StatValue>
                <StatLabel>შესრულებული დავალებები</StatLabel>
              </div>
              <div>
                <StatValue>
                  {Object.values(gameProgress.scrambleScores).length > 0
                    ? (Object.values(gameProgress.scrambleScores).reduce((a, b) => a + b, 0) / Object.values(gameProgress.scrambleScores).length).toFixed(1)
                    : '0.0'}
                </StatValue>
                <StatLabel>საშუალო ქულა</StatLabel>
              </div>
            </StatsGrid>
            <ProgressBar>
              <Progress
                type="scramble"
                percent={calculateProgress(gameProgress.completedScrambleTasks.length, totals.scramble)}
              />
            </ProgressBar>
            <ProgressText>
              {calculateProgress(gameProgress.completedScrambleTasks.length, totals.scramble)}% დასრულებული
            </ProgressText>
          </StatsCard>
        );
      case 'puzzle':
        return (
          <StatsCard
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -20 }}
            transition={{ duration: 0.5 }}
          >
            <StatsHeader>პაზლები</StatsHeader>
            <StatsGrid>
              <div>
                <StatValue>
                  {gameProgress.completedPuzzles.length} <span>/ {totals.puzzles}</span>
                </StatValue>
                <StatLabel>შესრულებული პაზლები</StatLabel>
              </div>
              <div>
                <StatValue>
                  {Object.values(gameProgress.puzzleScores).length > 0
                    ? (Object.values(gameProgress.puzzleScores).reduce((a, b) => a + b, 0) / Object.values(gameProgress.puzzleScores).length).toFixed(1)
                    : '0.0'}
                </StatValue>
                <StatLabel>საშუალო შეცდომები</StatLabel>
              </div>
            </StatsGrid>
            <ProgressBar>
              <Progress
                type="puzzle"
                percent={calculateProgress(gameProgress.completedPuzzles.length, totals.puzzles)}
              />
            </ProgressBar>
            <ProgressText>
              {calculateProgress(gameProgress.completedPuzzles.length, totals.puzzles)}% დასრულებული
            </ProgressText>
          </StatsCard>
        );
      default:
        return null;
    }
  };

  return (
    <DashboardContainer>
      <Title>მოგესალმები, {playerName}! 👋</Title>

      <TabContainer>
        <TabButton
          active={activeTab === 'tasks'}
          onClick={() => setActiveTab('tasks')}
        >
          <Book size={window.innerWidth <= 480 ? 16 : 20} />
          <span>სახალისო</span>
        </TabButton>
        <TabButton
          active={activeTab === 'scramble'}
          onClick={() => setActiveTab('scramble')}
        >
          <AlignLeft size={window.innerWidth <= 480 ? 16 : 20} />
          <span>ასოები</span>
        </TabButton>
        <TabButton
          active={activeTab === 'puzzle'}
          onClick={() => setActiveTab('puzzle')}
        >
          <PuzzlePiece size={window.innerWidth <= 480 ? 16 : 20} />
          <span>პაზლები</span>
        </TabButton>
      </TabContainer>

      {renderStats()}

      <ChartContainer>
        <StatsHeader>პროგრესის გრაფიკი</StatsHeader>
        <ResponsiveContainer width="100%" height="100%">
          <LineChart data={chartData}>
            <CartesianGrid strokeDasharray="3 3" stroke="rgba(255, 255, 255, 0.2)" />
            <XAxis dataKey="name" stroke="#fff" />
            <YAxis stroke="#fff" />
            <Tooltip
              contentStyle={{
                backgroundColor: 'rgba(255, 255, 255, 0.1)',
                border: 'none',
                backdropFilter: 'blur(10px)',
                color: 'white'
              }}
            />
            <Line type="monotone" dataKey="სახალისო" stroke="#4caf50" strokeWidth={2} />
            <Line type="monotone" dataKey="ასოები" stroke="#2196f3" strokeWidth={2} />
            <Line type="monotone" dataKey="პაზლები" stroke="#ffc107" strokeWidth={2} />
          </LineChart>
        </ResponsiveContainer>
      </ChartContainer>
    </DashboardContainer>
  );
};

export default Dashboard;
