const Tasks = () => {
  const { playerName, showNameModal, gameProgress, updateGameProgress, updatePlayerName } = usePlayer();
  const [currentTaskIndex, setCurrentTaskIndex] = useState(0);
  const [progress, setProgress] = useState(0);
  const [selectedItems, setSelectedItems] = useState([]);
  const [matchedPairs, setMatchedPairs] = useState([]);
  const [answer, setAnswer] = useState('');
  const [showSuccess, setShowSuccess] = useState(false);
  const [stickers, setStickers] = useState([]);
  const [selectedNumbers, setSelectedNumbers] = useState([]);
  const [selectedColors, setSelectedColors] = useState([]);
  const [feedback, setFeedback] = useState({});
  const [answers, setAnswers] = useState({});
  const [isVisible, setIsVisible] = useState(true);
  const [shuffledItems, setShuffledItems] = useState([]);
  const [shuffledColors, setShuffledColors] = useState([]);
  const [toast, setToast] = useState({ show: false, message: '' });
  const [completedTasks, setCompletedTasks] = useState(() => 
    (gameProgress?.completedTasks || []).length
  );
  const navigate = useNavigate();

  // Update completedTasks when gameProgress changes
  useEffect(() => {
    setCompletedTasks((gameProgress?.completedTasks || []).length);
  }, [gameProgress?.completedTasks]);

  const currentTask = useMemo(() => tasks[currentTaskIndex], [currentTaskIndex]);

  const totalTasks = useMemo(() => tasks.length, []);

  const completedTasksCount = useMemo(() => 
    (gameProgress?.completedTasks || []).length,
    [gameProgress?.completedTasks]
  );

  const isTaskCompleted = useMemo(() => 
    (gameProgress?.completedTasks || []).includes(currentTaskIndex),
    [gameProgress?.completedTasks, currentTaskIndex]
  );

  // Update progress display
  useEffect(() => {
    const completed = (gameProgress?.completedTasks || []).length;
    const total = tasks.length;
    setProgress(Math.round((completed / total) * 100));
  }, [gameProgress?.completedTasks, tasks.length]);

  useEffect(() => {
    // Load progress for current task
    const isCompleted = (gameProgress?.completedTasks || []).includes(currentTaskIndex);
    const taskScore = gameProgress?.taskScores?.[currentTaskIndex];

    if (isCompleted) {
      setShowSuccess(true);
      setStickers(Array(taskScore || 3).fill('â­'));
      console.log('Task completed:', currentTaskIndex, 'Score:', taskScore);
    } else {
      setShowSuccess(false);
      setStickers([]);
      console.log('Task not completed:', currentTaskIndex);
    }
  }, [currentTaskIndex, gameProgress]);

  const handleSuccess = async (score = 3) => {
    setShowSuccess(true);
    const newStickers = Array(score).fill('â­');
    setStickers(newStickers);

    // Update progress
    await updateGameProgress('task', new Date().toISOString(), {
      taskId: currentTaskIndex,
      score: score
    });

    // Update local state
    setCompletedTasks(prev => prev + 1);
    setProgress(prev => Math.round(((prev * tasks.length / 100) + 1) / tasks.length * 100));

    showCelebration(celebrationStickers);
    showToast('áƒ“áƒáƒ•áƒáƒšáƒ”áƒ‘áƒ áƒ¨áƒ”áƒ¡áƒ áƒ£áƒšáƒ”áƒ‘áƒ£áƒšáƒ˜áƒ! ğŸ‰');
  };

  const handleNumberClick = useCallback((number) => {
    setSelectedNumbers(prev => {
      const newSelection = [...prev];
      const index = newSelection.indexOf(number);
      if (index === -1) {
        newSelection.push(number);
      } else {
        newSelection.splice(index, 1);
      }
      return newSelection;
    });
  }, []);

  const findNextUncompletedTask = useCallback((currentIndex, direction = 1) => {
    let nextIndex = currentIndex;
    const totalTasks = tasks.length;
    const completedTasks = gameProgress?.completedTasks || [];

    do {
      nextIndex = (nextIndex + direction + totalTasks) % totalTasks;
      if (!completedTasks.includes(nextIndex)) {
        return nextIndex;
      }
    } while (nextIndex !== currentIndex);

    return currentIndex;
  }, [gameProgress?.completedTasks]);

  const handleNextTask = useCallback(() => {
    setIsVisible(false);
    setTimeout(() => {
      const nextIndex = findNextUncompletedTask(currentTaskIndex);
      setCurrentTaskIndex(nextIndex);
      setIsVisible(true);
    }, 300);
  }, [currentTaskIndex, findNextUncompletedTask]);

  const handlePreviousTask = useCallback(() => {
    setIsVisible(false);
    setTimeout(() => {
      setCurrentTaskIndex(prev => findNextUncompletedTask(prev, -1));
      setSelectedNumbers([]);
      setSelectedColors([]);
      setAnswers({});
      setFeedback({});
      setIsVisible(true);
    }, 300);
  }, [findNextUncompletedTask]);

  // áƒ™áƒáƒ›áƒáƒáƒœáƒ”áƒœáƒ¢áƒ˜áƒ¡ áƒ“áƒáƒ¢áƒ•áƒ˜áƒ áƒ—áƒ•áƒ˜áƒ¡áƒáƒ¡ áƒ’áƒáƒ“áƒáƒ•áƒ˜áƒ“áƒ”áƒ— áƒáƒ˜áƒ áƒ•áƒ”áƒš áƒ¨áƒ”áƒ£áƒ¡áƒ áƒ£áƒšáƒ”áƒ‘áƒ”áƒš áƒ“áƒáƒ•áƒáƒšáƒ”áƒ‘áƒáƒ–áƒ”
  useEffect(() => {
    if ((gameProgress?.completedTasks || []).includes(currentTaskIndex)) {
      setCurrentTaskIndex(findNextUncompletedTask(currentTaskIndex, 1));
    }
  }, [currentTaskIndex, gameProgress?.completedTasks, findNextUncompletedTask]);

  const showToast = useCallback((message) => {
    setToast({ show: true, message });
    setTimeout(() => {
      setToast({ show: false, message: '' });
    }, 2000);
  }, []);

  const handleColorClick = useCallback((index) => {
    setSelectedColors(prev => {
      if (prev.includes(index)) {
        return prev.filter(i => i !== index);
      }

      if (selectedNumbers.length === 0) return prev;

      const numberIndex = selectedNumbers[0];
      const selectedItem = shuffledItems[numberIndex];
      const selectedOption = shuffledColors[index];

      // Check if this is a correct match based only on colorName
      const isCorrectMatch = selectedItem.colorName === selectedOption.name && !selectedItem.matched;

      if (isCorrectMatch) {
        // Correct match
        const praise = currentTask.praise[Math.floor(Math.random() * currentTask.praise.length)];

        // Update shuffledItems immutably
        const newShuffledItems = shuffledItems.map((item, idx) => {
          if (idx === numberIndex) {
            return { ...item, matched: true };
          }
          return item;
        });
        setShuffledItems(newShuffledItems);

        const allMatched = newShuffledItems.every((item) => item.matched);
        if (allMatched) {
          handleSuccess(3);
          showCelebration(currentTask.stickers);
          setTimeout(handleNextTask, 1500);
        } else {
          showToast(praise);
        }
      } else {
        // Wrong match
        showToast(currentTask.hint);
      }

      setSelectedNumbers([]);
      return [];
    });
  }, [currentTask, selectedNumbers, shuffledItems, shuffledColors, handleNextTask, showToast, handleSuccess]);

  const checkAnswer = (answer, task) => {
    switch (task.type) {
      case 'math':
        // áƒ’áƒáƒ“áƒáƒ•áƒáƒ™áƒáƒœáƒ•áƒ”áƒ áƒ¢áƒáƒ— answer áƒ áƒ˜áƒªáƒ®áƒ•áƒáƒ“ áƒ¨áƒ”áƒ“áƒáƒ áƒ”áƒ‘áƒ˜áƒ¡áƒ—áƒ•áƒ˜áƒ¡
        return Number(answer) === Number(task.answer);
      case 'matching':
        return answer === task.correctMatch;
      default:
        return false;
    }
  };

  const handleAnswerSubmit = useCallback((answer) => {
    if (!currentTask) return;

    const isCorrect = checkAnswer(answer, currentTask);

    if (isCorrect) {
      // áƒ¨áƒ”áƒ•áƒ˜áƒœáƒáƒ®áƒáƒ— áƒáƒ áƒáƒ’áƒ áƒ”áƒ¡áƒ˜
      const newProgress = {
        taskId: currentTask.id,
        completed: true,
        date: new Date().toISOString()
      };

      updateGameProgress('tasks', currentTask.id, newProgress);

      // áƒ•áƒáƒ©áƒ•áƒ”áƒœáƒáƒ— áƒ¬áƒáƒ áƒ›áƒáƒ¢áƒ”áƒ‘áƒ˜áƒ¡ áƒ¨áƒ”áƒ¢áƒ§áƒáƒ‘áƒ˜áƒœáƒ”áƒ‘áƒ
      const praise = currentTask.praise[Math.floor(Math.random() * currentTask.praise.length)];
      setToast({ show: true, message: praise });

      // áƒ“áƒáƒ•áƒáƒ§áƒáƒ•áƒœáƒáƒ— áƒªáƒáƒ¢áƒ áƒ®áƒœáƒ˜áƒ— áƒ“áƒ áƒ’áƒáƒ“áƒáƒ•áƒ˜áƒ“áƒ”áƒ— áƒ¨áƒ”áƒ›áƒ“áƒ”áƒ’áƒ–áƒ”
      setTimeout(() => {
        setToast({ show: false, message: '' });
        handleNextTask();
      }, 1500);
    } else {
      // áƒáƒ áƒáƒ¡áƒ¬áƒáƒ áƒ˜ áƒáƒáƒ¡áƒ£áƒ®áƒ˜áƒ¡ áƒ¨áƒ”áƒ›áƒ—áƒ®áƒ•áƒ”áƒ•áƒáƒ¨áƒ˜
      setToast({ show: true, message: 'áƒªáƒáƒ“áƒ” áƒ—áƒáƒ•áƒ˜áƒ“áƒáƒœ! ğŸ¤”' });
      setTimeout(() => setToast({ show: false, message: '' }), 1500);
    }
  }, [currentTask, updateGameProgress]);

  const calculateProgress = useCallback(() => {
    if (!gameProgress?.tasks) return 0;
    const completedTasks = gameProgress.tasks.filter(task => task.completed);
    return completedTasks.length;
  }, [gameProgress]);

  useEffect(() => {
    const progress = calculateProgress();
    setCompletedTasks(progress);
  }, [gameProgress, calculateProgress]);

  useEffect(() => {
    const completedTasks = gameProgress?.completedTasks || [];
    const currentProgress = (completedTasks.length / tasks.length) * 100;
    setProgress(currentProgress);
  }, [gameProgress]);

  const renderMatchingGame = useCallback((task) => {
    if (!task?.items || !task?.colorOptions) {
      return null;
    }

    return (
      <MatchingGame>
        <ItemsContainer>
          {shuffledItems.map((item, index) => (
            <Item
              key={index}
              color={item.color}
              selected={selectedNumbers.includes(index)}
              onClick={() => handleNumberClick(index)}
              disabled={item.matched}
              fontSize={item.fontSize}
            >
              <span>{item.number}</span>
              {item.text && <span style={{ marginLeft: '8px' }}>{item.text}</span>}
            </Item>
          ))}
        </ItemsContainer>
        <ItemsContainer>
          {shuffledColors.map((option, index) => (
            <Item
              key={index}
              style={{ background: option.color }}
              selected={selectedColors.includes(index)}
              onClick={() => handleColorClick(index)}
              disabled={shuffledItems.find(item => item.colorName === option.name)?.matched}
            >
              {option.name}
            </Item>
          ))}
        </ItemsContainer>
      </MatchingGame>
    );
  }, [handleColorClick, handleNumberClick, selectedColors, selectedNumbers, shuffledItems, shuffledColors]);

  const renderMathGame = useCallback((task) => {
    if (!task?.options) {
      console.log('No options found for math task:', task);
      return null;
    }

    return (
      <MathGame>
        <MathProblem>
          {task.problem}
        </MathProblem>
        <NumberGrid>
          {task.options.map((number, i) => (
            <Item
              key={i}
              onClick={() => handleAnswerSubmit(number)}
              color="#4CAF50"
            >
              {number}
            </Item>
          ))}
        </NumberGrid>
      </MathGame>
    );
  }, [handleAnswerSubmit]);

  const handleRestart = useCallback(() => {
    const shouldRestart = window.confirm('áƒœáƒáƒ›áƒ“áƒ•áƒ˜áƒšáƒáƒ“ áƒ’áƒ¡áƒ£áƒ áƒ— áƒ—áƒáƒ•áƒ˜áƒ“áƒáƒœ áƒ“áƒáƒ¬áƒ§áƒ”áƒ‘áƒ? áƒáƒ áƒáƒ’áƒ áƒ”áƒ¡áƒ˜ áƒ’áƒáƒœáƒ£áƒšáƒ“áƒ”áƒ‘áƒ.');
    if (shouldRestart) {
      updateGameProgress(-1, 0); // áƒ”áƒ¡ áƒ’áƒáƒáƒ¡áƒ£áƒ¤áƒ—áƒáƒ•áƒ”áƒ‘áƒ¡ áƒáƒ áƒáƒ’áƒ áƒ”áƒ¡áƒ¡ PlayerContext-áƒ¨áƒ˜
      setCurrentTaskIndex(0);
      setSelectedNumbers([]);
      setSelectedColors([]);
      setAnswers({});
      setFeedback({});
      window.location.reload(); // áƒ’áƒ•áƒ”áƒ áƒ“áƒ˜áƒ¡ áƒ’áƒáƒ“áƒáƒ¢áƒ•áƒ˜áƒ áƒ—áƒ•áƒ áƒáƒ áƒáƒ’áƒ áƒ”áƒ¡áƒ˜áƒ¡ áƒ’áƒáƒœáƒ£áƒšáƒ”áƒ‘áƒ˜áƒ¡ áƒ¨áƒ”áƒ›áƒ“áƒ”áƒ’
    }
  }, [updateGameProgress]);

  // áƒ“áƒáƒ•áƒáƒ›áƒáƒ¢áƒáƒ— áƒáƒ®áƒáƒšáƒ˜ useEffect áƒ¨áƒ”áƒ áƒ”áƒ•áƒ˜áƒ¡áƒ—áƒ•áƒ˜áƒ¡
  useEffect(() => {
    if (currentTask?.type === 'matching') {
      // áƒ¨áƒ”áƒ•áƒ¥áƒ›áƒœáƒáƒ— áƒáƒ®áƒáƒšáƒ˜ áƒ›áƒáƒ¡áƒ˜áƒ•áƒ”áƒ‘áƒ˜ áƒ¨áƒ”áƒ›áƒ—áƒ®áƒ•áƒ”áƒ•áƒ˜áƒ—áƒ˜ áƒ’áƒáƒœáƒšáƒáƒ’áƒ”áƒ‘áƒ˜áƒ¡áƒ—áƒ•áƒ˜áƒ¡
      const shuffleArray = (array) => {
        const newArray = [...array];
        for (let i = newArray.length - 1; i > 0; i--) {
          const j = Math.floor(Math.random() * (i + 1));
          [newArray[i], newArray[j]] = [newArray[j], newArray[i]];
        }
        return newArray;
      };

      // áƒ¨áƒ”áƒ•áƒ£áƒ áƒ˜áƒáƒ— áƒ¤áƒ”áƒ áƒ”áƒ‘áƒ˜ áƒ“áƒ áƒ•áƒáƒ áƒ˜áƒáƒœáƒ¢áƒ”áƒ‘áƒ˜
      if (currentTask.colorOptions) {
        setShuffledColors(shuffleArray(currentTask.colorOptions));
      }
      if (currentTask.items) {
        setShuffledItems(shuffleArray(currentTask.items));
      }
    }
  }, [currentTask]);

  if (showNameModal) {
    return <NameModal onSubmit={updatePlayerName} />;
  }

  return (
    <Container>
      <GlobalStyle />
      {toast.show && <Toast>{toast.message}</Toast>}
      <ProgressIndicator>
        áƒ¨áƒ”áƒ¡áƒ áƒ£áƒšáƒ”áƒ‘áƒ£áƒšáƒ˜áƒ: <span>{completedTasksCount}</span> / {totalTasks}
      </ProgressIndicator>
      <TaskCard>
        <Title>
          <span>áƒ¡áƒáƒ®áƒáƒšáƒ˜áƒ¡áƒ áƒáƒ›áƒáƒªáƒáƒœáƒ”áƒ‘áƒ˜</span>
          {gameProgress?.taskScores?.[currentTaskIndex] && (
            <span>{'â­'.repeat(gameProgress.taskScores[currentTaskIndex])}</span>
          )}
        </Title>
        <Subtitle>{currentTask?.description || 'Loading...'}</Subtitle>

        {isVisible && (
          <div style={{ position: 'relative' }}>
            {completedTasksCount === totalTasks ? (
              <CompletionMessage>
                <div className="celebration">ğŸ‰</div>
                <div>áƒ§áƒáƒ©áƒáƒ¦! áƒ§áƒ•áƒ”áƒšáƒ áƒ“áƒáƒ•áƒáƒšáƒ”áƒ‘áƒ áƒ¨áƒ”áƒáƒ¡áƒ áƒ£áƒšáƒ”!</div>
                <RestartButton onClick={handleRestart}>
                  áƒ—áƒáƒ•áƒ˜áƒ“áƒáƒœ áƒ“áƒáƒ¬áƒ§áƒ”áƒ‘áƒ ğŸ”„
                </RestartButton>
              </CompletionMessage>
            ) : (
              <>
                {currentTask?.type === 'matching' && renderMatchingGame(currentTask)}
                {currentTask?.type === 'math' && renderMathGame(currentTask)}
              </>
            )}
          </div>
        )}

        {completedTasksCount !== totalTasks && (
          <ButtonsContainer>
            {currentTaskIndex > 0 && (
              <NavigationButton prev onClick={handlePreviousTask}>
                â¬…ï¸ áƒ¬áƒ˜áƒœáƒ áƒáƒ›áƒáƒªáƒáƒœáƒ
              </NavigationButton>
            )}
            <NavigationButton onClick={handleNextTask}>
              áƒ¨áƒ”áƒ›áƒ“áƒ”áƒ’áƒ˜ áƒáƒ›áƒáƒªáƒáƒœáƒ â¡ï¸
            </NavigationButton>
          </ButtonsContainer>
        )}
      </TaskCard>
    </Container>
  );
};

export default React.memo(Tasks);
