import React, { useState } from 'react';
import styled from 'styled-components';
import { usePlayer } from '../context/PlayerContext';

const ColoringGame = () => {
  const { updateGameProgress } = usePlayer();
  const [selectedColor, setSelectedColor] = useState(null);
  const [showFeedback, setShowFeedback] = useState(false);

  const colors = [
    { value: '#FFA500' }, // ნარინჯისფერი
    { value: '#90EE90' }, // მწვანე
    { value: '#FF0000' }  // წითელი - სწორი პასუხი
  ];

  const handleColorSelect = (color) => {
    setSelectedColor(color.value);
    const isCorrect = color.value === '#FF0000';
    
    if (isCorrect) {
      setShowFeedback(true);
      updateGameProgress('coloring', Date.now(), { completed: true });
      setTimeout(() => {
        setShowFeedback(false);
      }, 1500);
    }
  };

  return (
    <GameContainer>
      <GameHeader>
        <Title>გააფერადე გული</Title>
      </GameHeader>

      <ContentArea>
        {/* სანიმუშო გული */}
        <ExampleContainer>
          <ExampleTitle>ნიმუში:</ExampleTitle>
          <svg width="150" height="150" viewBox="0 0 100 100">
            <path
              d="M50 80 C 25 60, 0 40, 0 20 C 0 0, 20 0, 25 10 C 30 0, 50 0, 50 20 C 50 0, 70 0, 75 10 C 80 0, 100 0, 100 20 C 100 40, 75 60, 50 80"
              fill="#FF0000"
              stroke="none"
            />
          </svg>
        </ExampleContainer>

        {/* გასაფერადებელი გული */}
        <DrawingArea>
          <svg width="300" height="300" viewBox="0 0 100 100">
            <path
              d="M50 80 C 25 60, 0 40, 0 20 C 0 0, 20 0, 25 10 C 30 0, 50 0, 50 20 C 50 0, 70 0, 75 10 C 80 0, 100 0, 100 20 C 100 40, 75 60, 50 80"
              fill={selectedColor || 'transparent'}
              stroke="#FF0000"
              strokeWidth="2"
            />
          </svg>
        </DrawingArea>

        {/* ფერების პალიტრა */}
        <ColorPalette>
          {colors.map((color, index) => (
            <ColorButton
              key={index}
              color={color.value}
              onClick={() => handleColorSelect(color)}
            />
          ))}
        </ColorPalette>

        {showFeedback && (
          <Feedback>ყოჩაღ! სწორად გააფერადე! 🎉</Feedback>
        )}
      </ContentArea>
    </GameContainer>
  );
};

const GameContainer = styled.div`
  width: 100%;
  min-height: 100vh;
  background-color: #f5f5f5;
  padding: 20px;
`;

const GameHeader = styled.div`
  text-align: center;
  margin-bottom: 2rem;
`;

const Title = styled.h1`
  color: #333;
  font-size: 2rem;
`;

const ContentArea = styled.div`
  max-width: 800px;
  margin: 0 auto;
  background: white;
  padding: 2rem;
  border-radius: 12px;
  box-shadow: 0 2px 8px rgba(0,0,0,0.1);
`;

const ExampleContainer = styled.div`
  text-align: center;
  margin-bottom: 2rem;
`;

const ExampleTitle = styled.h2`
  font-size: 1.5rem;
  color: #666;
  margin-bottom: 1rem;
`;

const DrawingArea = styled.div`
  width: 100%;
  display: flex;
  justify-content: center;
  margin: 2rem 0;
  padding: 1rem;
  border: 2px solid #e0e0e0;
  border-radius: 8px;
`;

const ColorPalette = styled.div`
  display: flex;
  justify-content: center;
  gap: 1rem;
  margin-top: 2rem;
`;

const ColorButton = styled.button`
  width: 60px;
  height: 60px;
  border: none;
  border-radius: 8px;
  background-color: ${props => props.color};
  cursor: pointer;
  transition: transform 0.2s;

  &:hover {
    transform: scale(1.05);
  }
`;

const Feedback = styled.div`
  text-align: center;
  font-size: 1.5rem;
  color: #4CAF50;
  margin-top: 2rem;
`;

export default ColoringGame;
