          <Input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
            placeholder="·É®·Éî·Éò·Éß·Éï·Éê·Éú·Éî ·É®·Éî·Éú·Éò ·É°·Éê·ÉÆ·Éî·Éö·Éò"
            autoFocus
          />
          <Button type="submit" disabled={!name.trim()}>
            ·Éì·Éê·É¨·Éß·Éî·Éë·Éê üéÆ
          </Button>
        </form>
      </ModalContent>
    </ModalOverlay>
  );
};

export default NameModal;
