import React, { useState } from 'react';
import styled from 'styled-components';
import PuzzleBoard from './components/PuzzleBoard';
import DifficultySelector from './components/DifficultySelector';
import ImageUploader from './components/ImageUploader';
import { GlobalStyle } from './styles/GlobalStyle';

const AppContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  min-height: 100vh;
  padding: 20px;
  background: linear-gradient(135deg, #e0f7fa 0%, #b2ebf2 100%);
`;

const Title = styled.h1`
  color: #00796b;
  font-size: 2.5rem;
  text-align: center;
  margin-bottom: 20px;
  text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.1);
`;

function App() {
  const [image, setImage] = useState(null);
  const [difficulty, setDifficulty] = useState(3);
  const [progress, setProgress] = useState(0);
  const [isComplete, setIsComplete] = useState(false);
  const [gameStarted, setGameStarted] = useState(false);

  const handleImageUpload = (uploadedImage) => {
    setImage(uploadedImage);
    setProgress(0);
    setIsComplete(false);
    setGameStarted(true);
  };

  const handlePuzzleComplete = () => {
    setIsComplete(true);
    // ·Éê·É• ·É®·Éî·Éí·Éï·Éò·É´·Éö·Éò·Éê ·Éì·Éê·Éï·Éê·Éõ·Éê·É¢·Éù·Éó ·Éê·Éú·Éò·Éõ·Éê·É™·Éò·Éê ·Éê·Éú ·ÉÆ·Éõ·Éù·Éï·Éê·Éú·Éò ·Éî·É§·Éî·É•·É¢·Éò
  };

  const handleBackToMenu = () => {
    setGameStarted(false);
    setImage(null);
    setDifficulty(3);
  };

  return (
    <>
      <GlobalStyle />
      <AppContainer>
        <Title>üß© ·É°·Éê·ÉÆ·Éê·Éö·Éò·É°·Éù ·Éû·Éê·Éñ·Éö·Éî·Éë·Éò üß©</Title>

        {!gameStarted ? (
          <>
            <DifficultySelector 
              difficulty={difficulty} 
              setDifficulty={setDifficulty} 
            />
            <ImageUploader onImageUpload={handleImageUpload} />
          </>
        ) : (
          <PuzzleBoard
            image={image}
            difficulty={difficulty}
            onProgress={setProgress}
            onComplete={handlePuzzleComplete}
            onBackToMenu={handleBackToMenu}
          />
        )}

        {progress > 0 && !isComplete && (
          <div>·Éû·É†·Éù·Éí·É†·Éî·É°·Éò: {progress}%</div>
        )}

        {isComplete && (
          <div>üéâ ·Éí·Éò·Éö·Éù·É™·Éê·Éï! ·Éû·Éê·Éñ·Éö·Éò ·Éê·É¨·Éß·Éù·Éë·Éò·Éö·Éò·Éê! üéâ</div>
        )}
      </AppContainer>
    </>
  );
}

export default App;
