const celebrationStickers = ['üåü', 'üéâ', 'üéà', 'üéä', 'üèÜ', '‚≠ê', 'üí´', '‚ú®'];

const showCelebration = (stickers) => {
  const container = document.createElement('div');
  container.style.position = 'fixed';
  container.style.top = '0';
  container.style.left = '0';
  container.style.width = '100%';
  container.style.height = '100%';
  container.style.pointerEvents = 'none';
  container.style.zIndex = '1000';
  document.body.appendChild(container);

  // ·É™·Éî·Éú·É¢·É†·Éê·Éö·É£·É†·Éò ·É°·É¢·Éò·Éô·Éî·É†·Éò
  const mainSticker = document.createElement('div');
  mainSticker.style.position = 'fixed';
  mainSticker.style.fontSize = '100px';
  mainSticker.style.zIndex = '1000';
  mainSticker.style.top = '50%';
  mainSticker.style.left = '50%';
  mainSticker.style.transform = 'translate(-50%, -50%)';
  mainSticker.style.animation = 'celebrate 1s ease-out forwards';
  mainSticker.textContent = stickers[Math.floor(Math.random() * stickers.length)];
  container.appendChild(mainSticker);

  // ·Éõ·É§·É†·Éò·Éú·Éê·Éï·Éò ·É°·É¢·Éò·Éô·Éî·É†·Éî·Éë·Éò
  for (let i = 0; i < 15; i++) {
    const sticker = document.createElement('div');
    sticker.style.position = 'fixed';
    sticker.style.fontSize = '40px';
    sticker.style.zIndex = '999';
    sticker.style.left = Math.random() * 100 + 'vw';
    sticker.style.top = '100vh';
    sticker.style.animation = `float ${2 + Math.random() * 2}s linear forwards`;
    sticker.textContent = stickers[Math.floor(Math.random() * stickers.length)];
    container.appendChild(sticker);
  }

  setTimeout(() => {
    document.body.removeChild(container);
  }, 3000);
};

const Tasks = () => {
  const { playerName, showNameModal, updatePlayerName } = usePlayer();
  const [currentTaskIndex, setCurrentTaskIndex] = useState(0);
  const [selectedNumbers, setSelectedNumbers] = useState([]);
  const [selectedColors, setSelectedColors] = useState([]);
  const [feedback, setFeedback] = useState({});
  const [answers, setAnswers] = useState({});
  const [isVisible, setIsVisible] = useState(true);
  const [shuffledItems, setShuffledItems] = useState([]);
  const [shuffledColors, setShuffledColors] = useState([]);

  useEffect(() => {
    const currentTask = tasks[currentTaskIndex];
    if (currentTask && currentTask.type === 'matching' && currentTask.items) {
      const shuffleArray = (array) => {
        if (!Array.isArray(array) || array.length === 0) return [];
        const newArray = [...array];
        for (let i = newArray.length - 1; i > 0; i--) {
          const j = Math.floor(Math.random() * (i + 1));
          [newArray[i], newArray[j]] = [newArray[j], newArray[i]];
        }
        return newArray;
      };

      setShuffledItems(shuffleArray(currentTask.items));
      setShuffledColors(shuffleArray([...currentTask.items]));
    }
  }, [currentTaskIndex]);

  const handleNumberClick = (id) => {
    if (selectedNumbers.includes(id)) {
      setSelectedNumbers(selectedNumbers.filter(num => num !== id));
    } else if (selectedNumbers.length < 1) {
      setSelectedNumbers([...selectedNumbers, id]);
    }
  };

  const handleColorClick = (id) => {
    if (selectedNumbers.length === 0) return;

    const selectedNumber = selectedNumbers[0];
    const selectedItem = shuffledItems.find(item => item.id === selectedNumber);
    const selectedColorItem = shuffledColors.find(item => item.id === id);

    if (selectedItem && selectedColorItem) {
      const isCorrect = selectedItem.id === selectedColorItem.id;

      setSelectedNumbers([]);
      setSelectedColors([]);

      const newFeedback = { ...feedback };
      if (isCorrect) {
        newFeedback[`${selectedNumber}-${id}`] = true;

        // ·Éï·Éê·Éõ·Éù·É¨·Éõ·Éî·Éë·Éó ·Éê·É†·Éò·É° ·Éó·É£ ·Éê·É†·Éê ·Éß·Éï·Éî·Éö·Éê ·É¨·Éß·Éï·Éò·Éö·Éò ·É°·É¨·Éù·É†·Éê·Éì ·Éì·Éê·Éô·Éê·Éï·É®·Éò·É†·Éî·Éë·É£·Éö·Éò
        const allPairs = shuffledItems.every(item => 
          Object.keys(newFeedback).some(key => 
            key.startsWith(item.id.toString()) && newFeedback[key]
          )
        );

        if (allPairs) {
          const currentTask = tasks[currentTaskIndex];
          showCelebration(currentTask.stickers);
          setTimeout(() => {
            const randomPraise = currentTask.praise[Math.floor(Math.random() * currentTask.praise.length)];
            alert(`${playerName}, ${randomPraise}`);
          }, 1500);
        }
      } else {
        newFeedback[`${selectedNumber}-${id}`] = false;
      }
      setFeedback(newFeedback);
    }
  };

  const handleAnswerSubmit = (taskId) => {
    const currentTask = tasks[currentTaskIndex];
    const userAnswer = parseInt(answers[taskId]);
    const isCorrect = userAnswer === currentTask.correctAnswer;

    setFeedback(prev => ({
      ...prev,
      [taskId]: isCorrect
    }));

    if (isCorrect) {
      showCelebration(currentTask.stickers);
      setTimeout(() => {
        const randomPraise = currentTask.praise[Math.floor(Math.random() * currentTask.praise.length)];
        alert(`${playerName}, ${randomPraise}`);
      }, 1500);
    }
  };

  const handleNextTask = () => {
    setIsVisible(false);
    setTimeout(() => {
      setCurrentTaskIndex((prev) => (prev + 1) % tasks.length);
      setSelectedNumbers([]);
      setSelectedColors([]);
      setAnswers({});
      setFeedback({});
      setIsVisible(true);
    }, 300);
  };

  const handlePreviousTask = () => {
    setIsVisible(false);
    setTimeout(() => {
      setCurrentTaskIndex((prev) => (prev - 1 + tasks.length) % tasks.length);
      setSelectedNumbers([]);
      setSelectedColors([]);
      setAnswers({});
      setFeedback({});
      setIsVisible(true);
    }, 300);
  };

  const currentTask = tasks[currentTaskIndex];

  const renderMatchingGame = (task) => {
    return (
      <MatchingGame>
        <ItemsContainer>
          {task.items.map((item, index) => (
            <Item
              key={index}
              color={item.color}
              selected={selectedNumbers.includes(index)}
              onClick={() => handleNumberClick(index)}
              disabled={item.matched}
            >
              {item.number}
            </Item>
          ))}
        </ItemsContainer>
        <ItemsContainer>
          {task.items.map((item, index) => (
            <Item
              key={index}
              color={item.color}
              selected={selectedColors.includes(index)}
              onClick={() => handleColorClick(index)}
              disabled={item.matched}
            >
              {item.color}
            </Item>
          ))}
        </ItemsContainer>
      </MatchingGame>
    );
  };

  const renderMathGame = (task) => {
    return (
      <MathGame>
        <MathProblem>
          {task.problem}
        </MathProblem>
        <NumberGrid>
          {Array.from({ length: 9 }, (_, i) => (
            <Item
              key={i}
              onClick={() => handleAnswerSubmit(i + 1)}
              color="#4CAF50"
            >
              {i + 1}
            </Item>
          ))}
        </NumberGrid>
      </MathGame>
    );
  };

  if (showNameModal) {
    return <NameModal onSubmit={updatePlayerName} />;
  }

  return (
    <Container>
      <GlobalStyle />
      <HomeButton onClick={() => window.location.href = '/'}>üè†</HomeButton>
      <TaskCard isVisible={isVisible}>
        <Title>·Éí·Éê·Éõ·Éê·É†·ÉØ·Éù·Éë·Éê, {playerName}!</Title>
        <Subtitle>{currentTask.description}</Subtitle>
        {currentTask.type === 'matching' ? renderMatchingGame(currentTask) : renderMathGame(currentTask)}
      </TaskCard>
      <ButtonsContainer>
        <NavigationButton prev onClick={handlePreviousTask}>‚Üê ·É¨·Éò·Éú·Éê</NavigationButton>
        <NavigationButton onClick={handleNextTask}>·É®·Éî·Éõ·Éì·Éî·Éí·Éò ‚Üí</NavigationButton>
      </ButtonsContainer>
    </Container>
  );
};

export default Tasks;
