import React, { useState, useRef, useEffect } from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';

const Container = styled.div`
  min-height: 100vh;
  padding: 2rem;
  background: linear-gradient(135deg, #fff6e5, #ffecf2);
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const Title = styled.h1`
  color: #FF4081;
  text-align: center;
  margin-bottom: 2rem;
  font-size: 2.5rem;
  text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.1);
`;

const GameContainer = styled.div`
  background: white;
  border-radius: 20px;
  padding: 2rem;
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1);
  max-width: 1200px;
  width: 100%;
  margin: 0 auto;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const CanvasesContainer = styled.div`
  display: flex;
  gap: 2rem;
  margin: 2rem 0;
  justify-content: center;
  flex-wrap: wrap;
`;

const CanvasWrapper = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 1rem;
`;

const CanvasLabel = styled.h3`
  color: #333;
  margin: 0;
`;

const Canvas = styled.canvas`
  border: 2px solid #eee;
  border-radius: 10px;
  background: white;
`;

const ColorPalette = styled.div`
  display: flex;
  gap: 1rem;
  justify-content: center;
  margin: 2rem 0;
`;

const ColorButton = styled(motion.button)`
  width: 60px;
  height: 60px;
  border-radius: 50%;
  border: 3px solid ${props => props.isSelected ? '#333' : 'transparent'};
  background-color: ${props => props.color};
  cursor: pointer;
  transition: transform 0.2s ease;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);

  &:hover {
    transform: scale(1.1);
  }
`;

const Message = styled.div`
  font-size: 1.5rem;
  color: ${props => props.isCorrect ? '#4CAF50' : '#FF4081'};
  margin-top: 1rem;
  font-weight: bold;
`;

const colors = [
  '#FF0000', // Red
  '#0000FF', // Blue
  '#00FF00', // Green
];

const ColoringGame = () => {
  const [selectedColor, setSelectedColor] = useState(null);
  const [isDrawing, setIsDrawing] = useState(false);
  const [isCorrect, setIsCorrect] = useState(null);
  const exampleCanvasRef = useRef(null);
  const drawingCanvasRef = useRef(null);
  const contextRef = useRef(null);

  const drawHeart = (context, filled = false) => {
    const width = context.canvas.width;
    const height = context.canvas.height;
    const size = Math.min(width, height) * 0.6;
    const x = width / 2;
    const y = height / 2;

    context.beginPath();
    context.moveTo(x, y + size * 0.3);
    context.bezierCurveTo(
      x + size * 0.4, y, 
      x + size * 0.5, y - size * 0.4, 
      x, y - size * 0.5
    );
    context.bezierCurveTo(
      x - size * 0.5, y - size * 0.4, 
      x - size * 0.4, y, 
      x, y + size * 0.3
    );
    
    if (filled) {
      context.fillStyle = '#FF0000';
      context.fill();
    }
    context.strokeStyle = '#333';
    context.lineWidth = 2;
    context.stroke();
  };

  useEffect(() => {
    // Setup example canvas
    const exampleCanvas = exampleCanvasRef.current;
    exampleCanvas.width = 300;
    exampleCanvas.height = 300;
    const exampleCtx = exampleCanvas.getContext('2d');
    drawHeart(exampleCtx, true);

    // Setup drawing canvas
    const drawingCanvas = drawingCanvasRef.current;
    drawingCanvas.width = 300;
    drawingCanvas.height = 300;
    const drawingCtx = drawingCanvas.getContext('2d');
    drawHeart(drawingCtx, false);
    contextRef.current = drawingCtx;
  }, []);

  const startDrawing = ({ nativeEvent }) => {
    if (!selectedColor) return;
    const { offsetX, offsetY } = nativeEvent;
    contextRef.current.beginPath();
    contextRef.current.moveTo(offsetX, offsetY);
    setIsDrawing(true);
  };

  const draw = ({ nativeEvent }) => {
    if (!isDrawing || !selectedColor) return;
    const { offsetX, offsetY } = nativeEvent;
    contextRef.current.lineTo(offsetX, offsetY);
    contextRef.current.strokeStyle = selectedColor;
    contextRef.current.lineWidth = 20;
    contextRef.current.lineCap = 'round';
    contextRef.current.lineJoin = 'round';
    contextRef.current.stroke();
  };

  const stopDrawing = () => {
    contextRef.current.closePath();
    setIsDrawing(false);
    
    // Check if the correct color was used
    if (selectedColor === '#FF0000') {
      setIsCorrect(true);
    } else if (selectedColor) {
      setIsCorrect(false);
    }
  };

  const clearCanvas = () => {
    const canvas = drawingCanvasRef.current;
    const context = contextRef.current;
    context.clearRect(0, 0, canvas.width, canvas.height);
    drawHeart(context, false);
    setIsCorrect(null);
  };

  return (
    <Container>
      <Title>üé® ·Éí·Éê·Éê·É§·Éî·É†·Éê·Éì·Éî ·Éí·É£·Éö·Éò</Title>
      
      <GameContainer>
        <CanvasesContainer>
          <CanvasWrapper>
            <CanvasLabel>·Éú·Éò·Éõ·É£·É®·Éò</CanvasLabel>
            <Canvas ref={exampleCanvasRef} />
          </CanvasWrapper>
          <CanvasWrapper>
            <CanvasLabel>·Éí·Éê·Éê·É§·Éî·É†·Éê·Éì·Éî</CanvasLabel>
            <Canvas
              ref={drawingCanvasRef}
              onMouseDown={startDrawing}
              onMouseMove={draw}
              onMouseUp={stopDrawing}
              onMouseLeave={stopDrawing}
            />
          </CanvasWrapper>
        </CanvasesContainer>

        <ColorPalette>
          {colors.map((color) => (
            <ColorButton
              key={color}
              color={color}
              isSelected={selectedColor === color}
              onClick={() => setSelectedColor(color)}
              whileHover={{ scale: 1.1 }}
              whileTap={{ scale: 0.95 }}
            />
          ))}
        </ColorPalette>

        {isCorrect !== null && (
          <Message isCorrect={isCorrect}>
            {isCorrect ? 'üéâ ·É°·É¨·Éù·É†·Éò·Éê!' : '‚ùå ·É°·É™·Éê·Éì·Éî ·Éó·Éê·Éï·Éò·Éì·Éê·Éú'}
          </Message>
        )}

        {!isCorrect && selectedColor && (
          <ColorButton
            onClick={clearCanvas}
            style={{ 
              backgroundColor: '#f0f0f0',
              marginTop: '1rem',
              width: 'auto',
              height: 'auto',
              padding: '0.5rem 1rem',
              borderRadius: '8px'
            }}
          >
            üóëÔ∏è ·Éí·Éê·É°·É£·É§·Éó·Éê·Éï·Éî·Éë·Éê
          </ColorButton>
        )}
      </GameContainer>
    </Container>
  );
};

export default ColoringGame;
