import React, { useState } from 'react';
import styled from 'styled-components';
import { usePlayer } from '../context/PlayerContext';
// სურათების იმპორტი
import heartOutline from '../assets/images/heart-outline.svg';
import heartFilled from '../assets/images/heart-filled.svg';

const DrawingGame = () => {
  const { updateGameProgress } = usePlayer();
  const [selectedColor, setSelectedColor] = useState(null);
  const [showFeedback, setShowFeedback] = useState(false);

  const colors = [
    { value: '#FFA500' }, // ნარინჯისფერი
    { value: '#90EE90' }, // მწვანე
    { value: '#FF0000' }  // წითელი
  ];

  const handleColorSelect = (color) => {
    setSelectedColor(color.value);
    const isCorrect = color.value === '#FF0000';
    
    if (isCorrect) {
      setShowFeedback(true);
      updateGameProgress('drawing', Date.now(), { completed: true });
      setTimeout(() => {
        setShowFeedback(false);
      }, 1500);
    }
  };

  return (
    <Container>
      <Title>ხატვა</Title>
      
      <GameArea>
        {/* სანიმუშო გული */}
        <ExampleImage src={heartFilled} alt="სანიმუშო გული" />

        {/* გასაფერადებელი გული */}
        <DrawingArea style={{ backgroundColor: selectedColor || 'transparent' }}>
          <OutlineImage src={heartOutline} alt="გასაფერადებელი გული" />
        </DrawingArea>

        {/* ფერების პალიტრა */}
        <ColorPalette>
          {colors.map((color, index) => (
            <ColorButton
              key={index}
              color={color.value}
              onClick={() => handleColorSelect(color)}
            />
          ))}
        </ColorPalette>

        {showFeedback && (
          <Feedback>ყოჩაღ! სწორად გააფერადე! 🎉</Feedback>
        )}
      </GameArea>
    </Container>
  );
};

const Container = styled.div`
  width: 100%;
  min-height: 100vh;
  padding: 2rem;
  background-color: #fff;
`;

const Title = styled.h1`
  text-align: center;
  color: #333;
  margin-bottom: 2rem;
`;

const GameArea = styled.div`
  max-width: 800px;
  margin: 0 auto;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 2rem;
`;

const ExampleImage = styled.img`
  position: absolute;
  top: 2rem;
  right: 2rem;
  width: 150px;
  height: 150px;
`;

const DrawingArea = styled.div`
  width: 100%;
  max-width: 500px;
  aspect-ratio: 1;
  border: 2px solid #e0e0e0;
  border-radius: 8px;
  display: flex;
  justify-content: center;
  align-items: center;
  background: white;
`;

const ColorPalette = styled.div`
  display: flex;
  gap: 1rem;
  margin-top: 1rem;
`;

const ColorButton = styled.button`
  width: 60px;
  height: 60px;
  border: none;
  border-radius: 8px;
  background-color: ${props => props.color};
  cursor: pointer;
  transition: transform 0.2s;

  &:hover {
    transform: scale(1.05);
  }
`;

const Feedback = styled.div`
  font-size: 1.5rem;
  color: #4CAF50;
  text-align: center;
`;

const OutlineImage = styled.img`
  width: 100%;
  height: 100%;
  object-fit: contain;
`;

export default DrawingGame;
