      let currentIndex = scrambledIndices[i];
      if (currentIndex === i) {
        // ·Éó·É£ ·Éò·Éú·Éì·Éî·É•·É°·Éò ·Éî·Éõ·Éó·ÉÆ·Éï·Éî·Éï·Éê ·Éó·Éê·Éï·Éò·É° ·Éû·Éù·Éñ·Éò·É™·Éò·Éê·É°, ·Éï·É™·Éï·Éö·Éò·Éó ·É°·ÉÆ·Éï·Éê ·Éû·Éù·Éñ·Éò·É™·Éò·Éê·É°·Éó·Éê·Éú
        for (let j = i + 1; j < word.length; j++) {
          if (scrambledIndices[j] !== i) {
            // ·Éí·Éê·Éï·É™·Éï·Éê·Éö·Éù·Éó ·Éû·Éù·Éñ·Éò·É™·Éò·Éî·Éë·Éò
            [scrambledIndices[i], scrambledIndices[j]] = [scrambledIndices[j], scrambledIndices[i]];
            break;
          }
        }
        // ·Éó·É£ ·Éï·Éî·É† ·Éï·Éò·Éû·Éù·Éï·Éî·Éó ·É®·Éî·É°·Éê·É§·Éî·É†·Éò·É°·Éò ·Éû·Éù·Éñ·Éò·É™·Éò·Éê ·É¨·Éò·Éú, ·Éï·Éî·É´·Éî·Éë·Éó ·É£·Éô·Éê·Éú
        if (scrambledIndices[i] === i) {
          for (let j = i - 1; j >= 0; j--) {
            if (scrambledIndices[j] !== j) {
              [scrambledIndices[i], scrambledIndices[j]] = [scrambledIndices[j], scrambledIndices[i]];
              break;
            }
          }
        }
      }
    }
    
    // ·Éï·É•·Éõ·Éú·Éò·Éó ·É°·Éê·Éë·Éù·Éö·Éù·Éù ·Éê·É†·Éî·É£·Éö ·Éõ·Éê·É°·Éò·Éï·É°
    return scrambledIndices.map((index, i) => ({
      id: `letter-${i}`,
      content: originalLetters[index],
      originalIndex: index
    }));
  };

  const selectRandomAnimal = () => {
    const availableAnimals = categories[currentCategory].filter(animal => !completedTasks[currentCategory].includes(animal.name));
    
    if (availableAnimals.length === 0) {
      const shouldReset = window.confirm('·Éí·Éò·Éö·Éù·É™·Éê·Éï·Éó! ·Éó·É•·Éï·Éî·Éú ·Éß·Éï·Éî·Éö·Éê ·Éê·Éõ·Éù·É™·Éê·Éú·Éê ·É®·Éî·Éê·É°·É†·É£·Éö·Éî·Éó! ·Éí·É°·É£·É†·Éó ·Éó·Éê·Éï·Éò·Éì·Éê·Éú ·Éì·Éê·É¨·Éß·Éî·Éë·Éê?');
      if (shouldReset) {
        localStorage.removeItem('completedTasks');
        setCompletedTasks({ animals: [], instruments: [] });
        const randomAnimal = categories[currentCategory][Math.floor(Math.random() * categories[currentCategory].length)];
        initializeAnimal(randomAnimal);
        setShowGame(true);
      } else {
        setShowGame(false);
      }
      return;
    }
    
    const randomIndex = Math.floor(Math.random() * availableAnimals.length);
    const randomAnimal = availableAnimals[randomIndex];
    
    initializeAnimal(randomAnimal);
    setShowGame(true);
  };

  const initializeAnimal = (animal) => {
    setCurrentAnimal(animal);
    setScrambledLetters(scrambleWord(animal.name));
    setPlacedLetters(Array(animal.name.length).fill(null));
    setCorrectPositions([]);
  };

  const handleDragStart = (e, letter, index) => {
    setDraggedLetter({ letter, index });
    e.target.classList.add('dragging');
  };

  const handleDragOver = (e) => {
    e.preventDefault();
  };

  const handleDrop = (e, targetIndex) => {
    e.preventDefault();
    if (!draggedLetter) return;

    if (draggedLetter.letter.content !== currentAnimal.name[targetIndex]) {
      setWrongPosition(targetIndex);
      setTimeout(() => {
        setWrongPosition(null);
      }, 500);
      return;
    }

    const newPlacedLetters = [...placedLetters];
    const newScrambledLetters = [...scrambledLetters];

    newPlacedLetters[targetIndex] = draggedLetter.letter;
    newScrambledLetters.splice(draggedLetter.index, 1);

    setPlacedLetters(newPlacedLetters);
    setScrambledLetters(newScrambledLetters);

    const newCorrectPositions = [];
    newPlacedLetters.forEach((letter, index) => {
      if (letter && letter.content === currentAnimal.name[index]) {
        newCorrectPositions.push(index);
      }
    });

    setCorrectPositions(newCorrectPositions);

    if (newCorrectPositions.length === currentAnimal.name.length) {
      const newCompletedTasks = {
        ...completedTasks,
        [currentCategory]: [...completedTasks[currentCategory], currentAnimal.name]
      };
      setCompletedTasks(newCompletedTasks);
      localStorage.setItem('completedTasks', JSON.stringify(newCompletedTasks));
      
      if (newCompletedTasks.animals.length === categories.animals.length && newCompletedTasks.instruments.length === categories.instruments.length) {
        setShowGame(false);
        window.alert('·Éí·Éò·Éö·Éù·É™·Éê·Éï·Éó! ·Éó·É•·Éï·Éî·Éú ·Éß·Éï·Éî·Éö·Éê ·Éê·Éõ·Éù·É™·Éê·Éú·Éê ·É®·Éî·Éê·É°·É†·É£·Éö·Éî·Éó! üéâ');
      } else {
        setShowGame(false);
      }
    }
  };

  const startNewGame = () => {
    // ·ÉØ·Éî·É† ·Éï·É™·Éì·Éò·Éö·Éù·Éë·Éó ·Éõ·Éò·Éõ·Éì·Éò·Éú·Éê·É†·Éî ·Éô·Éê·É¢·Éî·Éí·Éù·É†·Éò·Éò·Éì·Éê·Éú
    let availableTasks = categories[currentCategory].filter(
      item => !completedTasks[currentCategory].includes(item.name)
    );

    // ·Éó·É£ ·Éõ·Éò·Éõ·Éì·Éò·Éú·Éê·É†·Éî ·Éô·Éê·É¢·Éî·Éí·Éù·É†·Éò·Éê ·É™·Éê·É†·Éò·Éî·Éö·Éò·Éê, ·Éï·É™·Éì·Éò·Éö·Éù·Éë·Éó ·É°·ÉÆ·Éï·Éê ·Éô·Éê·É¢·Éî·Éí·Éù·É†·Éò·Éê·É°
    if (availableTasks.length === 0) {
      const otherCategory = currentCategory === 'animals' ? 'instruments' : 'animals';
      availableTasks = categories[otherCategory].filter(
        item => !completedTasks[otherCategory].includes(item.name)
      );
      
      if (availableTasks.length > 0) {
        setCurrentCategory(otherCategory);
      } else {
        window.alert('·Éí·Éò·Éö·Éù·É™·Éê·Éï·Éó! ·Éó·É•·Éï·Éî·Éú ·Éß·Éï·Éî·Éö·Éê ·Éê·Éõ·Éù·É™·Éê·Éú·Éê ·É®·Éî·Éê·É°·É†·É£·Éö·Éî·Éó! üéâ');
        return;
      }
    }

    const randomTask = availableTasks[Math.floor(Math.random() * availableTasks.length)];
    initializeAnimal(randomTask);
    setShowGame(true);
  };

  if (isLoading) {
    return <div>·Éò·É¢·Éï·Éò·É†·Éó·Éî·Éë·Éê...</div>;
  }

  if (!currentAnimal || !showGame) {
    const remainingCount = categories[currentCategory].length - completedTasks[currentCategory].length;
    return (
      <div className="scramble-game">
        <h2 className="game-title">·É°·Éê·ÉÆ·Éê·Éö·Éò·É°·Éù ·Éê·Éõ·Éù·É™·Éê·Éú·Éî·Éë·Éò</h2>
        <p className="progress-text">
          ·É®·Éî·É°·É†·É£·Éö·Éî·Éë·É£·Éö·Éò ·Éê·Éõ·Éù·É™·Éê·Éú·Éî·Éë·Éò: {completedTasks[currentCategory].length} / {categories[currentCategory].length}
        </p>
        <button onClick={startNewGame} className="scramble-button">
          ·É®·Éî·Éõ·Éì·Éî·Éí·Éò ·Éê·Éõ·Éù·É™·Éê·Éú·Éê {remainingCount > 0 ? `(·Éì·Éê·É†·É©·Éî·Éú·Éò·Éö·Éò·Éê ${remainingCount})` : ''}
        </button>
        {completedTasks[currentCategory].length === categories[currentCategory].length && (
          <button onClick={handleReset} className="scramble-button reset">
            ·Éó·Éê·Éï·Éò·Éì·Éê·Éú ·Éì·Éê·É¨·Éß·Éî·Éë·Éê
          </button>
        )}
      </div>
    );
  }

  return (
    <div className="scramble-game">
      <h2 className="game-title">·Éì·Éê·Éê·Éö·Éê·Éí·Éî ·Éê·É°·Éù·Éî·Éë·Éò ·É°·É¨·Éù·É†·Éê·Éì</h2>
      <p className="game-instructions">
        ·Éí·Éê·Éì·Éõ·Éù·Éò·É¢·Éê·Éú·Éî ·Éê·É°·Éù·Éî·Éë·Éò ·Éì·Éê ·É©·Éê·É°·Éï·Éò ·É°·É¨·Éù·É† ·Éê·Éì·Éí·Éò·Éö·Éê·É°
      </p>
      <p className="progress-text">
        ·Éì·Éê·É†·É©·Éî·Éú·Éò·Éö·Éò ·Éê·Éõ·Éù·É™·Éê·Éú·Éî·Éë·Éò: {categories[currentCategory].length - completedTasks[currentCategory].length} / {categories[currentCategory].length}
      </p>
      
      <img 
        src={`${process.env.PUBLIC_URL}/photos/${currentAnimal.image}`} 
        alt={currentAnimal.name}
        className="animal-image"
      />
      
      <div 
        ref={targetContainerRef}
        className="target-container"
      >
        {placedLetters.map((letter, index) => (
          <div
            key={index}
            className={`letter-slot ${correctPositions.includes(index) ? 'correct' : ''} 
                      ${letter ? 'filled' : ''} 
                      ${wrongPosition === index ? 'wrong' : ''}`}
            onDragOver={handleDragOver}
            onDrop={(e) => handleDrop(e, index)}
          >
            {letter && letter.content}
          </div>
        ))}
      </div>

      <div 
        ref={lettersContainerRef}
        className="letters-container"
      >
        {scrambledLetters.map((letter, index) => (
          <div
            key={letter.id}
            data-id={letter.id}
            className="letter"
            draggable={true}
            onDragStart={(e) => handleDragStart(e, letter, index)}
          >
            {letter.content}
          </div>
        ))}
      </div>
    </div>
  );
};

export default ScrambleGame;
