        }
        if (scrambledIndices[i] === i) {
          for (let j = i - 1; j >= 0; j--) {
            if (scrambledIndices[j] !== j) {
              [scrambledIndices[i], scrambledIndices[j]] = [scrambledIndices[j], scrambledIndices[i]];
              break;
            }
          }
        }
      }
    }
    
    return scrambledIndices.map((index, i) => ({
      id: `letter-${i}`,
      content: originalLetters[index],
      originalIndex: index
    }));
  };

  const selectRandomAnimal = () => {
    const availableAnimals = animals.filter(animal => !completedAnimals.includes(animal.name));
    
    if (availableAnimals.length === 0) {
      window.alert('·Éí·Éò·Éö·Éù·É™·Éê·Éï·Éó! ·Éó·É•·Éï·Éî·Éú ·Éß·Éï·Éî·Éö·Éê ·Éê·Éõ·Éù·É™·Éê·Éú·Éê ·É®·Éî·Éê·É°·É†·É£·Éö·Éî·Éó! üéâ');
      localStorage.removeItem('completedAnimals');
      setCompletedAnimals([]);
      const randomAnimal = animals[Math.floor(Math.random() * animals.length)];
      initializeAnimal(randomAnimal);
      setShowGame(true);
      return;
    }
    
    const randomIndex = Math.floor(Math.random() * availableAnimals.length);
    const randomAnimal = availableAnimals[randomIndex];
    initializeAnimal(randomAnimal);
    setShowGame(true);
  };

  const initializeAnimal = (animal) => {
    setCurrentAnimal(animal);
    setScrambledLetters(scrambleWord(animal.name));
    setPlacedLetters(Array(animal.name.length).fill(null));
    setCorrectPositions([]);
  };

  const handleDragStart = (e, letter, index) => {
    setDraggedLetter({ letter, index });
    e.target.classList.add('dragging');
  };

  const handleDragOver = (e) => {
    e.preventDefault();
  };

  const handleDrop = (e, targetIndex) => {
    e.preventDefault();
    if (!draggedLetter) return;

    if (draggedLetter.letter.content !== currentAnimal.name[targetIndex]) {
      setWrongPosition(targetIndex);
      setTimeout(() => {
        setWrongPosition(null);
      }, 500);
      return;
    }

    const newPlacedLetters = [...placedLetters];
    const newScrambledLetters = [...scrambledLetters];

    newPlacedLetters[targetIndex] = draggedLetter.letter;
    newScrambledLetters.splice(draggedLetter.index, 1);

    setPlacedLetters(newPlacedLetters);
    setScrambledLetters(newScrambledLetters);

    const newCorrectPositions = [];
    newPlacedLetters.forEach((letter, index) => {
      if (letter && letter.content === currentAnimal.name[index]) {
        newCorrectPositions.push(index);
      }
    });

    setCorrectPositions(newCorrectPositions);

    if (newCorrectPositions.length === currentAnimal.name.length) {
      const newCompletedAnimals = [...completedAnimals, currentAnimal.name];
      setCompletedAnimals(newCompletedAnimals);
      localStorage.setItem('completedAnimals', JSON.stringify(newCompletedAnimals));
      
      if (newCompletedAnimals.length === animals.length) {
        window.alert('·Éí·Éò·Éö·Éù·É™·Éê·Éï·Éó! ·Éó·É•·Éï·Éî·Éú ·Éß·Éï·Éî·Éö·Éê ·Éê·Éõ·Éù·É™·Éê·Éú·Éê ·É®·Éî·Éê·É°·É†·É£·Éö·Éî·Éó! üéâ');
        localStorage.removeItem('completedAnimals');
        setCompletedAnimals([]);
        const randomAnimal = animals[Math.floor(Math.random() * animals.length)];
        initializeAnimal(randomAnimal);
      } else {
        const availableAnimals = animals.filter(animal => !newCompletedAnimals.includes(animal.name));
        const randomIndex = Math.floor(Math.random() * availableAnimals.length);
        const randomAnimal = availableAnimals[randomIndex];
        initializeAnimal(randomAnimal);
      }
    }
  };

  const startNewGame = () => {
    const availableAnimals = animals.filter(animal => !completedAnimals.includes(animal.name));
    
    if (availableAnimals.length === 0) {
      window.alert('·Éí·Éò·Éö·Éù·É™·Éê·Éï·Éó! ·Éó·É•·Éï·Éî·Éú ·Éß·Éï·Éî·Éö·Éê ·Éê·Éõ·Éù·É™·Éê·Éú·Éê ·É®·Éî·Éê·É°·É†·É£·Éö·Éî·Éó! üéâ');
      return;
    }
    
    const randomIndex = Math.floor(Math.random() * availableAnimals.length);
    const randomAnimal = availableAnimals[randomIndex];
    initializeAnimal(randomAnimal);
    setShowGame(true);
  };

  const handleReset = () => {
    window.alert('·Éó·Éê·Éõ·Éê·É®·Éò ·Éò·É¨·Éß·Éî·Éë·Éê ·Éó·Éê·Éï·Éò·Éì·Éê·Éú! üéÆ');
    localStorage.removeItem('completedAnimals');
    setCompletedAnimals([]);
    const randomAnimal = animals[Math.floor(Math.random() * animals.length)];
    initializeAnimal(randomAnimal);
    setShowGame(true);
  };

  useEffect(() => {
    const initializeGame = () => {
      const saved = localStorage.getItem('completedAnimals');
      const savedAnimals = saved ? JSON.parse(saved) : [];
      setCompletedAnimals(savedAnimals);
      
      const availableAnimals = animals.filter(animal => !savedAnimals.includes(animal.name));
      if (availableAnimals.length === 0) {
        setShowGame(false);
        return;
      }
      
      const randomAnimal = availableAnimals[Math.floor(Math.random() * availableAnimals.length)];
      initializeAnimal(randomAnimal);
      setShowGame(true);
    };

    initializeGame();
  }, []);

  if (!currentAnimal) {
    const randomAnimal = animals[Math.floor(Math.random() * animals.length)];
    initializeAnimal(randomAnimal);
  }

  return (
    <div className="scramble-game">
      <h2 className="game-title">·Éì·Éê·Éê·Éö·Éê·Éí·Éî ·Éê·É°·Éù·Éî·Éë·Éò ·É°·É¨·Éù·É†·Éê·Éì</h2>
      <p className="game-instructions">
        ·Éí·Éê·Éì·Éõ·Éù·Éò·É¢·Éê·Éú·Éî ·Éê·É°·Éù·Éî·Éë·Éò ·Éì·Éê ·É©·Éê·É°·Éï·Éò ·É°·É¨·Éù·É† ·Éê·Éì·Éí·Éò·Éö·Éê·É°
      </p>
      
      <img 
        src={`${process.env.PUBLIC_URL}/photos/${currentAnimal.image}`} 
        alt={currentAnimal.name}
        className="animal-image"
      />
      
      <div 
        ref={targetContainerRef}
        className="target-container"
      >
        {placedLetters.map((letter, index) => (
          <div
            key={index}
            className={`letter-slot ${correctPositions.includes(index) ? 'correct' : ''} 
                      ${letter ? 'filled' : ''} 
                      ${wrongPosition === index ? 'wrong' : ''}`}
            onDragOver={handleDragOver}
            onDrop={(e) => handleDrop(e, index)}
          >
            {letter && letter.content}
          </div>
        ))}
      </div>

      <div 
        ref={lettersContainerRef}
        className="letters-container"
      >
        {scrambledLetters.map((letter, index) => (
          <div
            key={letter.id}
            data-id={letter.id}
            className="letter"
            draggable={true}
            onDragStart={(e) => handleDragStart(e, letter, index)}
          >
            {letter.content}
          </div>
        ))}
      </div>
    </div>
  );
};

export default ScrambleGame;
