  const [selectedOption, setSelectedOption] = useState(null);
  const [optionImages, setOptionImages] = useState([]);
  const { updateGameProgress } = usePlayer();
  const navigate = useNavigate();

  // ·É§·Éù·É¢·Éù·Éî·Éë·Éò·É° ·É©·Éê·É¢·Éï·Éò·É†·Éó·Éï·Éê
  const loadImages = useCallback(async () => {
    try {
      const images = [
        '·Éê·Éö·É£·Éë·Éê·Éö·Éò.webp', '·Éê·É†·É¨·Éò·Éï·Éò.webp', '·Éê·É¢·Éê·Éõ·Éò.webp', '·Éê·É•·Éö·Éî·Éõ·Éò.webp', '·Éë·Éê·Éú·Éê·Éú·Éò.webp',
        '·Éë·É†·Éù·É¨·Éî·É£·Éö·Éò.webp', '·Éë·É£.webp', '·Éí·Éï·Éî·Éö·Éò.webp', '·Éì·Éê·Éó·Éï·Éò.webp', '·Éì·Éî·Éö·É§·Éò·Éú·Éò.webp',
        '·Éï·Éê·É®·Éö·Éò.webp', '·Éñ·Éî·Éë·É†·Éê.webp', '·Éñ·Éï·Éò·Éí·Éî·Éú·Éò.webp', '·Éñ·É¶·Éê·É†·Éë·Éò.webp', '·Éó·ÉÆ·Éê (2).webp',
        '·Éò·É†·Éî·Éõ·Éò (2).webp', '·Éô·Éê·É•·É¢·É£·É°·Éò.webp', '·Éô·Éî·Éú·Éí·É£·É†·É£.webp', '·Éô·Éò·É¢·É†·Éò.webp', '·Éô·Éù·Éì·Éê·Éö·Éê.webp',
        '·Éô·É£·É†·Éì·É¶·Éî·Éö·Éò.webp', '·Éö·Éù·Éõ·Éò.webp', '·Éõ·Éí·Éî·Éö·Éù.webp', '·Éõ·Éî·Éö·Éò·Éê.webp', '·Éõ·Éñ·Éî·É°·É£·Éõ·Éñ·Éò·É†·Éê.webp',
        '·Éõ·É°·ÉÆ·Éê·Éö·Éò.webp', '·Éú·Éê·Éß·Éò·Éú·Éò.webp', '·Éú·Éò·Éù·É†·Éò.webp', '·Éù·Éë·Éù·Éë·Éê.webp', '·Éû·Éî·Éû·Éî·Éö·Éê.webp',
        '·Éû·Éù·Éõ·Éò·Éì·Éù·É†·Éò.webp', '·Éü·Éò·É†·Éê·É§·Éò.webp', '·É†·Éô·Éù.webp', '·É°·Éò·Éõ·Éò·Éú·Éì·Éò.webp', '·É°·Éû·Éò·Éö·Éù.webp',
        '·É¢·Éò·É¢·Éê.webp', '·É§·Éù·É†·Éó·Éù·ÉÆ·Éê·Éö·Éò.webp', '·É•·Éö·Éò·Éê·Éï·Éò.webp', '·Éß·É£·É†·É´·Éî·Éú·Éò.webp', '·ÉÆ·Éê·ÉÆ·Éï·Éò.webp'
      ];

      return images.map(filename => ({
