        const newSequence = [...prev, { image, index }];

        if (newSequence.length === selectedImages.length) {
          const isCorrect = selectedImages.every((img, idx) => 
            newSequence[idx].image.name === img.name
          );

          if (isCorrect) {
            updateGameProgress('memory', Date.now(), { score: 100 });
            setShowSuccess(true);
            setTimeout(() => {
              setShowSuccess(false);
              setGameState('setup');
              setUserSequence([]);
            }, 2000);
          } else {
            setShowError(true);
            setTimeout(() => {
              setShowError(false);
              setUserSequence([]);
            }, 2000);
          }
        }

        return newSequence;
      });
    }
  }, [gameState, gameMode, selectedImages, updateGameProgress]);

  // ·Éù·É§·É™·Éò·Éò·É° ·Éê·É†·É©·Éî·Éï·Éê
  const handleOptionSelect = useCallback((image) => {
    if (gameState !== 'playing') return;

    setSelectedOption(image);
    const isCorrect = image.name === missingImage.name;

    if (isCorrect) {
      updateGameProgress('memory', Date.now(), { score: 100 });
      setShowSuccess(true);
      setTimeout(() => {
        setShowSuccess(false);
        setGameState('setup');
        setSelectedOption(null);
      }, 2000);
    } else {
      setShowError(true);
      setTimeout(() => {
        setShowError(false);
        setSelectedOption(null);
      }, 2000);
    }
  }, [gameState, missingImage, updateGameProgress]);

  // ·É§·Éù·É¢·Éù·Éî·Éë·Éò·É° ·Éê·É†·Éî·Éï·Éê
  const shuffleImages = useCallback(() => {
    setShuffledImages(prev => {
      const shuffled = shuffleArray(prev);
      console.log('Original order:', prev.map(img => img.name));
      console.log('Shuffled order:', shuffled.map(img => img.name));
      return shuffled;
    });
    setGameState('playing');
  }, [shuffleArray]);

  // ·É¢·Éê·Éò·Éõ·Éî·É†·Éò·É° ·Éö·Éù·Éí·Éò·Éô·Éê
  useEffect(() => {
    let interval;

    if ((gameState === 'showing' || gameState === 'memorizing') && timer > 0) {
      interval = setInterval(() => {
        setTimer(prev => {
          console.log('Timer tick:', prev - 1);
          return prev - 1;
        });
      }, 1000);

      console.log('Starting timer:', timer, 'State:', gameState);
    } else if (timer === 0) {
      if (gameState === 'showing') {
        console.log('Switching to memorizing');
        setGameState('memorizing');
        setTimer(2);
      } else if (gameState === 'memorizing') {
        console.log('Switching to playing');
        shuffleImages();
      }
    }

    return () => {
      if (interval) {
        clearInterval(interval);
      }
    };
  }, [timer, gameState, shuffleImages]);

  return (
    <>
      <GameContainer>
        {gameState === 'setup' && (
          <SetupContainer>
            <GameTitle>·Éõ·Éî·ÉÆ·É°·Éò·Éî·É†·Éî·Éë·Éò·É° ·Éó·Éê·Éõ·Éê·É®·Éò <span>üß†</span></GameTitle>
            <h2>·Éê·Éò·É†·É©·Éò·Éî ·É†·Éê·Éõ·Éì·Éî·Éú·Éò ·É°·É£·É†·Éê·Éó·Éò ·Éí·Éò·Éú·Éì·Éê ·Éì·Éê·Éò·Éõ·Éê·ÉÆ·É°·Éù·Éï·É†·Éù:</h2>
            <NumberSelect 
              value={numberOfImages} 
              onChange={(e) => setNumberOfImages(Number(e.target.value))}
            >
              <option value={3}>3 ·É°·É£·É†·Éê·Éó·Éò</option>
              <option value={4}>4 ·É°·É£·É†·Éê·Éó·Éò</option>
              <option value={5}>5 ·É°·É£·É†·Éê·Éó·Éò</option>
            </NumberSelect>

            <GameModeToggle>
              <ToggleButton 
                active={gameMode === 'sequence'} 
                onClick={() => setGameMode('sequence')}
              >
                ·Éó·Éê·Éú·Éõ·Éò·Éõ·Éì·Éî·Éï·É†·Éù·Éë·Éê
              </ToggleButton>
              <ToggleButton 
                active={gameMode === 'missing'} 
                onClick={() => setGameMode('missing')}
              >
                ·É§·Éù·É¢·Éù·É° ·Éê·Éõ·Éù·Éô·Éö·Éî·Éë·Éê
              </ToggleButton>
            </GameModeToggle>

            <StartButton onClick={startGame}>·Éì·Éê·É¨·Éß·Éî·Éë·Éê</StartButton>
          </SetupContainer>
        )}

        {showSuccess && (
          <SuccessMessage show>
            ·Éí·Éò·Éö·Éù·É™·Éê·Éï! {gameMode === 'sequence' ? '·É°·É¨·Éù·É†·Éê·Éì ·Éí·Éê·Éò·ÉÆ·É°·Éî·Éú·Éî ·Éó·Éê·Éú·Éõ·Éò·Éõ·Éì·Éî·Éï·É†·Éù·Éë·Éê!' : '·É°·É¨·Éù·É†·Éê·Éì ·Éí·Éê·Éõ·Éù·Éò·É™·Éê·Éú·Éò ·É†·Éù·Éõ·Éî·Éö·Éò ·É§·Éù·É¢·Éù ·Éê·Éô·Éö·Éì·Éê!'} üéâ
          </SuccessMessage>
        )}

        {showError && (
          <ErrorMessage show>
            ·É°·Éê·Éõ·É¨·É£·ÉÆ·Éê·É†·Éù·Éì {gameMode === 'sequence' ? '·Éó·Éê·Éú·Éõ·Éò·Éõ·Éì·Éî·Éï·É†·Éù·Éë·Éê ·Éê·É†·Éê·É°·É¨·Éù·É†·Éò·Éê' : '·Éï·Éî·É† ·Éí·Éê·Éõ·Éù·Éò·É™·Éê·Éú·Éò ·É†·Éù·Éõ·Éî·Éö·Éò ·É§·Éù·É¢·Éù ·Éê·Éô·Éö·Éì·Éê'}. ·É°·É™·Éê·Éì·Éî ·Éó·Éê·Éï·Éò·Éì·Éê·Éú! ü§î
          </ErrorMessage>
        )}

        {gameState === 'showing' && (
          <>
            <Timer>
              <span className="hourglass">‚åõ</span>
              {timer} ·É¨·Éê·Éõ·Éò
            </Timer>
            <Message show>·Éì·Éê·Éò·Éõ·Éê·ÉÆ·É°·Éù·Éï·É†·Éî ·É°·É£·É†·Éê·Éó·Éî·Éë·Éò!</Message>
            <ImagesGrid>
              {selectedImages.map((image, index) => (
                <ImageCard key={index}>
                  <img src={image.url} alt={image.name} />
                  <p>{image.name}</p>
                </ImageCard>
              ))}
            </ImagesGrid>
          </>
        )}

        {gameState === 'memorizing' && (
          <>
            <Timer>
              <span className="hourglass">‚åõ</span>
              {timer} ·É¨·Éê·Éõ·Éò
            </Timer>
            <Message show>·Éì·Éê·ÉÆ·É£·É≠·Éî ·Éó·Éï·Éê·Éö·Éî·Éë·Éò! {timer} ·É¨·Éê·Éõ·Éò ·Éì·Éê·É†·É©·Éê</Message>
          </>
        )}

        {gameState === 'playing' && gameMode === 'sequence' && (
          <>
            <Message show>·Éê·É¶·Éê·Éì·Éí·Éò·Éú·Éî ·É°·É£·É†·Éê·Éó·Éî·Éë·Éò·É° ·Éó·Éê·Éú·Éõ·Éò·Éõ·Éì·Éî·Éï·É†·Éù·Éë·Éê!</Message>
            <ImagesGrid>
              {shuffledImages.map((image, index) => (
                <ImageCard 
                  key={index}
                  isSelected={userSequence.some(seq => seq.index === index)}
                  isPlayable={!userSequence.some(seq => seq.index === index)}
                  onClick={() => !userSequence.some(seq => seq.index === index) && handleImageClick(image, index)}
                >
                  <img src={image.url} alt={image.name} />
                  <p>{image.name}</p>
                  {userSequence.findIndex(seq => seq.index === index) > -1 && (
                    <div className="order-number">
                      {userSequence.findIndex(seq => seq.index === index) + 1}
                    </div>
                  )}
                </ImageCard>
              ))}
            </ImagesGrid>
            <Message show>
              {userSequence.length < selectedImages.length 
                ? `·Éê·Éò·É†·É©·Éò·Éî ${userSequence.length + 1}-·Éî ·É°·É£·É†·Éê·Éó·Éò`
                : '·Éß·Éï·Éî·Éö·Éê ·É°·É£·É†·Éê·Éó·Éò ·Éê·É†·É©·Éî·É£·Éö·Éò·Éê'}
            </Message>
          </>
        )}

        {gameState === 'playing' && gameMode === 'missing' && (
          <>
            <Message show>·É†·Éù·Éõ·Éî·Éö·Éò ·É§·Éù·É¢·Éù ·Éê·Éô·Éö·Éò·Éê?</Message>
            <ImagesGrid>
              {shuffledImages.filter(img => img.name !== missingImage.name).map((image, index) => (
                <ImageCard key={index}>
                  <img src={image.url} alt={image.name} />
                  <p>{image.name}</p>
                </ImageCard>
              ))}
            </ImagesGrid>
            <Message show>·Éê·Éò·É†·É©·Éò·Éî ·É°·É¨·Éù·É†·Éò ·Éû·Éê·É°·É£·ÉÆ·Éò:</Message>
            <OptionsGrid>
              {optionImages.map((image, index) => (
                <OptionCard
                  key={index}
                  selected={selectedOption?.name === image.name}
                  onClick={() => handleOptionSelect(image)}
                >
                  <img src={image.url} alt={image.name} />
                  <p>{image.name}</p>
                </OptionCard>
              ))}
            </OptionsGrid>
          </>
        )}
      </GameContainer>
    </>
  );
};

export default MemoryGame;
