import React, { useState } from 'react';
import { createGlobalStyle } from 'styled-components';
import styled from 'styled-components';
import MainMenu from './components/MainMenu';
import PuzzleBoard from './components/PuzzleBoard';
import ImageUploader from './components/ImageUploader';
import DifficultySelector from './components/DifficultySelector';

const GlobalStyle = createGlobalStyle`
  body {
    margin: 0;
    padding: 0;
    font-family: 'Segoe UI', 'Roboto', sans-serif;
    background-color: #f0f8ff;
  }
`;

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 20px;
`;

const App = () => {
  const [currentGame, setCurrentGame] = useState(null);
  const [image, setImage] = useState(null);
  const [difficulty, setDifficulty] = useState(3);
  const [progress, setProgress] = useState(0);
  const [isComplete, setIsComplete] = useState(false);

  const handleGameSelect = (gameId) => {
    setCurrentGame(gameId);
    // თუ პაზლების თამაშს არ ირჩევს, გავასუფთაოთ პაზლის მდგომარეობა
    if (gameId !== 'puzzles') {
      setImage(null);
      setDifficulty(3);
      setProgress(0);
      setIsComplete(false);
    }
  };

  const handleImageUpload = (uploadedImage) => {
    setImage(uploadedImage);
    setProgress(0);
    setIsComplete(false);
  };

  const handlePuzzleComplete = () => {
    setIsComplete(true);
  };

  const handleBackToMenu = () => {
    setCurrentGame(null);
    setImage(null);
    setDifficulty(3);
    setProgress(0);
    setIsComplete(false);
  };

  const renderGame = () => {
    switch (currentGame) {
      case 'puzzles':
        return !image ? (
          <>
            <DifficultySelector 
              difficulty={difficulty} 
              onDifficultyChange={setDifficulty}
            />
            <ImageUploader onImageUpload={handleImageUpload} />
          </>
        ) : (
          <PuzzleBoard
            image={image}
            difficulty={difficulty}
            onProgress={setProgress}
            onComplete={handlePuzzleComplete}
            onBackToMenu={handleBackToMenu}
          />
        );
      case 'tasks':
        return <div>სახალისო ამოცანები - მალე დაემატება!</div>;
      case 'stories':
        return <div>ზღაპრები და კითხვები - მალე დაემატება!</div>;
      case 'photos':
        return <div>ფოტოების დალაგება - მალე დაემატება!</div>;
      default:
        return <MainMenu onGameSelect={handleGameSelect} />;
    }
  };

  return (
    <>
      <GlobalStyle />
      <Container>
        {renderGame()}
      </Container>
    </>
  );
};

export default App;
