import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import PuzzleBoard from './PuzzleBoard';
import DifficultySelector from './DifficultySelector';
import NameModal from './NameModal';
import { usePlayer } from '../context/PlayerContext';

const Container = styled.div`
  min-height: 100vh;
  background: linear-gradient(to bottom, #EBF3FE, #F3EFFE);
  padding: 0.5rem;
  
  @media (min-width: 640px) {
    padding: 1rem;
  }
`;

const Header = styled.header`
  display: flex;
  align-items: center;
  justify-content: space-between;
  margin-bottom: 1rem;
  max-width: 64rem;
  margin-left: auto;
  margin-right: auto;
`;

const Title = styled.h1`
  font-size: 1.5rem;
  font-weight: bold;
  color: #2196F3;
  display: flex;
  align-items: center;
  gap: 0.5rem;

  @media (min-width: 640px) {
    font-size: 1.875rem;
  }
`;

const GameIcon = styled.span`
  font-size: 1.5rem;
  
  @media (min-width: 640px) {
    font-size: 2rem;
  }
`;

const Main = styled.main`
  max-width: 64rem;
  margin: 0 auto;
  display: flex;
  flex-direction: column;
  gap: 1rem;
`;

const Card = styled.div`
  background: rgba(255, 255, 255, 0.8);
  backdrop-filter: blur(10px);
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1);
  border-radius: 1rem;
  padding: 1rem;
`;

const DifficultyCard = styled(Card)`
  text-align: center;
`;

const DifficultyTitle = styled.h2`
  font-size: 1.125rem;
  font-weight: 600;
  margin-bottom: 0.5rem;
`;

const DifficultyButtons = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  gap: 0.5rem;
`;

const DifficultyButton = styled.button`
  height: 2.5rem;
  width: 4rem;
  font-size: 1rem;
  border-radius: 0.75rem;
  transition: transform 0.2s;
  background: ${props => props.isSelected ? '#2196F3' : 'transparent'};
  color: ${props => props.isSelected ? 'white' : '#2196F3'};
  border: 2px solid ${props => props.isSelected ? 'transparent' : '#2196F3'};
  cursor: pointer;

  &:hover {
    transform: scale(1.05);
  }
`;

const GameGrid = styled.div`
  display: grid;
  grid-template-columns: 1fr;
  gap: 1rem;

  @media (min-width: 640px) {
    grid-template-columns: 1fr 1fr;
  }
`;

const PuzzleGame = () => {
  const navigate = useNavigate();
  const { playerName, showNameModal, updatePlayerName } = usePlayer();
  const [difficulty, setDifficulty] = useState(3);
  const [progress, setProgress] = useState(0);
  const [image, setImage] = useState('https://hebbkx1anhila5yf.public.blob.vercel-storage.com/image-Y0mIAy2Kx0OCcscA7vRBYqFmF32LG4.png');

  const handleComplete = () => {
    alert(`გილოცავთ ${playerName}! თქვენ წარმატებით დაასრულეთ პაზლი! 🎉`);
  };

  const handleBackToMenu = () => {
    navigate('/');
  };

  if (showNameModal) {
    return <NameModal onSubmit={updatePlayerName} />;
  }

  return (
    <Container>
      <Header>
        <Title>
          <GameIcon>🎮</GameIcon>
          გამარჯობა, {playerName}! 👋
        </Title>
      </Header>

      <Main>
        <DifficultyCard>
          <DifficultyTitle>აირჩიე სირთულის დონე</DifficultyTitle>
          <DifficultyButtons>
            {[3, 4, 5].map((size) => (
              <DifficultyButton
                key={size}
                isSelected={difficulty === size}
                onClick={() => setDifficulty(size)}
              >
                {size}x{size}
              </DifficultyButton>
            ))}
          </DifficultyButtons>
        </DifficultyCard>

        <PuzzleBoard
          image={image}
          difficulty={difficulty}
          onProgress={setProgress}
          onComplete={handleComplete}
          onBackToMenu={handleBackToMenu}
        />
      </Main>
    </Container>
  );
};

export default PuzzleGame;
