import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { useNavigate } from 'react-router-dom';
import ImageUploader from './ImageUploader';
import DifficultySelector from './DifficultySelector';

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 20px;
  gap: 20px;
`;

const Title = styled.h1`
  color: #2196F3;
  text-align: center;
  margin-bottom: 20px;
`;

const GameContainer = styled.div`
  display: flex;
  gap: 40px;
  align-items: flex-start;
  padding: 20px;
`;

const OriginalImage = styled.div`
  width: 200px;
  height: 200px;
  background-image: url(${props => props.image});
  background-size: cover;
  border-radius: 10px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.2);
  margin-bottom: 20px;
`;

const Board = styled.div`
  display: grid;
  grid-template-columns: repeat(${props => props.size}, 1fr);
  gap: 2px;
  background-color: ${props => props.isTarget ? '#f5f5f5' : '#fff'};
  padding: 10px;
  border-radius: 10px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.2);
  width: 400px;
  height: 400px;
  box-sizing: border-box;
`;

const PuzzlePiece = styled.div`
  width: 100%;
  height: 100%;
  background-image: ${props => props.image ? `url(${props.image})` : 'none'};
  background-size: ${props => props.size * 100}% ${props => props.size * 100}%;
  background-position: ${props => props.bgPosition};
  border: 2px solid ${props => props.isWrongAttempt ? '#ff4444' : '#ddd'};
  cursor: ${props => props.isPlaced ? 'default' : 'pointer'};
  transition: all 0.2s;
  display: flex;
  align-items: center;
  justify-content: center;
  background-color: ${props => {
    if (props.isWrongAttempt) return 'rgba(255, 68, 68, 0.1)';
    return props.isTarget ? '#f0f0f0' : 'transparent';
  }};
  background-repeat: no-repeat;
  position: relative;

  &:hover {
    transform: ${props => props.isPlaced ? 'none' : 'scale(1.05)'};
    z-index: 1;
  }
`;

const HomeButton = styled.button`
  position: fixed;
  top: 20px;
  left: 20px;
  font-size: 2rem;
  background: none;
  border: none;
  cursor: pointer;
  transition: transform 0.3s ease;
  z-index: 100;

  &:hover {
    transform: scale(1.1);
  }
`;

const PuzzleBoard = () => {
  const [image, setImage] = useState(null);
  const [difficulty, setDifficulty] = useState(3);
  const navigate = useNavigate();

  const handleImageUpload = (uploadedImage) => {
    setImage(uploadedImage);
  };

  if (!image) {
    return (
      <Container>
        <HomeButton onClick={() => navigate('/')}>üè†</HomeButton>
        <Title>·Éû·Éê·Éñ·Éö·Éò·É° ·Éó·Éê·Éõ·Éê·É®·Éò</Title>
        <DifficultySelector 
          difficulty={difficulty} 
          onDifficultyChange={setDifficulty}
        />
        <ImageUploader onImageUpload={handleImageUpload} />
      </Container>
    );
  }

  return (
    <Container>
      <HomeButton onClick={() => navigate('/')}>üè†</HomeButton>
      <Title>·Éû·Éê·Éñ·Éö·Éò·É° ·Éó·Éê·Éõ·Éê·É®·Éò</Title>
      <GameContainer>
        <OriginalImage image={image} />
        <Board size={difficulty}>
          {/* ·Éû·Éê·Éñ·Éö·Éò·É° ·Éú·Éê·É¨·Éò·Éö·Éî·Éë·Éò ·Éê·É• ·Éì·Éê·Éî·Éõ·Éê·É¢·Éî·Éë·Éê */}
        </Board>
      </GameContainer>
    </Container>
  );
};

export default PuzzleBoard;
