    const randomTask = availableTasks[
      Math.floor(Math.random() * availableTasks.length)
    ];
    setCurrentTask(randomTask);
    setShowGame(true);
  };

  const handleTaskComplete = (taskId) => {
    if (!completedTasks.includes(taskId)) {
      setCompletedTasks(prev => [...prev, taskId]);

      const remainingTasks = allTasks.filter(task => 
        !completedTasks.includes(task.id) && task.id !== taskId
      );

      if (remainingTasks.length === 0) {
        setShowGame(false);
        showCelebration(celebrationStickers);
      } else {
        const nextTask = remainingTasks[
          Math.floor(Math.random() * remainingTasks.length)
